INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A88A0A2E639C042A8871EBF6B4B3EC5', X'76B91297D057094C80BFBF1D9B9DEE87', X'76B91297D057094C80BFBF1D9B9DEE87', 3, NULL, NULL, 2, NULL, 0, 'Apply a thin cold smoke at 18º C (64º F) for 2 days.');
