INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'86AD8FB796905940AEF5BF1674198AF1', X'C7720E45D2082D4486378EB62397AC1C', X'C7720E45D2082D4486378EB62397AC1C', 0, NULL, NULL, 2, NULL, 0, 'Chop meat into sizes suitable for cooking. Place all meats in a pot, add some salt and cover with water. Occasionally soup greens are added for better flavor. Simmer the meat until soft. Drain meats but save the meat stock.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A7391CCE2E46E54A9068F2F14D555EA3', X'C7720E45D2082D4486378EB62397AC1C', X'C7720E45D2082D4486378EB62397AC1C', 1, NULL, NULL, 2, NULL, 0, 'Spread the meat on the table and separate meat from bones when the meat is still warm.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F32D219D0A91594082607E623EE0411D', X'C7720E45D2082D4486378EB62397AC1C', X'C7720E45D2082D4486378EB62397AC1C', 2, NULL, NULL, 2, NULL, 0, 'Break up or cut up white bread (the crust is left on) and soak it in the hot meat stock adding strips of meat, all spices and all ingredients. Drain and squeeze the excess stock out.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'667BC8CC5ABA6F4AB1079F9E3718FDC7', X'C7720E45D2082D4486378EB62397AC1C', X'C7720E45D2082D4486378EB62397AC1C', 3, NULL, NULL, 2, NULL, 0, 'Stuff into 70-100 mm hog casings (pork middles, fatends or bungs) about 10 (25 cm) long.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF44C0410856D147BCF55F636BD521DC', X'C7720E45D2082D4486378EB62397AC1C', X'C7720E45D2082D4486378EB62397AC1C', 4, NULL, NULL, 2, NULL, 0, 'Apply a thin cold smoke at 18° C (64° F) for 4 weeks using local wood (oak, chestnut). Smoking need not to be continuous, for example the fire can be put out at night and restarted in the morning as long as the temperature inside of the smokehouse stays below 18° C. Think of the process as drying with smoke.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'34FD3BE8C1198842A279A631A0AE5CA7', X'C7720E45D2082D4486378EB62397AC1C', X'C7720E45D2082D4486378EB62397AC1C', 5, NULL, NULL, 2, NULL, 0, 'Hold at 12-15° C (54-59° F), 75% humidity for 7 days.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1C3ACEDB09EC840843DD5216D06AA43', X'C7720E45D2082D4486378EB62397AC1C', X'C7720E45D2082D4486378EB62397AC1C', 6, NULL, NULL, 2, NULL, 0, 'Store at 12° C (54° F), 70% humidity or refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'748FEF000F16C84CBC70FC6CD39994BC', X'34519A9DACA62A44ACDFECDBAB462A2A', X'34519A9DACA62A44ACDFECDBAB462A2A', 0, NULL, NULL, 2, NULL, 0, 'Cut the meat and fat into small pieces and marinate with salt, cure #1, bay leaf, garlic and wine for up to 5 days. Stir daily.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2ED37B964C69A141AF0F7FBC28D5C74C', X'34519A9DACA62A44ACDFECDBAB462A2A', X'34519A9DACA62A44ACDFECDBAB462A2A', 1, NULL, NULL, 2, NULL, 0, 'Scrape the flesh of the squash (or pumpkin), discard the seeds. Wrap around with a cloth (or cheese cloth), place in a colander, add some weight on top and let it drain for 2 days.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3884E7183709C649A47765BE1FA38F3A', X'34519A9DACA62A44ACDFECDBAB462A2A', X'34519A9DACA62A44ACDFECDBAB462A2A', 2, NULL, NULL, 2, NULL, 0, 'Mix meat, squash, paprika, chili, cinnamon and ginger together.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30CCB44BFEB94D43BE89CDC71A6AC558', X'34519A9DACA62A44ACDFECDBAB462A2A', X'34519A9DACA62A44ACDFECDBAB462A2A', 3, NULL, NULL, 2, NULL, 0, 'Stuff into 60 mm hog casing. The casing is tied shut with two simple knots at each end, using a single piece of cotton twine, giving the sausage a horseshoe shape.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ACB873603744064BBE57D71E12B8CB90', X'34519A9DACA62A44ACDFECDBAB462A2A', X'34519A9DACA62A44ACDFECDBAB462A2A', 4, NULL, NULL, 2, NULL, 0, 'Apply a thin cold smoke at 18° C (64° F) for 30 days. Smoking need not to be continuous, for example the fire can be put out at night and restarted in the morning as long as the temperature inside of the smokehouse stays below 18° C. Think of the process as drying with smoke. The sausage is smoked with oak.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'64B783855F75E047B22DFE40576AD0CF', X'34519A9DACA62A44ACDFECDBAB462A2A', X'34519A9DACA62A44ACDFECDBAB462A2A', 5, NULL, NULL, 2, NULL, 0, 'Dry at 12-15° C (54-59° F), 75% humidity for 7 days.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DBEE50E3D1F202489805C199CC49777B', X'34519A9DACA62A44ACDFECDBAB462A2A', X'34519A9DACA62A44ACDFECDBAB462A2A', 6, NULL, NULL, 2, NULL, 0, 'Store at 12° C (54° F), 70% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16FAFE924270514AB74A6CEF52F95C3F', X'A6C53A616096F6498D1F4CD6E7F8E4EA', X'A6C53A616096F6498D1F4CD6E7F8E4EA', 0, NULL, NULL, 2, NULL, 0, 'Cut meat into 1/2 (12 mm) pieces.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1727EA0C418874184AB1F34CA214BA6', X'A6C53A616096F6498D1F4CD6E7F8E4EA', X'A6C53A616096F6498D1F4CD6E7F8E4EA', 1, NULL, NULL, 2, NULL, 0, 'Mix with all ingredients.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8DCDD0EE16846244B32D6AB98084A0A3', X'A6C53A616096F6498D1F4CD6E7F8E4EA', X'A6C53A616096F6498D1F4CD6E7F8E4EA', 2, NULL, NULL, 2, NULL, 0, 'Stuff firmly into 30 mm hog casings. Tie the ends together making a 8-12 (20-30 cm) long loop.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47E0EAEFD3B4D44D90E3D74B452B0E49', X'A6C53A616096F6498D1F4CD6E7F8E4EA', X'A6C53A616096F6498D1F4CD6E7F8E4EA', 3, NULL, NULL, 2, NULL, 0, 'Smoke with oak wood at 30° C (86° F) for at least 3 days.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1DE751877EF4674AA6DB13D4C8D254A3', X'A6C53A616096F6498D1F4CD6E7F8E4EA', X'A6C53A616096F6498D1F4CD6E7F8E4EA', 4, NULL, NULL, 2, NULL, 0, 'Store at at 5-8° C (41-46° F), 75% humidity. The sausage will start loosing moisture and in time will become a dry sausage.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36147F77369D7B42B6EFDA31DB25871A', X'915CA75EBDEFE14B931EF44B1DDC14CC', X'915CA75EBDEFE14B931EF44B1DDC14CC', 0, NULL, NULL, 2, NULL, 0, 'Make a marinade by mixing all ingredients with wine and water.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4ECAF41B4FE2C4D9BF6E2A07D246900', X'915CA75EBDEFE14B931EF44B1DDC14CC', X'915CA75EBDEFE14B931EF44B1DDC14CC', 1, NULL, NULL, 2, NULL, 0, 'Separately, cut meat and fat into 3/4 (20 mm) pieces.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6C6CA9079F79041B6BB0C108B4CAAEC', X'915CA75EBDEFE14B931EF44B1DDC14CC', X'915CA75EBDEFE14B931EF44B1DDC14CC', 2, NULL, NULL, 2, NULL, 0, 'Mix meat and fat with marinade. Hold for 2 days at 10° C (50° F), 80-90% humidity or keep in refrigerator. Stir daily.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02336A9FAE7CE54799A96EB9EE0E6C0B', X'915CA75EBDEFE14B931EF44B1DDC14CC', X'915CA75EBDEFE14B931EF44B1DDC14CC', 3, NULL, NULL, 2, NULL, 0, 'Stuff into 36-45 mm hog or beef casings. Tie the ends together to make a loop about 16 (40 cm) long.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5781540942EACF40B7BFCEF58E4A9AFE', X'915CA75EBDEFE14B931EF44B1DDC14CC', X'915CA75EBDEFE14B931EF44B1DDC14CC', 4, NULL, NULL, 2, NULL, 0, 'Apply a thin cold smoke at 18° C (64° F) for 3-10 days (depending on diameter). Smoking need not to be continuous, as long as the temperature inside of the smokehouse stays below 18° C. Think of the process as drying with smoke. The sausage is smoked with oak.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B15DCA20105E2D46B0897A23B77C992E', X'915CA75EBDEFE14B931EF44B1DDC14CC', X'915CA75EBDEFE14B931EF44B1DDC14CC', 5, NULL, NULL, 2, NULL, 0, 'Store at 12° C (54° F), 70% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0490F499BA9024FB67A24D8CEB59731', X'145991DDF14429498CC4C355B58CCE48', X'145991DDF14429498CC4C355B58CCE48', 0, NULL, NULL, 2, NULL, 0, 'Pepper paste. Cut bell peppers lengthwise into halves. Discard stems and seeds. Get a suitable pan and sprinkle salt on the bottom. Place peppers on salt, then cover them with more salt. Repeat the procedure creating a multilayer combination with salt being the top layer. Hold for 8 days. Rinse with water, pat dry with paper towel and grind through 1/8 (3 mm) plate or emulsify in food processor.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F14FB5AE2A0CA3459D86A72D8C416F70', X'145991DDF14429498CC4C355B58CCE48', X'145991DDF14429498CC4C355B58CCE48', 1, NULL, NULL, 2, NULL, 0, 'Make marinade by mixing salt, cure #1, spices, water and wine.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C570C0F86652FC4FB14B57740765D325', X'145991DDF14429498CC4C355B58CCE48', X'145991DDF14429498CC4C355B58CCE48', 2, NULL, NULL, 2, NULL, 0, 'Chop the meat and fat into 3/8 (10 mm) cubes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'35554B2A40B52245B4FD274CF5309E2E', X'145991DDF14429498CC4C355B58CCE48', X'145991DDF14429498CC4C355B58CCE48', 3, NULL, NULL, 2, NULL, 0, 'Marinate the meat and fat in marinade for 3 days in refrigerator or at <10° C (50° F).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'022AFA15B6BC7540AAAF4AEC7F1A8EBC', X'145991DDF14429498CC4C355B58CCE48', X'145991DDF14429498CC4C355B58CCE48', 4, NULL, NULL, 2, NULL, 0, 'Mix pepper paste with marinated meat.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0EFCCD59589F364C8C9C16C885C601F7', X'145991DDF14429498CC4C355B58CCE48', X'145991DDF14429498CC4C355B58CCE48', 5, NULL, NULL, 2, NULL, 0, 'Stuff firmly into 36 mm hog casings. Tie the ends together to form a loop.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'62959EF16B0C684B85B5886612EE2074', X'145991DDF14429498CC4C355B58CCE48', X'145991DDF14429498CC4C355B58CCE48', 6, NULL, NULL, 2, NULL, 0, 'Apply a thin cold smoke at 18° C (64° F) for 7 days. Smoking need not to be continuous, as long as the temperature inside of the smokehouse stays below 18° C. Think of the process as drying with smoke. The sausage is smoked with oak.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'45F884B552525147A57063F34FFBEA06', X'145991DDF14429498CC4C355B58CCE48', X'145991DDF14429498CC4C355B58CCE48', 7, NULL, NULL, 2, NULL, 0, 'Store at 12° C (54° F), 70% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B146A5234CD11E45A92DF9CA9AE9FA77', X'6305BBB89DCFB049936AC5AF8DEFBB5C', X'6305BBB89DCFB049936AC5AF8DEFBB5C', 0, NULL, NULL, 2, NULL, 0, 'Grind lean meat through 1/4 (5 mm) plate, then grind it twice through 1/8 (3 mm) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0630C93FA16624B933EC752A3535A79', X'6305BBB89DCFB049936AC5AF8DEFBB5C', X'6305BBB89DCFB049936AC5AF8DEFBB5C', 1, NULL, NULL, 2, NULL, 0, 'Cut pork belly into 1 (25 mm) cubes and partially freeze.');
