INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'90EBC4E5F136C544966CD781A6FB8C74', X'6305BBB89DCFB049936AC5AF8DEFBB5C', X'6305BBB89DCFB049936AC5AF8DEFBB5C', 6, NULL, NULL, 2, NULL, 0, 'Stuff into beef middles or large diameter hog casings (45 - 100 mm), 6 - 18 (15 - 45 cm) long.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A98C3CBBAFC41C47A0C43780F4208C9D', X'6305BBB89DCFB049936AC5AF8DEFBB5C', X'6305BBB89DCFB049936AC5AF8DEFBB5C', 7, NULL, NULL, 2, NULL, 0, 'Apply a thin cold smoke at 15-18° C (59-64° F) for 2-3 days.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E085918F99AB9940A686C09C64F1FBC8', X'6305BBB89DCFB049936AC5AF8DEFBB5C', X'6305BBB89DCFB049936AC5AF8DEFBB5C', 8, NULL, NULL, 2, NULL, 0, 'Dry sausages for 30 - 45 days (depending on diameter) at 12-15° C (53 - 59°- F), 65-80% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A3C348CBF0E144CA02217BDD4F96F2C', X'6305BBB89DCFB049936AC5AF8DEFBB5C', X'6305BBB89DCFB049936AC5AF8DEFBB5C', 9, NULL, NULL, 2, NULL, 0, 'Store at 10-12° C (50-53° F), 65-70% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60B817D805F0E54A9AB29C408A243359', X'510C5F37E3AA284388E0E20613BE895E', X'510C5F37E3AA284388E0E20613BE895E', 0, NULL, NULL, 2, NULL, 0, 'Curing back fat. Sprinkle salt on the bottom of the curing container, then take a slab of pork fat and forcefully rub in salt into it on all sides. You could use a zip-lock plastic bag as food container. Sprinkle more salt on top. Hold in refrigerator for 3 weeks. Shake off the salt, rinse with cold water and pat dry. Place in freezer for 30 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5D78AE00FABBE40BFCF520726D9662D', X'510C5F37E3AA284388E0E20613BE895E', X'510C5F37E3AA284388E0E20613BE895E', 1, NULL, NULL, 2, NULL, 0, 'Grind pork through 1/8 plate (3 mm).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2EF6FDDC0C49664FA1F0E63BBA58ED52', X'510C5F37E3AA284388E0E20613BE895E', X'510C5F37E3AA284388E0E20613BE895E', 2, NULL, NULL, 2, NULL, 0, 'Stir the culture in 1 tablespoon of cold de-chlorinated water 30 minutes before use.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'46739CE1F9B5804F93E3DC438AC1B0E0', X'510C5F37E3AA284388E0E20613BE895E', X'510C5F37E3AA284388E0E20613BE895E', 3, NULL, NULL, 2, NULL, 0, 'Mix/knead ground pork with salt and cure # 2 until sticky. Add all remaining ingredients and mix thoroughly.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4DA4FFB213F5774D996D24EC9F8B0C0E', X'510C5F37E3AA284388E0E20613BE895E', X'510C5F37E3AA284388E0E20613BE895E', 4, NULL, NULL, 2, NULL, 0, 'Cut back fat into strips 4 (100 mm) x 3/4 (20 mm) square.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C78382AC6D93C6449ACC8DB9DB50DD21', X'510C5F37E3AA284388E0E20613BE895E', X'510C5F37E3AA284388E0E20613BE895E', 5, NULL, NULL, 2, NULL, 0, 'Coglioni di Mulo has an oval shape and traditional weight of 330 g (12 oz).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ADB5D1FF6FAF37498D71D6F00DDF6934', X'510C5F37E3AA284388E0E20613BE895E', X'510C5F37E3AA284388E0E20613BE895E', 6, NULL, NULL, 2, NULL, 0, 'Divide meat paste into portions of about 330 g. Flatten the meat paste into a 1/2 (12 mm) patty making a heart-shaped form. Place a back fat stick on each end of the patty and fold it up into a tear drop shape (this is why the sausage is called mules balls).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'016159C0E89B2E4A9CFF038F203095BB', X'510C5F37E3AA284388E0E20613BE895E', X'510C5F37E3AA284388E0E20613BE895E', 7, NULL, NULL, 2, NULL, 0, 'Cut a suitable casing such as beef middle or a large hog casings. The casing will overlap the tear drop shaped sausage. It is like wrapping up a gift. Fold the casing over, make loops across with butchers twine and tie it lengthwise making a hanging loop at one end..');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F26ED50E4B9E9F49A427A4479F8730F9', X'510C5F37E3AA284388E0E20613BE895E', X'510C5F37E3AA284388E0E20613BE895E', 8, NULL, NULL, 2, NULL, 0, 'Ferment at 20º C (68º F) for 72 hours, 90-85% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D56DC9DDE2460C4FB936EF0CA8CE9934', X'510C5F37E3AA284388E0E20613BE895E', X'510C5F37E3AA284388E0E20613BE895E', 9, NULL, NULL, 2, NULL, 0, 'Dry at 16-12º C (60-54º F), 85-80% humidity for 4-6 weeks. The sausage is dried until around 30-35% in weight is lost.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41A1242B261EA64D9B01794685950F01', X'510C5F37E3AA284388E0E20613BE895E', X'510C5F37E3AA284388E0E20613BE895E', 10, NULL, NULL, 2, NULL, 0, 'Store sausages at 10-15º C (50-59º F), 75% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B54DAB6042B74F48B662EDDA2B457A63', X'4678234A3825DE40BDEDE73437640E3C', X'4678234A3825DE40BDEDE73437640E3C', 0, NULL, NULL, 2, NULL, 0, 'Meat is manually boned, which enables the thorough removal of sinews, gristle and connective tissue.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E91ECD1E3EA86B4D9F3F77D04C8253F7', X'4678234A3825DE40BDEDE73437640E3C', X'4678234A3825DE40BDEDE73437640E3C', 1, NULL, NULL, 2, NULL, 0, 'Grind meat through 1/4 (6 mm) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69AD8021E08D5141BA11EE17A2A5160B', X'4678234A3825DE40BDEDE73437640E3C', X'4678234A3825DE40BDEDE73437640E3C', 2, NULL, NULL, 2, NULL, 0, 'Grind fat through 1/4 (6 mm) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC9BEF5C605CF847AB1A31BEC7D4FF77', X'4678234A3825DE40BDEDE73437640E3C', X'4678234A3825DE40BDEDE73437640E3C', 3, NULL, NULL, 2, NULL, 0, 'Mix with salt, cure #2 and spices.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3CBE620AE124B4CB503F7A2F2661C35', X'4678234A3825DE40BDEDE73437640E3C', X'4678234A3825DE40BDEDE73437640E3C', 4, NULL, NULL, 2, NULL, 0, 'Stuff into 40-60 mm hog or synthetic fibrous casings forming 8-22 (20 - 55cm) links.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5AA2BFDC1B43AF4C879594124FE8928F', X'4678234A3825DE40BDEDE73437640E3C', X'4678234A3825DE40BDEDE73437640E3C', 5, NULL, NULL, 2, NULL, 0, 'Apply a thin cold smoke for 3-5 days at 20° C (66° F), 70-90% humidity. Original Gyulai kolbasz was smoked with beech wood.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE424105D69186459CDB1CBA920BAB0D', X'4678234A3825DE40BDEDE73437640E3C', X'4678234A3825DE40BDEDE73437640E3C', 6, NULL, NULL, 2, NULL, 0, 'Dry for 4 -6 weeks at 18° ? 16° C (64-60° F), 90-92% humidity, then gradually reduce humidity to 70 ? 65%.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0487DC8E894AE4CB51702F5BB34899E', X'4678234A3825DE40BDEDE73437640E3C', X'4678234A3825DE40BDEDE73437640E3C', 7, NULL, NULL, 2, NULL, 0, 'Store at 12° C (54° F), 65% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD4237009E2B714D83A417FC7D44D0F2', X'5A017220DC91634C9B9AE16B78030679', X'5A017220DC91634C9B9AE16B78030679', 0, NULL, NULL, 2, NULL, 0, 'Grind beef through 3 mm (1/8`) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20313DFF2079BC42A92A08B4EC2BDFB1', X'5A017220DC91634C9B9AE16B78030679', X'5A017220DC91634C9B9AE16B78030679', 1, NULL, NULL, 2, NULL, 0, 'Grind pork through 5 mm (1/4`) plate.');
