INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'28AB943865F61244BFEE8E68040C25C6', X'5A017220DC91634C9B9AE16B78030679', X'5A017220DC91634C9B9AE16B78030679', 3, NULL, NULL, 2, NULL, 0, 'Stuff into 55 mm beef or pork middles or synthetic protein lined casings. Form 50 cm (20`) straight links.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E3E0E7E2241C0A44A68FBE62ADE8D32C', X'5A017220DC91634C9B9AE16B78030679', X'5A017220DC91634C9B9AE16B78030679', 4, NULL, NULL, 2, NULL, 0, 'Ferment at 20° C (68° F), 95 ? 85% humidity for 3 days.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E9DBE044D16E04CAC6893C63205F6A5', X'5A017220DC91634C9B9AE16B78030679', X'5A017220DC91634C9B9AE16B78030679', 5, NULL, NULL, 2, NULL, 0, 'Cold smoke at 18° C (64° F) for 1 day.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A7A445C70EF2346BBDEC55A4A5EC348', X'5A017220DC91634C9B9AE16B78030679', X'5A017220DC91634C9B9AE16B78030679', 6, NULL, NULL, 2, NULL, 0, 'Dry at 15?12º C (58 ?53º F), 80-75% humidity for 5 weeks until sausage looses 33% of its original weight.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F76E969C75EF964AB39B3C27F43A8543', X'5A017220DC91634C9B9AE16B78030679', X'5A017220DC91634C9B9AE16B78030679', 7, NULL, NULL, 2, NULL, 0, 'Store at 10-12º C (50-53º F), <75% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B24C339A87CA945BC4564EAF9E408E5', X'BEC7AC18C8518D488E6FFEB6BE616A51', X'BEC7AC18C8518D488E6FFEB6BE616A51', 0, NULL, NULL, 2, NULL, 0, 'Cut meat into 25 mm (1) pieces. Mix with all ingredients and marinate overnight.');
