INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BF6193C78C85B24299541590453E5C2F', X'BEC7AC18C8518D488E6FFEB6BE616A51', X'BEC7AC18C8518D488E6FFEB6BE616A51', 5, NULL, NULL, 2, NULL, 0, 'Air dry at 45-48° C (113-120° F) for 48 hours without smoke.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0DFE9861E1B9954E8F3C1CD2B3C3F2C3', X'BEC7AC18C8518D488E6FFEB6BE616A51', X'BEC7AC18C8518D488E6FFEB6BE616A51', 6, NULL, NULL, 2, NULL, 0, 'This is a semi-dry sausage that must be refrigerated and cooked before serving.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88799CBE5C4C694FA7016CEC5FF0332A', X'BEC7AC18C8518D488E6FFEB6BE616A51', X'BEC7AC18C8518D488E6FFEB6BE616A51', 7, NULL, NULL, 2, NULL, 0, 'To make a dry sausage keep on drying/storing at 1°2 C (53° F) until the sausage loses 35% of its original weight.');
