INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CFC2295E4910D4B94C1916A051B90DE', X'F021510033CFB44C9220CDB308D5B15E', X'F021510033CFB44C9220CDB308D5B15E', 0, NULL, NULL, 2, NULL, 0, 'Grind meat and fat through 3-6 mm (1/8-1/4) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E117F4BADD97A418C9A9D46F008CA6E', X'F021510033CFB44C9220CDB308D5B15E', X'F021510033CFB44C9220CDB308D5B15E', 1, NULL, NULL, 2, NULL, 0, 'Dissolve culture in 1 tablespoon of de-chlorinated water 30 minutes before use.');
