INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6DF3D20B8686847A33169A74A78312F', X'F021510033CFB44C9220CDB308D5B15E', X'F021510033CFB44C9220CDB308D5B15E', 3, NULL, NULL, 2, NULL, 0, 'Stuff into pork or calf''s bladder, blind cap (caecum) or 65 mm beef middles. Make 6-12 (15-30 cm) links.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C72167BB5C9EB449B6FCFE143B74D35A', X'F021510033CFB44C9220CDB308D5B15E', X'F021510033CFB44C9220CDB308D5B15E', 4, NULL, NULL, 2, NULL, 0, 'Ferment at 20º C (68º F) for 72 hours, 90-85% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'83E781E96F4F6E4E9EB64BAFA4BC6B1E', X'F021510033CFB44C9220CDB308D5B15E', X'F021510033CFB44C9220CDB308D5B15E', 5, NULL, NULL, 2, NULL, 0, 'Ferment/dry at 18º C (64º F), 85-80% humidity for 24 hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C4C577A19E7024BB1E6FB3C4DC144D2', X'F021510033CFB44C9220CDB308D5B15E', X'F021510033CFB44C9220CDB308D5B15E', 6, NULL, NULL, 2, NULL, 0, 'Dry for 5-6 weeks at 15 ? 12º C (59 ? 54º F), 80 ? 75% humidity until the sausage looses 33% of its original weight. The diameter of the sausage influences the drying time.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF98F98C59CE974A93972414EF6B0049', X'2ED5DB73CCB45C41A8B86354A5535583', X'2ED5DB73CCB45C41A8B86354A5535583', 0, NULL, NULL, 2, NULL, 0, 'Grind meat and fat through 6-8 mm (1/4-3/8) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B43F280DE7116408956BCE846E46958', X'2ED5DB73CCB45C41A8B86354A5535583', X'2ED5DB73CCB45C41A8B86354A5535583', 1, NULL, NULL, 2, NULL, 0, 'Mix meat with spices and stuff into pork bungs or large diameter casings. Leave both ends open for now.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A7509C27DCD8F4B97D6344D4AB5462D', X'2ED5DB73CCB45C41A8B86354A5535583', X'2ED5DB73CCB45C41A8B86354A5535583', 2, NULL, NULL, 2, NULL, 0, 'Hold for 24 hours in refrigerator. Tie off the ends with twine.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B9502CA454D10E49A18702682371F41E', X'2ED5DB73CCB45C41A8B86354A5535583', X'2ED5DB73CCB45C41A8B86354A5535583', 3, NULL, NULL, 2, NULL, 0, 'Hang for 4 days at 8-10° C (48-50° F).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA6F90539A8FD544B8BF26EBCA131F75', X'2ED5DB73CCB45C41A8B86354A5535583', X'2ED5DB73CCB45C41A8B86354A5535583', 4, NULL, NULL, 2, NULL, 0, 'Place on a flat surface, place a suitable board with a weight on it (wooden planks were normally used) and flatten for 24 hours, of course, at low temperature. El Xolis is a flatten sausage, often 8 shaped. The top groove was usually created manually with fingers.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'31930A1E439395468A922C642344215B', X'2ED5DB73CCB45C41A8B86354A5535583', X'2ED5DB73CCB45C41A8B86354A5535583', 5, NULL, NULL, 2, NULL, 0, 'Hang at 12-15° C (53-59° F), 80-85% humidity, for 2-3 months.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7369BD58ACAE1149B071DA58183EFA28', X'2ED5DB73CCB45C41A8B86354A5535583', X'2ED5DB73CCB45C41A8B86354A5535583', 6, NULL, NULL, 2, NULL, 0, 'Store at 10-12° C, <75% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6AC8BAB012DDA74F908436E243DD4CB6', X'9516D44327576347AB86B338470C6BC5', X'9516D44327576347AB86B338470C6BC5', 0, NULL, NULL, 2, NULL, 0, 'Cook onion in water for 45 minutes. Drain.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39472A7C4FAF7E4C8E07E11A47DD710C', X'9516D44327576347AB86B338470C6BC5', X'9516D44327576347AB86B338470C6BC5', 1, NULL, NULL, 2, NULL, 0, 'Grind onion, bread and lard together.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5EC2DED1931E1B44B5D40E8F6780F3AD', X'9516D44327576347AB86B338470C6BC5', X'9516D44327576347AB86B338470C6BC5', 2, NULL, NULL, 2, NULL, 0, 'Mix the ground mixture with all other ingredients.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4DAD2DBD462C9A4DA5B0DE86D6D6FA37', X'9516D44327576347AB86B338470C6BC5', X'9516D44327576347AB86B338470C6BC5', 3, NULL, NULL, 2, NULL, 0, 'Stuff into 34-36 mm pork casings. Form 40 cm (16) loops with ends tied together.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97C3F4ED13E9834DBF5D9BEA551F2719', X'9516D44327576347AB86B338470C6BC5', X'9516D44327576347AB86B338470C6BC5', 4, NULL, NULL, 2, NULL, 0, 'Dry sausages at 18° C (64° F) for three days. In traditional production the sausages were hung in the kitchen above the wood fired stove for 3 days.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D61E47A4AA6D23498B0499B8C26B4B41', X'9516D44327576347AB86B338470C6BC5', X'9516D44327576347AB86B338470C6BC5', 5, NULL, NULL, 2, NULL, 0, 'Move sausage to a drying chamber and dry them at 12-14° C (53 -57° F), 75% humidity, for 15 days.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB087AF9AAE7E64B911C613F65E254BA', X'C205FCB5C538FD4C95972FAB5CBF2041', X'C205FCB5C538FD4C95972FAB5CBF2041', 0, NULL, NULL, 2, NULL, 0, '* Red pepper paste. Cut bell peppers lengthwise into halves. Discard stems and seeds. Get a suitable pan and sprinkle salt on the bottom. Place peppers on salt, then cover them with more salt. Repeat the procedure creating a multilayer combination with salt being the top layer. Hold for 8 days. Rinse with water, pat dry with paper towel and grind through 1/8 (3 mm) plate or emulsify in food processor.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'49BA019A5BEDE441AFAD2FCDF9F36F9B', X'C205FCB5C538FD4C95972FAB5CBF2041', X'C205FCB5C538FD4C95972FAB5CBF2041', 1, NULL, NULL, 2, NULL, 0, 'Made marinade by mixing salt, cure #1 and spices with wine.');
