INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE2FD0278806014294C94A3D1B495CCB', X'C205FCB5C538FD4C95972FAB5CBF2041', X'C205FCB5C538FD4C95972FAB5CBF2041', 8, NULL, NULL, 2, NULL, 0, 'Store at 12° C (54° F), 70% humidity or in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33DC86BF3F1A174FBF5EC5094D3ECEA3', X'BDF61BE93FD72F48A9DC3B455D252530', X'BDF61BE93FD72F48A9DC3B455D252530', 0, NULL, NULL, 2, NULL, 0, '* Pepper paste. Cut bell peppers lengthwise into halves. Discard stems and seeds. Get a suitable pan and sprinkle salt on the bottom. Place peppers on salt, then cover them with more salt. Repeat the procedure creating a multilayer combination with salt being the top layer. Hold for 8 days. Rinse with water, pat dry with paper towel and grind through 1/8 (3 mm) plate or emulsify in food processor.');
