INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'550E568D46672942A2D3C83D0D3BD53A', X'2463CA30FFF5C44DA5879E9881697195', X'2463CA30FFF5C44DA5879E9881697195', 1, NULL, NULL, 2, NULL, 0, 'Mix ground beef adding pepper until the mass feels sticky. Mix diced back fat with paprika, then mix everything together. Do not add water.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F9D27B73E23A44E91D900F7F6D34390', X'2463CA30FFF5C44DA5879E9881697195', X'2463CA30FFF5C44DA5879E9881697195', 2, NULL, NULL, 2, NULL, 0, 'Stuff firmly into 40 mm beef rounds, 20-30 (50-75) cm long. Do not add water. Prick any visible air pockets with a needle.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E223353F69BF5847B54867AEFC484CCE', X'2463CA30FFF5C44DA5879E9881697195', X'2463CA30FFF5C44DA5879E9881697195', 3, NULL, NULL, 2, NULL, 0, 'Hang for 1-2 days at 2-6° C (35-42° F), 85-90% humidity. At this stage curing continues and beneficial curing and flavor forming bacteria start to grow.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A4DDBDD4BE70A149BF3FFFD40108A713', X'2463CA30FFF5C44DA5879E9881697195', X'2463CA30FFF5C44DA5879E9881697195', 4, NULL, NULL, 2, NULL, 0, 'Smoke with a thin cold smoke for 3-4 days until a yellow color with occasional red areas is obtained. At this stage natural fermentation takes place although pH drop (acidity increase) is insignificant as no sugar is added. Due to a large amount of fat (fat contains only 15% water), the sausage loses moisture rapidly.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6D5C23F672A2C4CA2F0316416016D33', X'2463CA30FFF5C44DA5879E9881697195', X'2463CA30FFF5C44DA5879E9881697195', 5, NULL, NULL, 2, NULL, 0, 'Dry at 16-12º C (60-54º F), 85-80% humidity, for 1-2 months. The sausage is dried until around 30-35% in weight is lost.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A593013FC84BF14887B79A172A59A7FE', X'2463CA30FFF5C44DA5879E9881697195', X'2463CA30FFF5C44DA5879E9881697195', 6, NULL, NULL, 2, NULL, 0, 'Store sausages at 10-15º C (50-59º F), 75% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89B7E24FA08FC24882F53D6351915B5C', X'6300568A732E9C4784E1B4184A6BA86B', X'6300568A732E9C4784E1B4184A6BA86B', 0, NULL, NULL, 2, NULL, 0, 'Grind lean pork and fat through 3 mm (1/8) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A99337E4C4F394AB1DD2E3260E50162', X'6300568A732E9C4784E1B4184A6BA86B', X'6300568A732E9C4784E1B4184A6BA86B', 1, NULL, NULL, 2, NULL, 0, 'Mix ground meats with salt and spices. Hold for 24 hours in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'78B2F94912B6F246A300A04137DFA7B9', X'6300568A732E9C4784E1B4184A6BA86B', X'6300568A732E9C4784E1B4184A6BA86B', 2, NULL, NULL, 2, NULL, 0, 'Stuff the mixture into 38-40 mm natural casings forming straight links 30 cm (12) long.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B5656B891517EB48AA8DEF1773C97D18', X'6300568A732E9C4784E1B4184A6BA86B', X'6300568A732E9C4784E1B4184A6BA86B', 3, NULL, NULL, 2, NULL, 0, 'Dry/ferment at 20-27° C (68-80° F), 90-85% humidity, for 30 hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'653693DD2A38B24489BED66D8091718F', X'6300568A732E9C4784E1B4184A6BA86B', X'6300568A732E9C4784E1B4184A6BA86B', 4, NULL, NULL, 2, NULL, 0, 'Dry at 15-12° C (59-53° F), 85-75% humidity, for 15 days.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17A24EC69C056B4CBB082C1220D89D1C', X'6300568A732E9C4784E1B4184A6BA86B', X'6300568A732E9C4784E1B4184A6BA86B', 5, NULL, NULL, 2, NULL, 0, 'Store at 10-12° C (50-53° F), <70% humidity or refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E12310F1EE13AB4BA5D19DE2C8959491', X'98E187780151A440B9BE3803C7FB9FA7', X'98E187780151A440B9BE3803C7FB9FA7', 0, NULL, NULL, 2, NULL, 0, 'Grind lean pork and belly through 6 mm (1/4) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F8B59C364546A941B42A8D9C97608FCA', X'98E187780151A440B9BE3803C7FB9FA7', X'98E187780151A440B9BE3803C7FB9FA7', 1, NULL, NULL, 2, NULL, 0, 'Mix ground meats with salt and spices. Hold for 24 hours in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2ACC02CBB69E3408C93BA2D6FAE05CC', X'98E187780151A440B9BE3803C7FB9FA7', X'98E187780151A440B9BE3803C7FB9FA7', 2, NULL, NULL, 2, NULL, 0, 'Stuff into 38-40 mm calf casings forming links 20 cm (8) long.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2579BD0B50903B40BF2BF1928F6C9E78', X'98E187780151A440B9BE3803C7FB9FA7', X'98E187780151A440B9BE3803C7FB9FA7', 3, NULL, NULL, 2, NULL, 0, 'Dry at 15-12° C (59-53° F), 85-75% humidity for 15-20 days.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D1C4BDFAF406C24EA1E0917E77C80043', X'98E187780151A440B9BE3803C7FB9FA7', X'98E187780151A440B9BE3803C7FB9FA7', 4, NULL, NULL, 2, NULL, 0, 'Store at 10-12° C (50-53° F), <70% humidity or refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92B2C4726300AF4CA3662DC0E492B002', X'019829F601D866468D77652072CB1A6B', X'019829F601D866468D77652072CB1A6B', 0, NULL, NULL, 2, NULL, 0, 'Grind meat through 6 mm (1/4) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0CF4525241E79148A9C4E075973D7E83', X'019829F601D866468D77652072CB1A6B', X'019829F601D866468D77652072CB1A6B', 1, NULL, NULL, 2, NULL, 0, 'Manually cut partially frozen back fat into 6 mm (1/4) cubes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24D9F0E61916C84ABAC14E270FC67305', X'019829F601D866468D77652072CB1A6B', X'019829F601D866468D77652072CB1A6B', 2, NULL, NULL, 2, NULL, 0, 'Mix ground meat with all ingredients. Add fat cubes and mix again.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF919D558D64E044B4D3B63AE4D18073', X'019829F601D866468D77652072CB1A6B', X'019829F601D866468D77652072CB1A6B', 3, NULL, NULL, 2, NULL, 0, 'Stuff firmly into 60 mm natural casings making 50 cm (20`) links.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D317B674B5B374DA477D38FC92255F6', X'019829F601D866468D77652072CB1A6B', X'019829F601D866468D77652072CB1A6B', 4, NULL, NULL, 2, NULL, 0, 'Hold for 2-3 days in cool room at 1° C (34° F) and 95-100% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2120412638FAEA4DBC07141FAA8FCC2E', X'019829F601D866468D77652072CB1A6B', X'019829F601D866468D77652072CB1A6B', 5, NULL, NULL, 2, NULL, 0, 'Cold smoke at 18° C (64° F) using beech wood for 12 hours. Wipe off any slime with clean wet cloth.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B213A3029421442A5B30B4CC08A18A0', X'019829F601D866468D77652072CB1A6B', X'019829F601D866468D77652072CB1A6B', 6, NULL, NULL, 2, NULL, 0, 'Ferment/dry at 9-12° C (48-53° F), 90-95% humidity. When mold appears wash it off by hand with pure water and then proceed to the drying stage.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BADA08248413174089BD5DE7CE91ABAC', X'019829F601D866468D77652072CB1A6B', X'019829F601D866468D77652072CB1A6B', 7, NULL, NULL, 2, NULL, 0, 'Dry at 10° C (50° F), 75-80% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1056793356634345A6F4F392E135FEF4', X'019829F601D866468D77652072CB1A6B', X'019829F601D866468D77652072CB1A6B', 8, NULL, NULL, 2, NULL, 0, 'Maturing/drying process lasts about 8 weeks. During that time the sausage looses around 40% of its original weight.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9EF7E0B14CF927429589FF11FE04F54A', X'3D2575D9C812DD4F931A955352B43AC0', X'3D2575D9C812DD4F931A955352B43AC0', 0, NULL, NULL, 2, NULL, 0, 'Cut fat into 1/2 (12 mm) cubes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9356730EA6582147A338511A7D9E2B86', X'3D2575D9C812DD4F931A955352B43AC0', X'3D2575D9C812DD4F931A955352B43AC0', 1, NULL, NULL, 2, NULL, 0, 'Grind pork through 1/4 (5 mm) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'085C4A88549A5742BCA01D5ABD2A9EDB', X'3D2575D9C812DD4F931A955352B43AC0', X'3D2575D9C812DD4F931A955352B43AC0', 2, NULL, NULL, 2, NULL, 0, 'Grind beef through 1/4 (5 mm) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD24E995099D414FBF1B612E7F8F450C', X'3D2575D9C812DD4F931A955352B43AC0', X'3D2575D9C812DD4F931A955352B43AC0', 3, NULL, NULL, 2, NULL, 0, 'Mix beef with salt and cure #2. Add pork and remix.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7531B6023C93B148BE940F4575DF36FA', X'3D2575D9C812DD4F931A955352B43AC0', X'3D2575D9C812DD4F931A955352B43AC0', 4, NULL, NULL, 2, NULL, 0, 'Mix spices with wine and add to the mixture. Finely, add cubed fat and mix all again.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81300CEE880DBE4AAAC72B997D1DCF34', X'3D2575D9C812DD4F931A955352B43AC0', X'3D2575D9C812DD4F931A955352B43AC0', 5, NULL, NULL, 2, NULL, 0, 'Stuff firmly into beef middles or 40-42 mm hog casings. Form 8 (20 cm) long links.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63B0E9597E5A8347A16EAC88B0A7DBFC', X'3D2575D9C812DD4F931A955352B43AC0', X'3D2575D9C812DD4F931A955352B43AC0', 6, NULL, NULL, 2, NULL, 0, 'Hang for 1-2 days at 2-6º C (35-42º F) and 85-90% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39982DBA8E33004B957BEBDF61E101C6', X'3D2575D9C812DD4F931A955352B43AC0', X'3D2575D9C812DD4F931A955352B43AC0', 7, NULL, NULL, 2, NULL, 0, 'Apply a thin cold smoke at 18º C (64º F) for 2 days. Dry at 12º C (53º F), 75-80% humidity for 3 weeks.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6607F00E6C9EB0498211A51C2BCDC78B', X'3D2575D9C812DD4F931A955352B43AC0', X'3D2575D9C812DD4F931A955352B43AC0', 8, NULL, NULL, 2, NULL, 0, 'Store at 10º C (53º F) or lower.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CDB38100590FFC448080BB319DD1021B', X'8E54AB42E4D81A49BE9DCA9B927C22C5', X'8E54AB42E4D81A49BE9DCA9B927C22C5', 0, NULL, NULL, 2, NULL, 0, 'Grind pork and back fat through 3/16 plate (5 mm).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0A5BE496DE72E4D97F0F747A9D19FE9', X'8E54AB42E4D81A49BE9DCA9B927C22C5', X'8E54AB42E4D81A49BE9DCA9B927C22C5', 1, NULL, NULL, 2, NULL, 0, 'Mix all ingredients with meat.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'728675A59315B6468C7385C7815226A5', X'8E54AB42E4D81A49BE9DCA9B927C22C5', X'8E54AB42E4D81A49BE9DCA9B927C22C5', 2, NULL, NULL, 2, NULL, 0, 'Stuff loosely (80% capacity) into large diameter fibrous casings (70 mm). Place stuffed sausage between two boards, with some weight on top to flatten the sausage. Then move to a fermentation room.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E928CD01D80156499FE848676CEFCC86', X'8E54AB42E4D81A49BE9DCA9B927C22C5', X'8E54AB42E4D81A49BE9DCA9B927C22C5', 3, NULL, NULL, 2, NULL, 0, 'Ferment at 20º C (68º F) for 96 hours, 95-90% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4453A665DDDE0E40A0EC0F3C8765FE5A', X'8E54AB42E4D81A49BE9DCA9B927C22C5', X'8E54AB42E4D81A49BE9DCA9B927C22C5', 4, NULL, NULL, 2, NULL, 0, 'Remove boards and wipe off any slime that might have accumulated under boards.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0EE859F53C541C44A496C945726BEBC1', X'8E54AB42E4D81A49BE9DCA9B927C22C5', X'8E54AB42E4D81A49BE9DCA9B927C22C5', 5, NULL, NULL, 2, NULL, 0, 'Dry at room temperature until casings are dry to the touch. Hang square shaped sausages on smokesticks.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6CA3EF235B346348A69C7013EEF7C8AA', X'8E54AB42E4D81A49BE9DCA9B927C22C5', X'8E54AB42E4D81A49BE9DCA9B927C22C5', 6, NULL, NULL, 2, NULL, 0, 'Cold smoke (20º C, 68º F) for a few hours to prevent growth of mold.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF0F29135BAD294F9E15B7C3FD77968A', X'8E54AB42E4D81A49BE9DCA9B927C22C5', X'8E54AB42E4D81A49BE9DCA9B927C22C5', 7, NULL, NULL, 2, NULL, 0, 'Dry for 2 days at 20-18º C (68-64º F), 90-85% humidity. Apply smoke from time to time.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5D151D7BFD9E04B83954ECC17ADE588', X'8E54AB42E4D81A49BE9DCA9B927C22C5', X'8E54AB42E4D81A49BE9DCA9B927C22C5', 8, NULL, NULL, 2, NULL, 0, 'Dry at 16-12º C (60-54º F), 85 -80% humidity. In about 8 weeks a shrink of 30% should be achieved.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09BF420E3AE7184688B3D519A6480B19', X'8E54AB42E4D81A49BE9DCA9B927C22C5', X'8E54AB42E4D81A49BE9DCA9B927C22C5', 9, NULL, NULL, 2, NULL, 0, 'Store sausages at 10-15º C (50-59º F), <75% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EFE9E4E056C3314C91DCACC6B8FB9357', X'CDB5080F4AC9B1478DB6452EFB42740C', X'CDB5080F4AC9B1478DB6452EFB42740C', 0, NULL, NULL, 2, NULL, 0, 'Smoke raw belly at 18° C (64° F) for 6 hours.');
