INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5CC8D0BFB4F1764E8535783BE4A73281', X'03A342E161F98A4A8F617F34BE130393', X'03A342E161F98A4A8F617F34BE130393', 6, NULL, NULL, 2, NULL, 0, 'Apply cold smoke at 18° C (64° F) for 24 hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB3F38FB9FB634478F476F54507AB89D', X'03A342E161F98A4A8F617F34BE130393', X'03A342E161F98A4A8F617F34BE130393', 7, NULL, NULL, 2, NULL, 0, 'Dry at 14?12° C (58?54° F), 85-80% humidity for 4 weeks or until sausage looses 30% of its original weight.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E4922696C361A4CBB55CBCB86A813B1', X'03A342E161F98A4A8F617F34BE130393', X'03A342E161F98A4A8F617F34BE130393', 8, NULL, NULL, 2, NULL, 0, 'Store at 10-12° C (50-53° F), 65-70% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'945D274B0C73CB4E9CAF06412E9C3D2D', X'553D378F996FB54B8064B355DE0F3FB9', X'553D378F996FB54B8064B355DE0F3FB9', 0, NULL, NULL, 2, NULL, 0, 'Curing meat: cut meat into 1 inch (25 mm) pieces. Dice fat into 3/16 (4 mm) cubes Place beef, sheep meat and sheep fat in separate food grade containers. Mix sheep fat with 3 g (1/2 tsp) salt. Mix sheep meat with 24 g (4 tsp) salt, 3/4 cure #1 and ½ sugar. Mix beef with 3 g (1/2 tsp) salt, ¼ cure #1 and ½ sugar. Estimate amounts. Pack tightly to eliminate air and cover with clean cloth. Hold for 72 hours in refrigerator. Discard any liquid brine and submit meat to grinding.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92C7A3915787E34799F66294CAD73768', X'553D378F996FB54B8064B355DE0F3FB9', X'553D378F996FB54B8064B355DE0F3FB9', 1, NULL, NULL, 2, NULL, 0, 'Grind beef through 1/8 (2 mm) plate. Grind sheep meat with 1/8 (2 mm) plate. Dice fat into 3/16 (4 mm) cubes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'695BE9A8A1F3A5499C9583F9C9501A7F', X'553D378F996FB54B8064B355DE0F3FB9', X'553D378F996FB54B8064B355DE0F3FB9', 2, NULL, NULL, 2, NULL, 0, 'Mix beef with spices. Add pork and mix. Add fat and remix everything together. Dont add any water.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07D626637EA3B74AB5692AE60CD5B069', X'553D378F996FB54B8064B355DE0F3FB9', X'553D378F996FB54B8064B355DE0F3FB9', 3, NULL, NULL, 2, NULL, 0, 'Stuff sausages firmly into 40-55 mm beef middles or fibrous casings.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A667DAEA9169654C8E24C3AF9C715837', X'553D378F996FB54B8064B355DE0F3FB9', X'553D378F996FB54B8064B355DE0F3FB9', 4, NULL, NULL, 2, NULL, 0, 'Hang sausages for 5-7 days at 2-4º C (34-40º F).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0BDE5E0284EF034684AD4769CC8DE6A8', X'553D378F996FB54B8064B355DE0F3FB9', X'553D378F996FB54B8064B355DE0F3FB9', 5, NULL, NULL, 2, NULL, 0, 'Cold smoke for 2-3 days at 18-22º C (64-72º F).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A33926CDD3C6FB448FCD3AB02DE31DDA', X'553D378F996FB54B8064B355DE0F3FB9', X'553D378F996FB54B8064B355DE0F3FB9', 6, NULL, NULL, 2, NULL, 0, 'Dry sausages for 5-7 days (depending on sausage diameter) at 11-15º C (53-59º F), 80-84 humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A681F2CE466684DBFF35D9914FA777B', X'553D378F996FB54B8064B355DE0F3FB9', X'553D378F996FB54B8064B355DE0F3FB9', 7, NULL, NULL, 2, NULL, 0, 'Dry 20-23 days more at 10-12º C (50-54º F), 74-78% humidity. Finished sausage should retain 60% of its original weight.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A89489F48A77D4B921BA2B094DB40EB', X'1E067FFCDA13B046A81AF9BBBE2BED4E', X'1E067FFCDA13B046A81AF9BBBE2BED4E', 0, NULL, NULL, 2, NULL, 0, 'Grind pork through 5 mm (1/4`) plate. Grind beef through 3 mm (1/8`) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA0F9718325C944FA4F50C53FF6E3C75', X'1E067FFCDA13B046A81AF9BBBE2BED4E', X'1E067FFCDA13B046A81AF9BBBE2BED4E', 1, NULL, NULL, 2, NULL, 0, 'Mix all ingredients with meat.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'178242249559DD40B54AC1786171A238', X'1E067FFCDA13B046A81AF9BBBE2BED4E', X'1E067FFCDA13B046A81AF9BBBE2BED4E', 2, NULL, NULL, 2, NULL, 0, 'Stuff loosely (80% capacity) into 32-36 mm hog casings. Make links 8 (20 cm) long. Place stuffed sausage between two boards, with some weight on top, to flatten the sausage. Then move to a fermentation room.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA194F1C6821C04593C94A4CB2AF5E99', X'1E067FFCDA13B046A81AF9BBBE2BED4E', X'1E067FFCDA13B046A81AF9BBBE2BED4E', 3, NULL, NULL, 2, NULL, 0, 'Ferment at 20º C (68º F) for 72 hours, 95-90% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'54D6131A7686F44E896B0E2ED3830814', X'1E067FFCDA13B046A81AF9BBBE2BED4E', X'1E067FFCDA13B046A81AF9BBBE2BED4E', 4, NULL, NULL, 2, NULL, 0, 'Remove boards and wipe off any slime that might have accumulated under boards.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0A874A2649DBE429E138BF32EC82AD0', X'1E067FFCDA13B046A81AF9BBBE2BED4E', X'1E067FFCDA13B046A81AF9BBBE2BED4E', 5, NULL, NULL, 2, NULL, 0, 'Dry at room temperature until casings are dry to the touch. Hang square shaped sausages on smoke sticks.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1F8FB1C0A2BBE4492519E4ECDC079D0', X'1E067FFCDA13B046A81AF9BBBE2BED4E', X'1E067FFCDA13B046A81AF9BBBE2BED4E', 6, NULL, NULL, 2, NULL, 0, 'Cold smoke at < 20º C (68º F) for a few hours to prevent growth of mold.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3E50A90A423734F8A8CB84BCEC3B572', X'1E067FFCDA13B046A81AF9BBBE2BED4E', X'1E067FFCDA13B046A81AF9BBBE2BED4E', 7, NULL, NULL, 2, NULL, 0, 'Dry at 14?12° C (58?54° F), 85-80% humidity for 3 weeks or until sausage looses 30% of its original weight.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BFA052CAF5EEA24B8D1A70B0B0C4C87C', X'1E067FFCDA13B046A81AF9BBBE2BED4E', X'1E067FFCDA13B046A81AF9BBBE2BED4E', 8, NULL, NULL, 2, NULL, 0, 'Store at 10-12° C (50-53° F), <75% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E36BB181EFEC34284B64D0D962C30DE', X'A8165BEC6376AB47BC2A853474DF22C8', X'A8165BEC6376AB47BC2A853474DF22C8', 0, NULL, NULL, 2, NULL, 0, 'Grind pork and back fat through 3/8 (10 mm) plate. 30 minutes before mixing dissolve starter culture in 1 tablespoon de-chlorinated water.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B28B4DF4AC1B34F8A5955522AAE8D91', X'A8165BEC6376AB47BC2A853474DF22C8', X'A8165BEC6376AB47BC2A853474DF22C8', 1, NULL, NULL, 2, NULL, 0, 'Mix all ingredients with meat.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A85AD054CC66E48B0DF204BD9BD89D0', X'A8165BEC6376AB47BC2A853474DF22C8', X'A8165BEC6376AB47BC2A853474DF22C8', 2, NULL, NULL, 2, NULL, 0, 'Stuff firmly into 18-26 mm (3/4-1) hog or sheep casings. Form 5-6 (15 cm) long links.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29974970DBD43A44ACC30E1DFBB765A3', X'A8165BEC6376AB47BC2A853474DF22C8', X'A8165BEC6376AB47BC2A853474DF22C8', 3, NULL, NULL, 2, NULL, 0, 'Ferment at 38º C (100º F) for 24 hours, 90-85% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F31AD7C964FECB4E82E4F5C98CD21FA1', X'A8165BEC6376AB47BC2A853474DF22C8', X'A8165BEC6376AB47BC2A853474DF22C8', 4, NULL, NULL, 2, NULL, 0, 'Gradually increase the temperature until the sausage reaches 60º C (140º F) internal temperature.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F33CBF133A84F499BAB922D7CE8CDCF', X'A8165BEC6376AB47BC2A853474DF22C8', X'A8165BEC6376AB47BC2A853474DF22C8', 5, NULL, NULL, 2, NULL, 0, 'For a drier sausage: dry 2 more days at 22-16º C (70-60º F), 75-65% humidity, or until desired weight loss has occurred.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0DB32FB9D5CE7A49A841C89D51A9F26C', X'A8165BEC6376AB47BC2A853474DF22C8', X'A8165BEC6376AB47BC2A853474DF22C8', 6, NULL, NULL, 2, NULL, 0, 'Store at 10-12º C (50-53º F), 65% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C6B11789EB0914FA086A63585683788', X'F7441816C3B5E8479E15F710D5F2227C', X'F7441816C3B5E8479E15F710D5F2227C', 0, NULL, NULL, 2, NULL, 0, '* Pepper paste. Cut bell peppers lengthwise into halves. Discard stems and seeds. Get a suitable pan and sprinkle salt on the bottom. Place peppers on salt, then cover them with more salt. Repeat the procedure creating a multilayer combination with salt being the top layer. Hold for 8 days. Rinse with water, pat dry with paper towel and grind through 1/8 (3 mm) plate or emulsify in food processor.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25DD1CE7A346E449B5648219BCE4A5AA', X'F7441816C3B5E8479E15F710D5F2227C', X'F7441816C3B5E8479E15F710D5F2227C', 1, NULL, NULL, 2, NULL, 0, 'Made marinade by mixing salt, cure #1, sugar and spices with wine and water.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37EA710BC8CF074D8B37C238B83D03AB', X'F7441816C3B5E8479E15F710D5F2227C', X'F7441816C3B5E8479E15F710D5F2227C', 2, NULL, NULL, 2, NULL, 0, 'Chop meat and fat into 3/8 (10 mm) pieces.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'869653FFDAF75A448EA68B9ECB0E6093', X'F7441816C3B5E8479E15F710D5F2227C', X'F7441816C3B5E8479E15F710D5F2227C', 3, NULL, NULL, 2, NULL, 0, 'Mix meat and fat with marinade and hold for 2 days at 10 °C (50° F) or lower (refrigerator). Drain. Mix marinated meat and red pepper paste together.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13DBEA8F0D0A5C408C01F42345E329D4', X'F7441816C3B5E8479E15F710D5F2227C', X'F7441816C3B5E8479E15F710D5F2227C', 4, NULL, NULL, 2, NULL, 0, 'Stuff into 36 mm hog casings, tie the ends together making 12 (30 cm) long loops.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8AD81A15B6785144B36AA9520ED35566', X'F7441816C3B5E8479E15F710D5F2227C', X'F7441816C3B5E8479E15F710D5F2227C', 5, NULL, NULL, 2, NULL, 0, 'Using oak wood apply a thin smoke at 18° C (64° F) for 7 days. Smoking need not to be continuous, as long as the temperature inside of the smokehouse stays below 18° C. Think of the process as drying with smoke. Due to seasonal variations in temperature, the sausage is smoked for 3-4 days in the summer and 5-6 days in the winter.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75E858BA4E87AC4BBF6C718A3914719E', X'F7441816C3B5E8479E15F710D5F2227C', X'F7441816C3B5E8479E15F710D5F2227C', 6, NULL, NULL, 2, NULL, 0, 'Dry at 12-15° C (54-59° F), 75-80% humidity for 10 days.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'892BBCB694DA84408A1C462D4101CD6E', X'F7441816C3B5E8479E15F710D5F2227C', X'F7441816C3B5E8479E15F710D5F2227C', 7, NULL, NULL, 2, NULL, 0, 'Store at 12° C (54° F), 70% humidity.');
