INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF23752AAF25EE479F09A55101F9F00F', X'E5724D0519BF984E9F9949542CCE821D', X'E5724D0519BF984E9F9949542CCE821D', 3, NULL, NULL, 2, NULL, 0, 'Mix meat and fat with marinade and hold for 3 days at 10° C (50° F) or lower or in refrigerator. Drain.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'200E50DA441705439848A509184C8FDE', X'E5724D0519BF984E9F9949542CCE821D', X'E5724D0519BF984E9F9949542CCE821D', 4, NULL, NULL, 2, NULL, 0, 'Mix marinated meat and red pepper paste together.');
