INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'52380D2D3331B948A8C7E6959BAE94F0', X'6C5F9A1B049F0A42A28AC9471797D08E', X'6C5F9A1B049F0A42A28AC9471797D08E', 4, NULL, NULL, 2, NULL, 0, 'Dry at 14-15° C (58-59° F), 75% humidity for 2-3 months depending on diameter. If needed, the temperature was regulated by burning wood in the drying chamber, so the loin was also cold smoked. The loin should lose about 33% of its original weight. Store at 10-12° C (°F), <75% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75FFA8BF7D998A48B1FDDF616F38B96F', X'23FCA41AD8699F419E4182F7B3AE29A9', X'23FCA41AD8699F419E4182F7B3AE29A9', 0, NULL, NULL, 2, NULL, 0, 'Trim off the skin and connective tissue from the loin. Hold in refrigerator for 24 hours at 0-2° C (32-35° F).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10C2B2A755BB0D43AF308935DDC4546E', X'23FCA41AD8699F419E4182F7B3AE29A9', X'23FCA41AD8699F419E4182F7B3AE29A9', 1, NULL, NULL, 2, NULL, 0, 'Make a strong curing solution of Baume 20° (80° Salometer) by dissolving 290 g of salt and 40 g of cure #1 in 1250 ml of water. This amount of solution is enough for 2-3 kg loin.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0FF30DE9E7F61B47A3E4FEE8B8F840F8', X'23FCA41AD8699F419E4182F7B3AE29A9', X'23FCA41AD8699F419E4182F7B3AE29A9', 2, NULL, NULL, 2, NULL, 0, 'Immerse loin into the curing solution, keep it submerged and hold in refrigerator for 3-4 days. Wash the loin briefly with water to remove salt crystals, then soak it in cold water for 12-24 hours to equalize salt distribution. Hang it to dry until the loin stops dripping water. Pat it dry with paper towels.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'01D2E6BD2EB1114AA42CD3BC125D93A4', X'23FCA41AD8699F419E4182F7B3AE29A9', X'23FCA41AD8699F419E4182F7B3AE29A9', 3, NULL, NULL, 2, NULL, 0, 'Mix spices with a little of water or white wine to create paste. Remainder: multiply the above ingredients per weight of loin.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'64316E8F339EFC4395728A1E3A3E5917', X'23FCA41AD8699F419E4182F7B3AE29A9', X'23FCA41AD8699F419E4182F7B3AE29A9', 4, NULL, NULL, 2, NULL, 0, 'Rub the paste into the loin all around and hold for 48 hours in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0030A12113F6F74EB7FEFF2C507D0AD8', X'23FCA41AD8699F419E4182F7B3AE29A9', X'23FCA41AD8699F419E4182F7B3AE29A9', 5, NULL, NULL, 2, NULL, 0, 'Stuff loin into 50-80 mm veal or beef bungs. The length of stuffed loin around 50-70 cm (20-28).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13B85E412E25784E8EC10008872A1515', X'23FCA41AD8699F419E4182F7B3AE29A9', X'23FCA41AD8699F419E4182F7B3AE29A9', 6, NULL, NULL, 2, NULL, 0, 'Ferment/dry at 18-20° C (64-68° F), 80-85% humidity, for 24-48 hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0BC80DA73E28DD4895B0475C9084F570', X'23FCA41AD8699F419E4182F7B3AE29A9', X'23FCA41AD8699F419E4182F7B3AE29A9', 7, NULL, NULL, 2, NULL, 0, 'Dry at 12-14° C (53-57° F), 75-80% humidity, for 2-3 months until the loin loses about 33% of its original weight.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3935EEA517296A4DA47ADBB7176F9E66', X'BE055EBBDA039149B5C001E348144353', X'BE055EBBDA039149B5C001E348144353', 0, NULL, NULL, 2, NULL, 0, 'Trim off the skin and connective tissue from the loin. Using force rub in a generous amount of salt (as needed) all over loin. The loin should rest in a suitable container on a layer of salt and be well covered with salt all around leaving no exposed areas. Hold in refrigerator for 24 hours.');
