INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA780DB9C0C6B54D847016F70675631E', X'BE055EBBDA039149B5C001E348144353', X'BE055EBBDA039149B5C001E348144353', 3, NULL, NULL, 2, NULL, 0, 'Wipe off the paste and stuff the loin into 55-70 mm beef bungs (tripa cular). The length of stuffed loin around 70-90 cm (28).');
