INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CFDD4787A9CB5C4AA0C3228FEC4E21CB', X'BE055EBBDA039149B5C001E348144353', X'BE055EBBDA039149B5C001E348144353', 5, NULL, NULL, 2, NULL, 0, 'Dry at 12-14° C (53-57° F), 75-80% humidity for 6-8 weeks until the sausage loses 33% of its original weight.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B7953AB87ABDFE4D86D3106A8A7E2CFA', X'C7D76FB5284991459B92D4B8155493EC', X'C7D76FB5284991459B92D4B8155493EC', 0, NULL, NULL, 2, NULL, 0, 'Remainder: multiply the above ingredients per weight of loin.');
