INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CDE4D5332379A4ABD71250BC17DA1D6', X'C7D76FB5284991459B92D4B8155493EC', X'C7D76FB5284991459B92D4B8155493EC', 3, NULL, NULL, 2, NULL, 0, 'Stuff the loin into 55-70 mm pork or beef bungs, about 50-70 cm (20-28`) long.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26CFD6280FF11F498D92F33F137C6CDD', X'C7D76FB5284991459B92D4B8155493EC', X'C7D76FB5284991459B92D4B8155493EC', 4, NULL, NULL, 2, NULL, 0, 'Dry/ferment at 18-20° C (64-68° F), 80-85% humidity, for 24-48 hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BBDB9EBBB0ADFC49849FCC7FB09C55FF', X'C7D76FB5284991459B92D4B8155493EC', X'C7D76FB5284991459B92D4B8155493EC', 5, NULL, NULL, 2, NULL, 0, 'Dry at 12-14° C (53-57° F), 75-80% humidity, for 3 months.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'56C783A5AA51AC489ED71201B91A6223', X'7B3782BAAF22034F91CE400F16E5FA79', X'7B3782BAAF22034F91CE400F16E5FA79', 0, NULL, NULL, 2, NULL, 0, 'Trim off the skin and connective tissue from the loin. Hold in refrigerator for 24 hours at 0-2° C (32-35° F).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2459D4BA47B3E444BDBAAE1DA9E455C7', X'7B3782BAAF22034F91CE400F16E5FA79', X'7B3782BAAF22034F91CE400F16E5FA79', 1, NULL, NULL, 2, NULL, 0, 'Make a strong curing solution of Baume 20° (80° Salometer) by dissolving 290 g of salt and 40 g of cure #1 in 1250 ml of water. This amount of solution is enough for 2-3 kg loin.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DFD9559775FE5146801440217356FD3F', X'7B3782BAAF22034F91CE400F16E5FA79', X'7B3782BAAF22034F91CE400F16E5FA79', 2, NULL, NULL, 2, NULL, 0, 'Immerse loin into the curing solution, keep it submerged and hold in refrigerator for 3-4 days. Wash the loin briefly with water to remove salt crystals, then soak it in cold water for 12-24 hours to equalize salt distribution. Hang it to dry until the loin stops dripping water. Pat it dry with paper towels.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8ECC62B79635B041B463908143A8B161', X'7B3782BAAF22034F91CE400F16E5FA79', X'7B3782BAAF22034F91CE400F16E5FA79', 3, NULL, NULL, 2, NULL, 0, 'Mix spices with a little of water or white wine to create paste. Remainder: multiply the above ingredients per weight of loin.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB75801373BA7640A577DE15531C8FD5', X'7B3782BAAF22034F91CE400F16E5FA79', X'7B3782BAAF22034F91CE400F16E5FA79', 4, NULL, NULL, 2, NULL, 0, 'Rub the paste into the loin all around and hold for 48 hours in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D4F1256D4E83E444BB8E6D84FC9B97E4', X'7B3782BAAF22034F91CE400F16E5FA79', X'7B3782BAAF22034F91CE400F16E5FA79', 5, NULL, NULL, 2, NULL, 0, 'Stuff into 55-70 mm pork bung forming straight links about 45-60 cm (18-24) long.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E8B7B5B2C360E4782A0D37557778F04', X'7B3782BAAF22034F91CE400F16E5FA79', X'7B3782BAAF22034F91CE400F16E5FA79', 6, NULL, NULL, 2, NULL, 0, 'Hold in a drying chamber at 10-12° C (50-53° F), 80% humidity, for 40 days.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E230EE94245504C83EEDDE520D56071', X'7B3782BAAF22034F91CE400F16E5FA79', X'7B3782BAAF22034F91CE400F16E5FA79', 7, NULL, NULL, 2, NULL, 0, 'Hold at room temperature (on in drying chamber) at 18° C (64° F) or lower until fully cured what will take about 3-4 months, depending on the diameter of the sausage and drying conditions.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ECE18429F01F7E4DBA409FD501A33EEC', X'F7EE6C5BF361B84380F88C0A95B81A90', X'F7EE6C5BF361B84380F88C0A95B81A90', 0, NULL, NULL, 2, NULL, 0, 'Dice loin pieces into 1-3 cm (1/4 - 1`) cubes. Trim off the skin and connective tissue from the loin. Mix meat with all ingredients well together. Hold in refrigerator for 48 hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'553BBEE9E5207E409AD49E4DD46D96CE', X'F7EE6C5BF361B84380F88C0A95B81A90', X'F7EE6C5BF361B84380F88C0A95B81A90', 1, NULL, NULL, 2, NULL, 0, 'Stuff tightly into 50-80 mm pork bungs, synthetic permeable casings or blind cap (caecum).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C48579EF13F8584FB82BD76C791B239E', X'F7EE6C5BF361B84380F88C0A95B81A90', X'F7EE6C5BF361B84380F88C0A95B81A90', 2, NULL, NULL, 2, NULL, 0, 'Ferment at 20-22° C (68-72° F), 95?90% humidity, for 48 hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'914534C3E701854A91EFC96C3F63B100', X'F7EE6C5BF361B84380F88C0A95B81A90', X'F7EE6C5BF361B84380F88C0A95B81A90', 3, NULL, NULL, 2, NULL, 0, 'Dry at 15?12° C (59-53° F), 85?75% humidity for 2-3 months, depending on diameter. The loin should lose around 33% of its original weight.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0DB708F74BACF64EA89FAA7D052D056B', X'F7EE6C5BF361B84380F88C0A95B81A90', X'F7EE6C5BF361B84380F88C0A95B81A90', 4, NULL, NULL, 2, NULL, 0, 'Store at 10-12° C (°F), <75% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5703A89005EAD3458BB21E632D6BF568', X'277097C9D14ABD459F14D61D8E784D4B', X'277097C9D14ABD459F14D61D8E784D4B', 0, NULL, NULL, 2, NULL, 0, 'Smash garlic and using mortar and pestle grind it with wine into the paste.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2857E693A2E07C4486CDC9016E710698', X'277097C9D14ABD459F14D61D8E784D4B', X'277097C9D14ABD459F14D61D8E784D4B', 1, NULL, NULL, 2, NULL, 0, 'Grind meat and back fat through 10-12 mm (3/8-1/2`) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5AF4B79EDF06124B9919143BD9BEE26E', X'277097C9D14ABD459F14D61D8E784D4B', X'277097C9D14ABD459F14D61D8E784D4B', 2, NULL, NULL, 2, NULL, 0, 'Mix meat, fat and all ingredients adding water as necessary to produce a firm paste. Hold the paste for 48 hours in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3037E8018A0DD4D9CAA6CCA438CFDC4', X'277097C9D14ABD459F14D61D8E784D4B', X'277097C9D14ABD459F14D61D8E784D4B', 3, NULL, NULL, 2, NULL, 0, 'Stuff into 34-36 mm pork or beef casings forming 40-60 cm (16-24`) sections.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33D2411F75335D459821B30CE9BCCB4E', X'277097C9D14ABD459F14D61D8E784D4B', X'277097C9D14ABD459F14D61D8E784D4B', 4, NULL, NULL, 2, NULL, 0, 'Dry/ferment at 22-24º C (72-75º F) for 24 hours, 90-85% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'001431BAB8EF04419F1F566DF0255EF9', X'277097C9D14ABD459F14D61D8E784D4B', X'277097C9D14ABD459F14D61D8E784D4B', 5, NULL, NULL, 2, NULL, 0, 'Dry sausages for 16 days at 12-15° C (53-59° F), 75-80% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1FACAC4E3168594586B2350DCCB300EF', X'277097C9D14ABD459F14D61D8E784D4B', X'277097C9D14ABD459F14D61D8E784D4B', 6, NULL, NULL, 2, NULL, 0, 'Store at 10° C (50° F) in a cool and ventilated area or refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33411B6B3516A1418DB3C7766BC5C8F0', X'43230DC07474184785A60EFB4F1B3303', X'43230DC07474184785A60EFB4F1B3303', 0, NULL, NULL, 2, NULL, 0, 'Grind pork with 1/8` plate.');
