INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F182D4A5CC1C544A32DD3D3B9BDA71E', X'43230DC07474184785A60EFB4F1B3303', X'43230DC07474184785A60EFB4F1B3303', 3, NULL, NULL, 2, NULL, 0, 'Ferment at 20º C (68º F) for 72 hours, 90-85% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'299B8558E1487545ACFE46CAC133580B', X'43230DC07474184785A60EFB4F1B3303', X'43230DC07474184785A60EFB4F1B3303', 4, NULL, NULL, 2, NULL, 0, 'Dry at 16-12º C (60-54º F), 85-80% humidity for about 2 months. The sausage is dried until around 30-35% in weight is lost.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2988F2E8FE3B6429593F838DE4BD8FC', X'43230DC07474184785A60EFB4F1B3303', X'43230DC07474184785A60EFB4F1B3303', 5, NULL, NULL, 2, NULL, 0, 'Store sausages at 10-15º C (50-59º F), 75% humidity.');
