INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3EB5762202C0B448956ABED1D1065964', X'290BF166A8D1304FAC896CC7F0E06A4A', X'290BF166A8D1304FAC896CC7F0E06A4A', 3, NULL, NULL, 2, NULL, 0, 'Hang the sausages for 3-4 days at 15-20° C (59-68° F), 60-70% humidity. At this point longaniza can be classified a semi-dry sausage which is served fried or cooked.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02BFFA1E37CBA9408A4CBF76E12D9828', X'290BF166A8D1304FAC896CC7F0E06A4A', X'290BF166A8D1304FAC896CC7F0E06A4A', 4, NULL, NULL, 2, NULL, 0, 'To make a dry sausage continue drying at 12-15° C (53-59° F), 75-90% for 1-2 weeks depending on diameter. The dry sausage can be consumed raw.');
