INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9BCE41A7EBC3054C8ACFA52D1049D4B8', X'2144977866A90248A2296638E4CF09C1', X'2144977866A90248A2296638E4CF09C1', 0, NULL, NULL, 2, NULL, 0, 'Cut or grind meat through a 3/8 (10 mm) plate. Cut fat into 3/8 (10 mm) cubes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'00558BB4CFC7EF40925B9A3F13845C33', X'2144977866A90248A2296638E4CF09C1', X'2144977866A90248A2296638E4CF09C1', 1, NULL, NULL, 2, NULL, 0, 'Roast pine nuts in butter or lard until light brown. Mix salt, cure #2 and spices with garum sauce then mix with ground meat. Add pepper corns, roasted pine nuts, fat and mix again.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F011E72E88EBB34282E4FE93EF0433D7', X'2144977866A90248A2296638E4CF09C1', X'2144977866A90248A2296638E4CF09C1', 2, NULL, NULL, 2, NULL, 0, 'Stuff firmly into 32-26 mm hog casings making pairs. Make links 12 (30 cm) long. Smoke with a thin cold smoke at 18° C (68° F) for two days. Dry sausage for a month at 12° C (53° F) at low humidity. Store at 12° C ( 53° F).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CEAFB2717BE78F42B742789C4F9BA962', X'2144977866A90248A2296638E4CF09C1', X'2144977866A90248A2296638E4CF09C1', 3, NULL, NULL, 2, NULL, 0, 'An easier to make hot smoked version: Decrease the amount of salt to 12 g (2 tsp). Except smoking other processing steps remain the same. Smoke sausage for 2 hours at 60° C (140° F). Increase smokehouse temperature to 85° C (185° F) and bake the sausage for 90 minutes or cook in water at 80° C (176° F) for 35 minutes. Cool in cold water, dry briefly and refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6D36E7583284740ACA40FC34D0BABB5', X'7617610A1EFAAD44B6092E3422616F98', X'7617610A1EFAAD44B6092E3422616F98', 0, NULL, NULL, 2, NULL, 0, 'Grind 1/2 lean pork (250 g) through 3 mm (1/8`) plate.');
