INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4232B4ED5E60744B2BFDDCD7951F85E', X'7617610A1EFAAD44B6092E3422616F98', X'7617610A1EFAAD44B6092E3422616F98', 2, NULL, NULL, 2, NULL, 0, 'Mix ground meats with all ingredients.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E587E945204E1468F2C81F3DE16E788', X'7617610A1EFAAD44B6092E3422616F98', X'7617610A1EFAAD44B6092E3422616F98', 3, NULL, NULL, 2, NULL, 0, 'Stuff into 60 mm pork or beef middles or permeable synthetic casings.');
