INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF73906D641BD34A9DD5300E28C31E22', X'7617610A1EFAAD44B6092E3422616F98', X'7617610A1EFAAD44B6092E3422616F98', 5, NULL, NULL, 2, NULL, 0, 'Dry at 18º C (64º F), 90 ? 85% humidity for 3 days.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'327F89C695272E488BACEA2F6EF0A618', X'7617610A1EFAAD44B6092E3422616F98', X'7617610A1EFAAD44B6092E3422616F98', 6, NULL, NULL, 2, NULL, 0, 'Dry at 15 ? 12º C (59 ? 12º F), 85 ? 75% humidity for 4-5 weeks or until the sausage looses 30% of its original weight..');
