INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E228462DC454224E89988E56014B15D9', X'4F28C4FFE7E61C47AC2059658E0AC3E0', X'4F28C4FFE7E61C47AC2059658E0AC3E0', 5, NULL, NULL, 2, NULL, 0, 'Store sausages at 10-15º C (50-59º F), 75% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF12AEDD678A984398E9580BF2116CC8', X'3C5ECC696D5F234F8921E71130D5B73A', X'3C5ECC696D5F234F8921E71130D5B73A', 0, NULL, NULL, 2, NULL, 0, 'Grind all meats through 3 mm (1/8` plate). Re-freeze meats and grind again. You may grind once and then emulsify in the food processor without adding water.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B73C94D2139EBA439538BA3AA24C2FE7', X'3C5ECC696D5F234F8921E71130D5B73A', X'3C5ECC696D5F234F8921E71130D5B73A', 1, NULL, NULL, 2, NULL, 0, 'Add all ingredients during this step.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5012517B866BBD4AAE5688493381686C', X'3C5ECC696D5F234F8921E71130D5B73A', X'3C5ECC696D5F234F8921E71130D5B73A', 2, NULL, NULL, 2, NULL, 0, 'Stuff firmly into 40-60 mm beef middles or fibrous casings. 40 cm (16`) links.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6BD001B62A4614484688A1D33278BEB', X'3C5ECC696D5F234F8921E71130D5B73A', X'3C5ECC696D5F234F8921E71130D5B73A', 3, NULL, NULL, 2, NULL, 0, 'Ferment for 48 hours at 18º C (64º F), 75-80% humidity.');
