INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FFD64662A778644DAA54BD45F948EA65', X'3C5ECC696D5F234F8921E71130D5B73A', X'3C5ECC696D5F234F8921E71130D5B73A', 5, NULL, NULL, 2, NULL, 0, 'Store in a refrigerator');
