INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6C16FFE923559429F09B81E011F0645', X'0E727DE067E84F4AADB927B6201CD093', X'0E727DE067E84F4AADB927B6201CD093', 2, NULL, NULL, 2, NULL, 0, 'Stuff firmly into 40 mm beef middles or fibrous casings. Form 8-10 (20-25 cm) links.');
