INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38EB91132D20794D80E6A3F3DAC85714', X'0E727DE067E84F4AADB927B6201CD093', X'0E727DE067E84F4AADB927B6201CD093', 4, NULL, NULL, 2, NULL, 0, 'Apply a thin cold smoke at 18-22° C (64-72° F) for 1-2 days until casings develop brown color.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'479FA4C561C7834F9F0C8A2399B6DACB', X'0E727DE067E84F4AADB927B6201CD093', X'0E727DE067E84F4AADB927B6201CD093', 5, NULL, NULL, 2, NULL, 0, 'Cool in air to 12° C (53° F) or lower. Store in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2AE6F4C39D452E4791621D46059986EC', X'6CA927E2D716594C991695AA2B2149DF', X'6CA927E2D716594C991695AA2B2149DF', 0, NULL, NULL, 2, NULL, 0, 'Grind meats through 6 mm (1/4) plate. Beat the eggs.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2249E55A7D7FBC4F911A8676FBA3410D', X'6CA927E2D716594C991695AA2B2149DF', X'6CA927E2D716594C991695AA2B2149DF', 1, NULL, NULL, 2, NULL, 0, 'Mix meats with eggs and all ingredients. Hold for 24 hours in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D366BE879FA5247AE63D3BD7BE7CB3B', X'6CA927E2D716594C991695AA2B2149DF', X'6CA927E2D716594C991695AA2B2149DF', 2, NULL, NULL, 2, NULL, 0, 'Stuff into 30 mm pork or beef casings forming rings about 25-40 cm (10-16) long. Tie the ends together.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30E432D55101724991D6C72A4322B778', X'6CA927E2D716594C991695AA2B2149DF', X'6CA927E2D716594C991695AA2B2149DF', 3, NULL, NULL, 2, NULL, 0, 'Hold at 20-25° C (60-77° F), 75-85% humidity, for 10 days.');
