INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13F6DB018535F04BBE3368D96759041A', X'78B66DECAF695447BA0A8851343E179C', X'78B66DECAF695447BA0A8851343E179C', 2, NULL, NULL, 2, NULL, 0, 'Mix meat and fat with marinade and hold for 2 days in refrigerator.');
