INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10CDD8AAE526644DA60FA2A1F8583A2C', X'78B66DECAF695447BA0A8851343E179C', X'78B66DECAF695447BA0A8851343E179C', 5, NULL, NULL, 2, NULL, 0, 'Using oak wood apply a thin smoke at 18° C (64° F) for 7 days. Smoking need not to be continuous, as long as the temperature inside of the smokehouse stays below 18° C. Think of the process as drying with smoke. Due to seasonal variations in temperature, the sausage is smoked for 3-4 days in the summer and 5-6 days in the winter.');
