INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB4DA6FB5FCBB249B58B24D71A0FD01D', X'713A42E825C28B458A24B56BBDFFC8E1', X'713A42E825C28B458A24B56BBDFFC8E1', 8, NULL, NULL, 2, NULL, 0, 'Store at 10-12° C (50-53° F), <75% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2610C13FE47A084890A2DDAC817842B0', X'61D9FAB11F950D499054C00F1B946096', X'61D9FAB11F950D499054C00F1B946096', 0, NULL, NULL, 2, NULL, 0, 'Grind the lean meat with a 3/8 grinder plate and the fat meat through 3/16 plate.');
