INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7A77E870E541C469142993B71229E47', X'61D9FAB11F950D499054C00F1B946096', X'61D9FAB11F950D499054C00F1B946096', 3, NULL, NULL, 2, NULL, 0, 'Hang for 1-2 days at 2-6°C (35-42°F) and 85-90% humidity.');
