INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'31B5A11659882140A4FB57483D2A6BD7', X'61D9FAB11F950D499054C00F1B946096', X'61D9FAB11F950D499054C00F1B946096', 5, NULL, NULL, 2, NULL, 0, 'Dry at 10-12°C (50-53°F) and 75-80% humidity until the yield is 87% in relation to the original weight of the meat.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15037390CEB1E94784CB1FE342372814', X'61D9FAB11F950D499054C00F1B946096', X'61D9FAB11F950D499054C00F1B946096', 6, NULL, NULL, 2, NULL, 0, 'Divide into pairs.');
