INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3BC32D3EAE0AF24BA002739F9D56201A', X'ED59E69C4C39CD4B876EB1D877FF6A84', X'ED59E69C4C39CD4B876EB1D877FF6A84', 0, NULL, NULL, 2, NULL, 0, 'Grind all meats and fat through 5 mm plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3DCEDFDFC1180C44B242242512824F4E', X'ED59E69C4C39CD4B876EB1D877FF6A84', X'ED59E69C4C39CD4B876EB1D877FF6A84', 1, NULL, NULL, 2, NULL, 0, 'Mix ground meats with all ingredients until sticky.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB0264B3C5051741A3F1F5E1A73E3F3D', X'ED59E69C4C39CD4B876EB1D877FF6A84', X'ED59E69C4C39CD4B876EB1D877FF6A84', 2, NULL, NULL, 2, NULL, 0, 'Place the mixture in a container for 24 hours at 2-4° C (35-40° F) which is refrigerator temperature.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'86E62DC7A7A93941A415861FD625DF92', X'ED59E69C4C39CD4B876EB1D877FF6A84', X'ED59E69C4C39CD4B876EB1D877FF6A84', 3, NULL, NULL, 2, NULL, 0, 'Stuff into 60 mm synthetic fibrous casings.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'630D03E68FBBFC47BC87696ED4BE7C21', X'ED59E69C4C39CD4B876EB1D877FF6A84', X'ED59E69C4C39CD4B876EB1D877FF6A84', 4, NULL, NULL, 2, NULL, 0, 'Place sausages in a container one on top of another and hold for 4-5 days at 2-4° C (35-40°F), 85-90% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63A29E0FB6501A4CAE5042FFE8F91CE6', X'ED59E69C4C39CD4B876EB1D877FF6A84', X'ED59E69C4C39CD4B876EB1D877FF6A84', 5, NULL, NULL, 2, NULL, 0, 'Hang for 2-3 days at 8-10° C (46-50° F), 80-85% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DCA8B58351304C42B10131859095D886', X'ED59E69C4C39CD4B876EB1D877FF6A84', X'ED59E69C4C39CD4B876EB1D877FF6A84', 6, NULL, NULL, 2, NULL, 0, 'Apply cold smoke for 2-3 days until brown in color.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0FC100741A6FDC4898273C9066C7B172', X'ED59E69C4C39CD4B876EB1D877FF6A84', X'ED59E69C4C39CD4B876EB1D877FF6A84', 7, NULL, NULL, 2, NULL, 0, 'Cool in air to 10-12° C (50-53° F) or lower. Hold at this temperature at 75-85% humidity for 7-10 days.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8D67C0DAFA7944A97EDD52D34A7915F', X'1B5E7202587079448F1D86EE0AAA9CA3', X'1B5E7202587079448F1D86EE0AAA9CA3', 0, NULL, NULL, 2, NULL, 0, 'Curing meat. Cut meat into 1 inch (25 mm) pieces. Place beef, pork and fat in separate food grade containers and mix beef and pork with salt, Cure #1 and sugar according to the recipe (estimate amounts). Fat needs salt only. Pack tightly to eliminate air and cover with clean cloth. Place for 72 hours in refrigerator. Discard any liquid brine and submit meat to grinding.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9395CBF37CC18D40BEC6996B5CAF3815', X'1B5E7202587079448F1D86EE0AAA9CA3', X'1B5E7202587079448F1D86EE0AAA9CA3', 1, NULL, NULL, 2, NULL, 0, 'Grind beef through 1/8` (2 mm) plate. Grind pork through 1/8` (2 mm) plate. Dice pork belly into 1/8` (3 mm) cubes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4362EAAD431362449C912F453564DCE8', X'1B5E7202587079448F1D86EE0AAA9CA3', X'1B5E7202587079448F1D86EE0AAA9CA3', 2, NULL, NULL, 2, NULL, 0, 'Mix beef with spices. Add pork and wine and mix. Add fat cubes and remix everything together. Dont add any water.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF831248C1CFD34485308FE6370D3795', X'1B5E7202587079448F1D86EE0AAA9CA3', X'1B5E7202587079448F1D86EE0AAA9CA3', 3, NULL, NULL, 2, NULL, 0, 'Stuff sausages firmly into 35-45 mm beef rounds or hog casings. Sausage in ring shape, inside diameter 3-6 (8-15 cm). Both ends tied together.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8DA8F3BA2EBBE346B5C1E668DB19460C', X'1B5E7202587079448F1D86EE0AAA9CA3', X'1B5E7202587079448F1D86EE0AAA9CA3', 4, NULL, NULL, 2, NULL, 0, 'Hang sausages for 5-7 days at 2-4 C, 85-90% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4FCAC94671C68F468ED5B53CD9C9A325', X'1B5E7202587079448F1D86EE0AAA9CA3', X'1B5E7202587079448F1D86EE0AAA9CA3', 5, NULL, NULL, 2, NULL, 0, 'Using dry wood, smoke sausages for 2-3 days with cold smoke, 18-22 C.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'611E1CB48163184FBE45B6CB9007364D', X'1B5E7202587079448F1D86EE0AAA9CA3', X'1B5E7202587079448F1D86EE0AAA9CA3', 6, NULL, NULL, 2, NULL, 0, 'Dry sausages for 5-7 days (depending on sausage diameter) at 11-15 C, 80-84 humidity. Dry 20-23 days more at 10-12 C, 74-78% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C00211EFA677ED47B97F3B0154C025FB', X'3016ECC742DB794D83393CDEAE40AECA', X'3016ECC742DB794D83393CDEAE40AECA', 0, NULL, NULL, 2, NULL, 0, 'Grind lean pork and belly through 5 mm (1/4`) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC522EC4E531DD40BD07A62A0CEC148C', X'3016ECC742DB794D83393CDEAE40AECA', X'3016ECC742DB794D83393CDEAE40AECA', 1, NULL, NULL, 2, NULL, 0, 'Mix the sausage mass with all ingredients.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33EF15717DD2064AA9007422E796FF9F', X'3016ECC742DB794D83393CDEAE40AECA', X'3016ECC742DB794D83393CDEAE40AECA', 2, NULL, NULL, 2, NULL, 0, 'Stuff into 36 mm pork casings forming 36 cm (15`) links.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21A1B0DB45FA7E42BA479C560E80A100', X'3016ECC742DB794D83393CDEAE40AECA', X'3016ECC742DB794D83393CDEAE40AECA', 3, NULL, NULL, 2, NULL, 0, 'Ferment for 48 hours at 18° C (64° F), 75-80% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E09E438A9A511E4BAB420D175F6978E4', X'3016ECC742DB794D83393CDEAE40AECA', X'3016ECC742DB794D83393CDEAE40AECA', 4, NULL, NULL, 2, NULL, 0, 'Cold smoke at 18° C (64° F) for 12 hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B60B43CE01258144B62E557FEBF0870F', X'3016ECC742DB794D83393CDEAE40AECA', X'3016ECC742DB794D83393CDEAE40AECA', 5, NULL, NULL, 2, NULL, 0, 'Refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B84A0366D811964B8B175C41A7A665BA', X'0C24109FD0976642997DCF757702A140', X'0C24109FD0976642997DCF757702A140', 0, NULL, NULL, 2, NULL, 0, 'Grind meat through 5/16 (8 mm) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41A10C836C391C48A048F26D25EB77F7', X'0C24109FD0976642997DCF757702A140', X'0C24109FD0976642997DCF757702A140', 1, NULL, NULL, 2, NULL, 0, 'Using a sharp knife cut partially frozen back fat into 5/16 (8 mm) cubes. Dilute culture in 1 tablespoon of de-chlorinated water 3o minutes before mixing.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07EC2224E19B9549A577D67A46936AC5', X'0C24109FD0976642997DCF757702A140', X'0C24109FD0976642997DCF757702A140', 2, NULL, NULL, 2, NULL, 0, 'Mix ground lean meat with salt and cure #2 until sticky.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40B4C0311FE285448E4AE95A99908990', X'0C24109FD0976642997DCF757702A140', X'0C24109FD0976642997DCF757702A140', 3, NULL, NULL, 2, NULL, 0, 'Add fat cubes, culture and spices and mix all together.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'64C97B81D073384BB8B4E29D3E6C19C8', X'0C24109FD0976642997DCF757702A140', X'0C24109FD0976642997DCF757702A140', 4, NULL, NULL, 2, NULL, 0, 'Stuff firmly into 50 mm hog or beef middles.Ferment at 20º C (68º F) for 72 hours, 90-85% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D1E3141FFEB8BC449FB5C110DF89C7F2', X'0C24109FD0976642997DCF757702A140', X'0C24109FD0976642997DCF757702A140', 5, NULL, NULL, 2, NULL, 0, 'Dry at 16 ? 12º C (60-54º F), 85 ? 80% humidity for 45 days. The sausage is dried until around 30% in weight is lost. The sausage should develop a white mold which is expected and desired.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C5773E8689C384694CC74AEF064EE65', X'0C24109FD0976642997DCF757702A140', X'0C24109FD0976642997DCF757702A140', 6, NULL, NULL, 2, NULL, 0, 'Store sausages at 10-12º C (50-53º F), <75% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'61727CC33953FC409125FDCDBD913F89', X'4676AEB22BE74746BB3AB4D575323160', X'4676AEB22BE74746BB3AB4D575323160', 0, NULL, NULL, 2, NULL, 0, 'Cut meat and fat into 5-6 cm (2) pieces, mix with salt, sugar and Cure # 1 and place in a cool area for 4 - 5 days.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BCA899B5BA2E344591EF22FF6D204D39', X'4676AEB22BE74746BB3AB4D575323160', X'4676AEB22BE74746BB3AB4D575323160', 1, NULL, NULL, 2, NULL, 0, 'Grind beef with 3/8 (10 mm) plate. Manually cut back fat into ¼ (6 mm) cubes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C21F6366D5132744B5ECE43855CBCB7A', X'4676AEB22BE74746BB3AB4D575323160', X'4676AEB22BE74746BB3AB4D575323160', 2, NULL, NULL, 2, NULL, 0, 'Mix all ingredients with meat adding wine. Mix until all wine is absorbed by the mixture. Then add diced fat and mix everything together.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38A5BADB36858E4D977E9567072BBA3F', X'4676AEB22BE74746BB3AB4D575323160', X'4676AEB22BE74746BB3AB4D575323160', 3, NULL, NULL, 2, NULL, 0, 'Stuff into dry beef middles or synthetic fibrous casings 40-60 mm and form 12 (30 cm) long links.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E3FACECB1808CA409B1CAFC55262F662', X'4676AEB22BE74746BB3AB4D575323160', X'4676AEB22BE74746BB3AB4D575323160', 4, NULL, NULL, 2, NULL, 0, 'Hang for 2 days in a cool place.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B109F95CB58A2D449326804DF278BF02', X'4676AEB22BE74746BB3AB4D575323160', X'4676AEB22BE74746BB3AB4D575323160', 5, NULL, NULL, 2, NULL, 0, 'Cold smoke for 6 days.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1257F2ABC81464F9AE4B8733C648640', X'4676AEB22BE74746BB3AB4D575323160', X'4676AEB22BE74746BB3AB4D575323160', 6, NULL, NULL, 2, NULL, 0, 'Dry for 25 - 30 days in a cool, dry and drafty area.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B3D41971E94232498B536ACC8AB6ED01', X'58FA97E8708D174D911BDC897579B5BF', X'58FA97E8708D174D911BDC897579B5BF', 0, NULL, NULL, 2, NULL, 0, 'Cook heart, lungs, stomach in water (below boiling point) until soft. Scald liver in hot water for 5 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F81015D28AC264998E274FFDA1764BC', X'58FA97E8708D174D911BDC897579B5BF', X'58FA97E8708D174D911BDC897579B5BF', 1, NULL, NULL, 2, NULL, 0, 'Grind beef and fat through 10 mm (3/8`) plate. Grind offal meats through 3 mm (1/8`) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D62420F6FF20E54A914B9AFC01DAF541', X'58FA97E8708D174D911BDC897579B5BF', X'58FA97E8708D174D911BDC897579B5BF', 2, NULL, NULL, 2, NULL, 0, 'Mix meats, fat and all ingredients together. Hold for 12 hours in refrigerator. Stuff into 40-50 mm pork or beef middles.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E4A16C89AFB7C418817C8FCDD5BA7F9', X'58FA97E8708D174D911BDC897579B5BF', X'58FA97E8708D174D911BDC897579B5BF', 3, NULL, NULL, 2, NULL, 0, 'Ferment/dry at 25° C (77° F), 90% humidity for 48 hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A996B20F72B1464AA0398C53CE31C700', X'58FA97E8708D174D911BDC897579B5BF', X'58FA97E8708D174D911BDC897579B5BF', 4, NULL, NULL, 2, NULL, 0, 'Dry at 14-15° C (57-59° F), 70% humidity for 30 days.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E8520DB4222DA54287C57396660EF063', X'58FA97E8708D174D911BDC897579B5BF', X'58FA97E8708D174D911BDC897579B5BF', 5, NULL, NULL, 2, NULL, 0, 'Store at 10-12° C (50-52° F), 60-65% humidity or refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7AE68F9368CB8C4C8B284F9ACDC8E83A', X'47BA9669382B084D80B3B0AEB9A4D4B9', X'47BA9669382B084D80B3B0AEB9A4D4B9', 0, NULL, NULL, 2, NULL, 0, 'Grind lean pork and belly through 5 mm (1/4`) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EACA413233D92F48AB41F1E1FDFC5D39', X'47BA9669382B084D80B3B0AEB9A4D4B9', X'47BA9669382B084D80B3B0AEB9A4D4B9', 1, NULL, NULL, 2, NULL, 0, 'Mix with all ingredients.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F9EEF5619045A048AD8727603EDC0546', X'47BA9669382B084D80B3B0AEB9A4D4B9', X'47BA9669382B084D80B3B0AEB9A4D4B9', 2, NULL, NULL, 2, NULL, 0, 'Stuff into 42 mm pork or beef casings forming 50 cm (20`) rings.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C116A5E33E49A45B1D64D6647854F40', X'47BA9669382B084D80B3B0AEB9A4D4B9', X'47BA9669382B084D80B3B0AEB9A4D4B9', 3, NULL, NULL, 2, NULL, 0, 'Ferment for 24-48 hours at 18° C (64° F), 75-80% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C22DA3822BC504092BE2A5BB653AA8F', X'47BA9669382B084D80B3B0AEB9A4D4B9', X'47BA9669382B084D80B3B0AEB9A4D4B9', 4, NULL, NULL, 2, NULL, 0, 'Cold smoke at 18° C (64° F) for 12 hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB16A7943879A8469C4902A7D53F68A1', X'47BA9669382B084D80B3B0AEB9A4D4B9', X'47BA9669382B084D80B3B0AEB9A4D4B9', 5, NULL, NULL, 2, NULL, 0, 'Refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD9FC2F23DDB2C4486EE7058C9BD94A2', X'7A2FA2DA1714934E919B1E1F7C4BAF14', X'7A2FA2DA1714934E919B1E1F7C4BAF14', 0, NULL, NULL, 2, NULL, 0, 'Grind lean meat through 1/8-3/16 (3-4 mm) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16C85DBBF49AB2428E1F0501FE0AB617', X'7A2FA2DA1714934E919B1E1F7C4BAF14', X'7A2FA2DA1714934E919B1E1F7C4BAF14', 1, NULL, NULL, 2, NULL, 0, 'Cut fat into 1/8-3/16 (3-4 mm) cubes or grind through 1/8-3/16 (3-4 mm) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8FF834692EC2B34FB2B4342A74F38C19', X'7A2FA2DA1714934E919B1E1F7C4BAF14', X'7A2FA2DA1714934E919B1E1F7C4BAF14', 2, NULL, NULL, 2, NULL, 0, '30 minutes before mixing dissolve starter culture in 1 tablespoon de-chlorinated water.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F4F278717A95244AF6C42718EC251DF', X'7A2FA2DA1714934E919B1E1F7C4BAF14', X'7A2FA2DA1714934E919B1E1F7C4BAF14', 3, NULL, NULL, 2, NULL, 0, 'Mix lean pork with salt and cure #2. Add spices and culture. Mix. Add diced fat and mix all together.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09065766555BDE42876D7F1356038EED', X'7A2FA2DA1714934E919B1E1F7C4BAF14', X'7A2FA2DA1714934E919B1E1F7C4BAF14', 4, NULL, NULL, 2, NULL, 0, 'Stuff firmly into 60-90 mm horse or collagen casings making 8-12 (20-30 cm) straight links, clipped or tied at both ends.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F29BF2EBEF4DFB4CB4236DCA66B28080', X'7A2FA2DA1714934E919B1E1F7C4BAF14', X'7A2FA2DA1714934E919B1E1F7C4BAF14', 5, NULL, NULL, 2, NULL, 0, 'Dry sausages for 24 hours at 10-15° C (50-59° F) with moderate air circulation.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'32CC8487BCBB4D42BF9541662F697C31', X'7A2FA2DA1714934E919B1E1F7C4BAF14', X'7A2FA2DA1714934E919B1E1F7C4BAF14', 6, NULL, NULL, 2, NULL, 0, 'Apply cold smoke at 10-24° C (50-75° F) for 3 to maximum 10 days, with relative humidity of 85 to 92%, using oak, beech or mixture of both woods. Smoking does not have to be continuous as long as low temperature is maintained. Cold smoking is drying with smoke. Fermentation takes place during the smoking step.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0F32DB3779B3F4BA01E089547C96F0A', X'7A2FA2DA1714934E919B1E1F7C4BAF14', X'7A2FA2DA1714934E919B1E1F7C4BAF14', 7, NULL, NULL, 2, NULL, 0, 'When no more smoke is applied (smoke prevents growth of mold) the sausages are sprayed with Penicillium nalgiovense mold growing culture. The temperature is maintained at 10-24° C (50-75° F), 85% humidity for about 10-12 days.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD6CC3BEA6423247BD2C4A321AF54A78', X'7A2FA2DA1714934E919B1E1F7C4BAF14', X'7A2FA2DA1714934E919B1E1F7C4BAF14', 8, NULL, NULL, 2, NULL, 0, 'When the sausages are completely covered with white, grey, or white yellow mold, they are manually brushed what creates a better look and facilitates drying. Then the sausages are dried/matured at 10-15° C (50-59° F) for about 60 days. As the drying continues the humidity is lowered.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB1E13F101C5E1459EB5DE74776E04E6', X'7A2FA2DA1714934E919B1E1F7C4BAF14', X'7A2FA2DA1714934E919B1E1F7C4BAF14', 9, NULL, NULL, 2, NULL, 0, 'Store sausages at 10-12° C (50°-55 F), 60-70% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'395AB269354E8F46970D3018F55D4846', X'357CCD9E5F9D594BAAE91E8DAC09D738', X'357CCD9E5F9D594BAAE91E8DAC09D738', 0, NULL, NULL, 2, NULL, 0, '1. Chop the tongue into 1/4 (6 mm) cubes and marinade in red wine for at least 2 hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CD231638577C54D9FD23A85D727572A', X'357CCD9E5F9D594BAAE91E8DAC09D738', X'357CCD9E5F9D594BAAE91E8DAC09D738', 1, NULL, NULL, 2, NULL, 0, '2. Cut the liver into 1/8 (3-4 mm) slices.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E3A77738CBB6E34FB462AB7BE81642E1', X'357CCD9E5F9D594BAAE91E8DAC09D738', X'357CCD9E5F9D594BAAE91E8DAC09D738', 2, NULL, NULL, 2, NULL, 0, '3. Dice back fat into 1/4 (6 mm) cubes.');
