INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1F09440A7C54140B3BC8DBB36E911B6', X'0644AA2B1DACAF4C858674F9055FC037', X'0644AA2B1DACAF4C858674F9055FC037', 0, NULL, NULL, 2, NULL, 0, 'Curing: cut meat into 3-4 (10 cm) pieces and place in a slightly raised container with holes in the bottom to allow for draining of liquid. Leave for 24 hours at 1-2° C (33-35° F), then grind with ¾ plate and leave for additional 2-3 days. During that period turn meat around 1-2 times. Leave sheets of unsalted back fat for 2-3 days at -2° C (28° F) to - 4° C (24° F) and then dice into 1/8 3 mm cubes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE3EDEC859E7C74784CD378E2DBFAE4A', X'0644AA2B1DACAF4C858674F9055FC037', X'0644AA2B1DACAF4C858674F9055FC037', 1, NULL, NULL, 2, NULL, 0, 'Mix meat, back fat, salt, nitrite and spices together. Grind through 1/8 (3 mm) plate. Leave the sausage mass for 36-48 hours at 2-4° C (35-40° F).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'623FCBFE44863446B872CA1F94E11E9B', X'0644AA2B1DACAF4C858674F9055FC037', X'0644AA2B1DACAF4C858674F9055FC037', 2, NULL, NULL, 2, NULL, 0, 'Stuff firmly into 55-60 mm beef middles. Make links 16-18 long. Lace up with twine: once lengthwise and every 4-5 cm (1.5-2) across. The ends tied up with a twine, 10-12 cm (4-5) hanging loop on one end.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82474B1CAE9B5F4BA7213161EF319DD4', X'0644AA2B1DACAF4C858674F9055FC037', X'0644AA2B1DACAF4C858674F9055FC037', 3, NULL, NULL, 2, NULL, 0, 'Hang for 2-4 days at 2-4° C (35-40° F), 85-90% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB2B591DBBAA534F832582307249409F', X'0644AA2B1DACAF4C858674F9055FC037', X'0644AA2B1DACAF4C858674F9055FC037', 4, NULL, NULL, 2, NULL, 0, 'Apply a thin cold smoke 16-18° C (60-64° F) for 5-7 days, until dark red color is obtained.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE81FE8373C3854AB0805D2CFB65AE5B', X'0644AA2B1DACAF4C858674F9055FC037', X'0644AA2B1DACAF4C858674F9055FC037', 5, NULL, NULL, 2, NULL, 0, 'Dry in a dark, lightly drafty area at 10-12° C (50-53° F), humidity 90%, for 2 weeks until salami develops white, dry mold on outside. If green mold appears wipe it off and move the sausage for 4-5 hours to a drier place. Then it can go back to the original room for drying.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6639B3493B49EE46A68E0140F2E22A86', X'0644AA2B1DACAF4C858674F9055FC037', X'0644AA2B1DACAF4C858674F9055FC037', 6, NULL, NULL, 2, NULL, 0, 'Place covered with white mold sausages for 2-3 months in a dark and lightly drafty area at 12-15° C (54-59° F), 75-85% humidity, until 63% yield is obtained.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A85B113FB648904AAD7EBEB79C8B079B', X'9EBB9F58D801C24C83AA26B3773DC190', X'9EBB9F58D801C24C83AA26B3773DC190', 0, NULL, NULL, 2, NULL, 0, 'Cut meat and fat into 2 (5 - 6 cm) pieces. Add salt, Cure # 2, garlic and pepper and mix well.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B3FB75CC0D33F443BF51A7B971454252', X'9EBB9F58D801C24C83AA26B3773DC190', X'9EBB9F58D801C24C83AA26B3773DC190', 1, NULL, NULL, 2, NULL, 0, 'Grind mixture through 3/16 (4 mm) plate. Pack ground meat tightly into suitable containers (stainless steel, food grade plastic) and cover with a layer of skins or butcher paper. Keep for 10 - 14 days at 35º - 39º F (2º - 4º C).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33896CEC5ADB3547BE630F1EE90772E8', X'9EBB9F58D801C24C83AA26B3773DC190', X'9EBB9F58D801C24C83AA26B3773DC190', 2, NULL, NULL, 2, NULL, 0, 'Stuff into 60 mm beef middles or synthetic fibrous casings, make straight links 40 - 45 cm long. Pack the meat very tightly to eliminate air pockets. Make 10 - 12 cm loop at one end for hanging.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'759A07F0601D3E46879C8F9DCE078226', X'9EBB9F58D801C24C83AA26B3773DC190', X'9EBB9F58D801C24C83AA26B3773DC190', 3, NULL, NULL, 2, NULL, 0, 'Hang for two hours in a well ventilated area.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB2205F429B58B40A09CCD09DC5BCFEE', X'9EBB9F58D801C24C83AA26B3773DC190', X'9EBB9F58D801C24C83AA26B3773DC190', 4, NULL, NULL, 2, NULL, 0, 'Smoking:');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6ECE02CA08E0A44CADE30B6CAE1020CF', X'9EBB9F58D801C24C83AA26B3773DC190', X'9EBB9F58D801C24C83AA26B3773DC190', 5, NULL, NULL, 2, NULL, 0, 'Hang in a shaded well ventilated area at 10º-12º C, 80 - 85 % humidity for 5 - 6 weeks until salami develops white mold on outside. In case the mold becomes wet and green in color wash the sausages with warm salty water and wipe them dry. Dry salami until it loses 35 % of its original weight.');
