INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'67184378640348408AF7BA28E0411B6F', X'8FB3AB2B255E2C4BA623DFB5FE89754E', X'8FB3AB2B255E2C4BA623DFB5FE89754E', 0, NULL, NULL, 2, NULL, 0, 'Cut meat into 10 cm (3-4) pieces and place in a slightly raised container with holes in the bottom to allow for draining of curing liquid. Leave for 24 hours at 1-2°C (33-35°F). Then grind with ¾ plate and leave for an additional 2-3 days following the above procedure. During that period turn meat around 1-2 times. Leave sheets of unsalted back fat for 2-3 days at -2° C (28° F) to -4° C (24° F) and then cut into 3 mm (?) pieces.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A6B6B7B99440F4698C012FDF94BE29C', X'8FB3AB2B255E2C4BA623DFB5FE89754E', X'8FB3AB2B255E2C4BA623DFB5FE89754E', 1, NULL, NULL, 2, NULL, 0, 'Mix meat, back fat, salt, nitrate and spices together. Grind through 3 mm (?) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'373CF0F9D0ED7D4FADA1DE89E5EDC16D', X'8FB3AB2B255E2C4BA623DFB5FE89754E', X'8FB3AB2B255E2C4BA623DFB5FE89754E', 2, NULL, NULL, 2, NULL, 0, 'Leave the sausage mass for 36-48 hours at 2-4°C (35-40°F).');
