INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C61D70A1BA23974AABD17D3964AE8BA4', X'6D02C109714B154EB02340A7FBDF94FA', X'6D02C109714B154EB02340A7FBDF94FA', 0, NULL, NULL, 2, NULL, 0, 'Grind lean pork through 3/8 (10 mm) plate. Cut pork belly into 5/16 (8 mm) cubes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1061967122001499EE74FAFD50381AC', X'6D02C109714B154EB02340A7FBDF94FA', X'6D02C109714B154EB02340A7FBDF94FA', 1, NULL, NULL, 2, NULL, 0, 'Mix meat and spices together.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD1C489463143A40B40468F4A843E39D', X'6D02C109714B154EB02340A7FBDF94FA', X'6D02C109714B154EB02340A7FBDF94FA', 2, NULL, NULL, 2, NULL, 0, 'Stuff into pork stomach, bladder or artificial casing (the artificial casing must be permeable to allow the moisture escape).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'561B3E776AAA754EA6BEE35BBFF06E81', X'6D02C109714B154EB02340A7FBDF94FA', X'6D02C109714B154EB02340A7FBDF94FA', 3, NULL, NULL, 2, NULL, 0, 'Hold for 10 days in a cool room (4° C/40° F) between two wooden planks. Place weight on top board..');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9BF416CC9A31C489C749DCCDC6287CC', X'6D02C109714B154EB02340A7FBDF94FA', X'6D02C109714B154EB02340A7FBDF94FA', 4, NULL, NULL, 2, NULL, 0, 'Apply a thin cold smoke at 18° C (64° F) for 2 days.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8415DB65EB1F4C41803912CDD8820719', X'6D02C109714B154EB02340A7FBDF94FA', X'6D02C109714B154EB02340A7FBDF94FA', 5, NULL, NULL, 2, NULL, 0, 'Dry between 10-15° C (50-59° F), 60-80% humidity for 3-5 months.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF50D1D1B6893D4991BDC618C28CB0E3', X'EB0E7BCAC4E15F4EADF26CF953A5338B', X'EB0E7BCAC4E15F4EADF26CF953A5338B', 0, NULL, NULL, 2, NULL, 0, 'Grind lean pork and pork belly through 6-8 mm (1/4`) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD85C9885A2CC6478445EC9E875C4B20', X'EB0E7BCAC4E15F4EADF26CF953A5338B', X'EB0E7BCAC4E15F4EADF26CF953A5338B', 1, NULL, NULL, 2, NULL, 0, 'Mix all ingredients with meat. Hold in refrigerator for 24 hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BFF85BE3728E8C439456DD2C87D0414F', X'EB0E7BCAC4E15F4EADF26CF953A5338B', X'EB0E7BCAC4E15F4EADF26CF953A5338B', 2, NULL, NULL, 2, NULL, 0, 'Stuff into 32-34 mm pork casings making straight links.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5ACFF53B9C352F458CE1B8AA95DBFB63', X'EB0E7BCAC4E15F4EADF26CF953A5338B', X'EB0E7BCAC4E15F4EADF26CF953A5338B', 3, NULL, NULL, 2, NULL, 0, 'Dry at 14-15º C (54-59º F), 75-80% humidity for 12 days.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CFF3AED03E73464DBE95B3CB0A353089', X'EB0E7BCAC4E15F4EADF26CF953A5338B', X'EB0E7BCAC4E15F4EADF26CF953A5338B', 4, NULL, NULL, 2, NULL, 0, 'Store sausages at 10-12º C (50-53º F), <75% humidity or refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'706A765F710BC845B43EE4C3EE2E0215', X'B78D94A7BDC07D43BFFC15851631FEDB', X'B78D94A7BDC07D43BFFC15851631FEDB', 0, NULL, NULL, 2, NULL, 0, 'Curing: cut beef into 1-2 (2.5-5 cm) pieces and mix with 12 g salt and 2.0 g cure # 1. Cut belly into 1-2 (2.5-5 cm) pieces and mix with 9 g salt and 0.5 g cure # 1. Pack tightly in separately container, cover with cloth and keep in refrigerator for 3 days.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C47723637E8EEA4E94BB5F8FF8D18A4C', X'B78D94A7BDC07D43BFFC15851631FEDB', X'B78D94A7BDC07D43BFFC15851631FEDB', 1, NULL, NULL, 2, NULL, 0, 'Grind beef through 5 mm plate. Partially freeze and grind again. Grind pork belly through 1/4 (5 mm) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5890E5CA8A3EC7409ED2A50F493D69C9', X'B78D94A7BDC07D43BFFC15851631FEDB', X'B78D94A7BDC07D43BFFC15851631FEDB', 2, NULL, NULL, 2, NULL, 0, 'Mix all meats with spices until sticky.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EF10C0B78E915240AA3B7440E4CB68E7', X'B78D94A7BDC07D43BFFC15851631FEDB', X'B78D94A7BDC07D43BFFC15851631FEDB', 3, NULL, NULL, 2, NULL, 0, 'Stuff firmly into 65 mm synthetic fibrous casings forming 10 (25 cm) links.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA230B646066064882239C71A0066E0C', X'B78D94A7BDC07D43BFFC15851631FEDB', X'B78D94A7BDC07D43BFFC15851631FEDB', 4, NULL, NULL, 2, NULL, 0, 'Hang for 1-2 days at 2-4° C (35-40° F), 85-90% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'633A281E4180674DBDA61640F88AE182', X'B78D94A7BDC07D43BFFC15851631FEDB', X'B78D94A7BDC07D43BFFC15851631FEDB', 5, NULL, NULL, 2, NULL, 0, 'Cold smoke at 18° C (64° F) for 1-2 days until casings are brown. Keep refrigerated.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39D593EB7647EB489982343EE19372A9', X'A28266305AE6644F915369FF96C58D7D', X'A28266305AE6644F915369FF96C58D7D', 0, NULL, NULL, 2, NULL, 0, 'Curing meat. Cut meat into 1 inch (25 mm) pieces. Place beef, sheep meat and sheep fat in separate food grade containers. Mix sheep fat with 3 g (1/2 tsp) salt. Mix sheep meat with 24 g (4 tsp) salt, 3/4 Cure #1 and ½ sugar. Mix beef with 3 g (1/2 tsp) salt, ¼ cure #1 and ½ sugar. Pack tightly to eliminate air and cover with clean cloth. Place for 72 hours in refrigerator. Discard any liquid brine and submit meat to grinding.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'44F6292AA66F0846A5D5CD64A438F570', X'A28266305AE6644F915369FF96C58D7D', X'A28266305AE6644F915369FF96C58D7D', 1, NULL, NULL, 2, NULL, 0, 'Grind beef through 1/8` (2 mm) plate. Grind sheep meat with 1/8 (2 mm) plate. Dice fat into 3/16 (4 mm) cubes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17B1E56AC1E3624296DED4DBBF35DD08', X'A28266305AE6644F915369FF96C58D7D', X'A28266305AE6644F915369FF96C58D7D', 2, NULL, NULL, 2, NULL, 0, 'Mix beef with salt, cure, sugar and spices. Add pork and mix. Add bacon strips and remix everything together. Dont add any water.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'649CAE8016D7644D92526D5802753419', X'A28266305AE6644F915369FF96C58D7D', X'A28266305AE6644F915369FF96C58D7D', 3, NULL, NULL, 2, NULL, 0, 'Stuff sausages firmly into 40-55 mm beef middles or fibrous casings.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6713292E5B76404EBF508AC7EB88546B', X'A28266305AE6644F915369FF96C58D7D', X'A28266305AE6644F915369FF96C58D7D', 4, NULL, NULL, 2, NULL, 0, 'Hang sausages for 5-7 days at 2-4 C.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'61E31570F4912E4095494816B65CB528', X'A28266305AE6644F915369FF96C58D7D', X'A28266305AE6644F915369FF96C58D7D', 5, NULL, NULL, 2, NULL, 0, 'Using dry wood, smoke sausages for 2-3 days with cold smoke, 18-22 C.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'86EFFC408C26984B8DD5725BCF8D0D69', X'A28266305AE6644F915369FF96C58D7D', X'A28266305AE6644F915369FF96C58D7D', 6, NULL, NULL, 2, NULL, 0, 'Dry sausages for 5-7 days (depending on sausage diameter) at 11-15 C, 80-84 humidity. Dry 20-23 days more at 10-12 C, 74-78% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9991CE2E13973144885CF9D02603CC7C', X'BEEE8A3D2438954AB7556374E7A2A191', X'BEEE8A3D2438954AB7556374E7A2A191', 0, NULL, NULL, 2, NULL, 0, 'Cut meat and belly into 1.5 (30 mm) pieces and mix with pepper and smashed garlic.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'623442C01D7AE546AF7120748F03B72A', X'BEEE8A3D2438954AB7556374E7A2A191', X'BEEE8A3D2438954AB7556374E7A2A191', 1, NULL, NULL, 2, NULL, 0, 'Fry salt briefly on a hot pan (removes moisture), stirring often. Rub salt thoroughly into the meat. Add sugar, cure #2 and alcohol. Pure alcohol evaporates rapidly removing moisture at the same time.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1821DF8EBB3E0840AD133F4CF322879A', X'BEEE8A3D2438954AB7556374E7A2A191', X'BEEE8A3D2438954AB7556374E7A2A191', 2, NULL, NULL, 2, NULL, 0, 'Mix everything well together. Stuff firmly into pork stomach, bladder or 60 mm fibrous casing. Avoid creating air pockets. Reinforce with butchers twine: two loops lengthwise and loops across the casings every 2 (5 cm). Form 10-12 cm (4-5) hanging loop on one end.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C83DDE5FEDEC4F4FBAF815F4E345A941', X'BEEE8A3D2438954AB7556374E7A2A191', X'BEEE8A3D2438954AB7556374E7A2A191', 3, NULL, NULL, 2, NULL, 0, 'Hang for at least 10 days in a cool, dry and ventilated area. This is when curing, drying and fermenting are taking place.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9AF77F94D28A2E499570600DDE1CC36A', X'BEEE8A3D2438954AB7556374E7A2A191', X'BEEE8A3D2438954AB7556374E7A2A191', 4, NULL, NULL, 2, NULL, 0, 'Cold smoke (below 18º C, 64º F) for 3 weeks, applying smoke 3-4 hours daily. On the last day of smoking add juniper berries or juniper twigs into the fire.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E8407CAA46D3B545A0B5E0798CF6DA9B', X'BEEE8A3D2438954AB7556374E7A2A191', X'BEEE8A3D2438954AB7556374E7A2A191', 5, NULL, NULL, 2, NULL, 0, 'Dry for 2 months at 8° C (46° F) and 75% humidity. The sausage should lose about 35% of its original weight.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'779724CA729EF348AD9C138BED23BFE9', X'BEEE8A3D2438954AB7556374E7A2A191', X'BEEE8A3D2438954AB7556374E7A2A191', 6, NULL, NULL, 2, NULL, 0, 'Store in a dark, cool and dry place.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F045EE3180F734D8FE00D78690EFC4C', X'09270124E17D4042BA32ABFD52788687', X'09270124E17D4042BA32ABFD52788687', 0, NULL, NULL, 2, NULL, 0, 'Grind meat through 3/8 (10 mm) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'287C1BB5647AE2419FD7BD98EB463E8B', X'09270124E17D4042BA32ABFD52788687', X'09270124E17D4042BA32ABFD52788687', 1, NULL, NULL, 2, NULL, 0, '30 minutes before mixing dissolve starter culture in 1 tablespoon de-chlorinated water.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5CC0C0976DB9E04994C1AFCF42BEA91A', X'09270124E17D4042BA32ABFD52788687', X'09270124E17D4042BA32ABFD52788687', 2, NULL, NULL, 2, NULL, 0, 'Mix lean pork with salt and cure #2. Add spices and wine. Lastly, add culture and mix all together.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D44DF4C909384B44B19534E64C8C58BE', X'09270124E17D4042BA32ABFD52788687', X'09270124E17D4042BA32ABFD52788687', 3, NULL, NULL, 2, NULL, 0, 'Stuff tightly into pork caecum, bung or bladder.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'753386ADC218A043BD3E1C23210CC4DE', X'09270124E17D4042BA32ABFD52788687', X'09270124E17D4042BA32ABFD52788687', 4, NULL, NULL, 2, NULL, 0, 'Ferment at 20º C (68º F) for 72 hours, 90-85% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E3D8EDE0DA72AD46880009CF20BE7725', X'09270124E17D4042BA32ABFD52788687', X'09270124E17D4042BA32ABFD52788687', 5, NULL, NULL, 2, NULL, 0, 'Apply a thin cold smoke at 18° C (64° F) for 4 weeks using oak, beech or mixture of both woods. Maintain high humidity. Smoking does not have to be continuous as long as a low temperature is maintained. Cold smoking is drying with smoke. Fermentation takes place during the smoking step.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2039A81D7F467A48A07B73B8873AFC2E', X'09270124E17D4042BA32ABFD52788687', X'09270124E17D4042BA32ABFD52788687', 6, NULL, NULL, 2, NULL, 0, 'Dry at 15?12° C (59?53° F), 80-75% humidity for 6 weeks.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0889C08CA904CB42BF551B41A3F4FD1F', X'09270124E17D4042BA32ABFD52788687', X'09270124E17D4042BA32ABFD52788687', 7, NULL, NULL, 2, NULL, 0, 'Dry at 10-12° C (50°-55 F), 75% humidity for 10 weeks.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D3F6A9BF6384645A381038E3943752E', X'09270124E17D4042BA32ABFD52788687', X'09270124E17D4042BA32ABFD52788687', 8, NULL, NULL, 2, NULL, 0, 'Store at 10-12° C (50-53° F), <75% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A0E05034E14FF49A0C68C54B9BCCF54', X'841B857D841F784F960CFD41D2F7A5C5', X'841B857D841F784F960CFD41D2F7A5C5', 0, NULL, NULL, 2, NULL, 0, 'Grind all meats through 3/16 plate (5 mm).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BBFDDD808720304B9BACE556F78D8B71', X'841B857D841F784F960CFD41D2F7A5C5', X'841B857D841F784F960CFD41D2F7A5C5', 1, NULL, NULL, 2, NULL, 0, 'Mix all ingredients with ground meat.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A668165ACA4FEA4B81033D7D4955F24A', X'841B857D841F784F960CFD41D2F7A5C5', X'841B857D841F784F960CFD41D2F7A5C5', 2, NULL, NULL, 2, NULL, 0, 'Stuff into beef middles or 90-100 mm fibrous casings.');
