INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D110CFE0BD6E246A6199A3C90B1EC90', X'A8F8C8012AB033458EC6F97AFFE67192', X'A8F8C8012AB033458EC6F97AFFE67192', 7, NULL, NULL, 2, NULL, 0, 'Dry at 14?12° C (57?53° F), 80-75% humidity for 5 weeks or until sausage looses 33% of its original weight.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB9F3B999433174496DB6FCE9BA9A0B1', X'A8F8C8012AB033458EC6F97AFFE67192', X'A8F8C8012AB033458EC6F97AFFE67192', 8, NULL, NULL, 2, NULL, 0, 'Store at 12° C (53° F),<75% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69C12F7EF4312C49A51BAFE680832B93', X'718754DE17592B43ACF6F7C2BDEA2E2B', X'718754DE17592B43ACF6F7C2BDEA2E2B', 0, NULL, NULL, 2, NULL, 0, 'Grind lean pork through 3/8 (10 mm) plate. Cut back fat into 5/16 (8 mm) cubes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDDE03C6EC6AE847BC081A1017D03FD2', X'718754DE17592B43ACF6F7C2BDEA2E2B', X'718754DE17592B43ACF6F7C2BDEA2E2B', 1, NULL, NULL, 2, NULL, 0, 'Mix meat and spices together.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D73C3A4E479C84E8048DB579E74C0D6', X'718754DE17592B43ACF6F7C2BDEA2E2B', X'718754DE17592B43ACF6F7C2BDEA2E2B', 2, NULL, NULL, 2, NULL, 0, 'Stuff into pork stomach, bladder or artificial casing.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DFB7532DD297F5409965382FA421BF74', X'718754DE17592B43ACF6F7C2BDEA2E2B', X'718754DE17592B43ACF6F7C2BDEA2E2B', 3, NULL, NULL, 2, NULL, 0, 'Hold for 10 days in a cool room (4° C/40° F) between two wooden planks.Place some weight on top board.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E03E8843D002C4E92E21C71B57ABB5F', X'718754DE17592B43ACF6F7C2BDEA2E2B', X'718754DE17592B43ACF6F7C2BDEA2E2B', 4, NULL, NULL, 2, NULL, 0, 'Dry between 12-18° C (53-64° F), 60-80% humidity for 3-5 months. The sausage should loose at least 36% of its original weight. It should be covered with a grayish mold.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'942DC43D3123F0438736A8378CA51829', X'718754DE17592B43ACF6F7C2BDEA2E2B', X'718754DE17592B43ACF6F7C2BDEA2E2B', 5, NULL, NULL, 2, NULL, 0, 'Store at 8° C (48° F) or lower.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7634785C55C067409334CF0E7B9AEFFC', X'1B71BDEF07E2D4479C54A3F8736361EA', X'1B71BDEF07E2D4479C54A3F8736361EA', 0, NULL, NULL, 2, NULL, 0, 'Except spleen, brains and blood, poach other meats in a small amount of water:lungs, tripe and bloody meats at 85°C (185°F) until soft heads, skins, lips, udders, tripe and pork stomachs at 95°C (203°F) until soft jowls at 85°C (185°F) until semi-soft');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D397266D3262E840BA27A47CD89DA204', X'1B71BDEF07E2D4479C54A3F8736361EA', X'1B71BDEF07E2D4479C54A3F8736361EA', 1, NULL, NULL, 2, NULL, 0, 'After poaching remove gristle from lungs and meat from bones. Spread meats apart on a flat surface to cool.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0EF832D9EA42124F8C0830555848026E', X'1B71BDEF07E2D4479C54A3F8736361EA', X'1B71BDEF07E2D4479C54A3F8736361EA', 2, NULL, NULL, 2, NULL, 0, 'Cut jowls into 8-10 mm (3-4) cubes. Grind udders and lungs through 5 mm plate. Grind raw spleen ground through 3 mm plate. Grind all other meats through 3 mm plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD8E56A5873E2E45A218057B7E691F00', X'1B71BDEF07E2D4479C54A3F8736361EA', X'1B71BDEF07E2D4479C54A3F8736361EA', 3, NULL, NULL, 2, NULL, 0, 'Mix all ground meats with spices and blood.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'52B849DF20FC6742AB3BB96810721314', X'1B71BDEF07E2D4479C54A3F8736361EA', X'1B71BDEF07E2D4479C54A3F8736361EA', 4, NULL, NULL, 2, NULL, 0, 'Stuffing materials: veal bladders or 60 mm (2¼) synthetic casings. Butchers twine. Stuff casings loosely and sew the bladder ends with twine. Tie the synthetic casing ends with twine.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'45C43F5AC4D70C48B3756BD32550B1E0', X'1B71BDEF07E2D4479C54A3F8736361EA', X'1B71BDEF07E2D4479C54A3F8736361EA', 5, NULL, NULL, 2, NULL, 0, 'Poach bladders at 82°C (180°F) for 78-80 min (depending on size) and synthetic casings at 78-80°C (172-176°F) until the internal temperature of the meat reaches 68-70°C (154-158°F). Remove air with a needle from pieces that swim up to the surface.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'54A1560E8F157145BE3DA8FC9AC168AA', X'1B71BDEF07E2D4479C54A3F8736361EA', X'1B71BDEF07E2D4479C54A3F8736361EA', 6, NULL, NULL, 2, NULL, 0, 'Spread head cheeses on a flat surface at 2-6°C (35-43°F) and let the steam out. Then flatten them with weight and cool them to below 6°C (43°F). Do not flatten synthetic casings. If there is no proper cooler available, cool them down to less than 12°C (53°F).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FABD7E49B9C2D44B96108EF705ED3286', X'1B71BDEF07E2D4479C54A3F8736361EA', X'1B71BDEF07E2D4479C54A3F8736361EA', 7, NULL, NULL, 2, NULL, 0, 'After cooling clean head cheeses of any fat and aspic that accumulated on the surface, even them out and cut off excess twine.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'600E7867F1D1DA4A9007EB020A0EDDF1', X'1BA45F1535056D40BB45CFBA60C3CE99', X'1BA45F1535056D40BB45CFBA60C3CE99', 0, NULL, NULL, 2, NULL, 0, 'Cure all meats in wet brine. Make 40° Salometer brine (10° Baume):');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A0394297C33DF4EAEE54C8FEAB2C1F7', X'1BA45F1535056D40BB45CFBA60C3CE99', X'1BA45F1535056D40BB45CFBA60C3CE99', 1, NULL, NULL, 2, NULL, 0, '1 gal water, 333 g (0.73 lb) salt, 120 g (4.2 oz) Cure #1. About 3/4 qt (710 ml) of brine is needed for 1 kg of meat. Place meats in brine in a suitable container. Hold them completely submerged for 4-5 days. Drain and wash briefly.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36A364F56D955A4182A7A0F0122DB6F9', X'1BA45F1535056D40BB45CFBA60C3CE99', X'1BA45F1535056D40BB45CFBA60C3CE99', 2, NULL, NULL, 2, NULL, 0, 'Cover meats with sufficient amount of water and cook at 95° C (203° F) until soft (about 2 hours). For best flavor add soup greens to water: celery, carrot, leek, parsley root, whole peppers, onion, allspice. Remove meat from bones when still warm. The skins are done when they can be penetrated with a finger. Save meat stock.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7EC9A8B6F264EE449EBDB01518F5983F', X'1BA45F1535056D40BB45CFBA60C3CE99', X'1BA45F1535056D40BB45CFBA60C3CE99', 3, NULL, NULL, 2, NULL, 0, 'Chop onions and fry in fat until golden.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6BBC1F4DFEA7EE44BFDDAAC2AE118C3A', X'1BA45F1535056D40BB45CFBA60C3CE99', X'1BA45F1535056D40BB45CFBA60C3CE99', 4, NULL, NULL, 2, NULL, 0, 'Cut lean meats and tongues into 12 mm (1/2`) pieces.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5330823610D2104896582921A68B89FC', X'1BA45F1535056D40BB45CFBA60C3CE99', X'1BA45F1535056D40BB45CFBA60C3CE99', 5, NULL, NULL, 2, NULL, 0, 'Cut bellies into long strips.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B198161C45073A478C7423E00B0B729F', X'1BA45F1535056D40BB45CFBA60C3CE99', X'1BA45F1535056D40BB45CFBA60C3CE99', 6, NULL, NULL, 2, NULL, 0, 'Grind skins and onions through 3 mm (1/8`) plate. Place in food processor and emulsify with milk and spices.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'67BA8DE67612C34B8DC67F6758729A6F', X'1BA45F1535056D40BB45CFBA60C3CE99', X'1BA45F1535056D40BB45CFBA60C3CE99', 7, NULL, NULL, 2, NULL, 0, 'Mix all meats with emulsified skins and meat stock.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B640790F3949946B1482481F963F1A3', X'1BA45F1535056D40BB45CFBA60C3CE99', X'1BA45F1535056D40BB45CFBA60C3CE99', 8, NULL, NULL, 2, NULL, 0, 'Stuff into pork stomachs or 100 mm synthetic waterproof casings.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9CD9F803815E442A095F1E26D8194FD', X'1BA45F1535056D40BB45CFBA60C3CE99', X'1BA45F1535056D40BB45CFBA60C3CE99', 9, NULL, NULL, 2, NULL, 0, 'In a large pot bring the water to a boil. Insert sausages in hot water, lower the heat and cook pork stomachs at 80° C (176° F) for 180 minutes, 100 mm casings for 2 hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6428557DA017FD49BCD094FB93885D9F', X'1BA45F1535056D40BB45CFBA60C3CE99', X'1BA45F1535056D40BB45CFBA60C3CE99', 10, NULL, NULL, 2, NULL, 0, 'Immerse sausages for 20 minutes in cold water, then place them on a flat surface to finish cooling. Roll over periodically the sausages stuffed into round synthetic casings to prevent jelly and fat from accumulating in one area.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6FBD0FCE05A67D48BD84E179F1355821', X'1BA45F1535056D40BB45CFBA60C3CE99', X'1BA45F1535056D40BB45CFBA60C3CE99', 11, NULL, NULL, 2, NULL, 0, 'Refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51C552ABF6452045806DE238625EDADC', X'C1B33E981992A046AFA1F2E33F994741', X'C1B33E981992A046AFA1F2E33F994741', 0, NULL, NULL, 2, NULL, 0, 'Curing. Cut meats into 1 inch (25 mm) pieces and then mix with salt and cure 1. Pack tight and place in refrigerator for 48 hours. Meats not cured will not develop pink color.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B1A34D0086EFC4983D2C2DCDB23C133', X'C1B33E981992A046AFA1F2E33F994741', X'C1B33E981992A046AFA1F2E33F994741', 1, NULL, NULL, 2, NULL, 0, 'Poach meats in a small amount of water hearts, kidneys, lungs at 85°C (185°F) until soft. skins, heads and legs at 95°C (203°F) until soft. jowls at 85°C (185°F) until semi-soft. After poaching remove gristle from lungs and meat from heads and legs. Spread apart on a flat surface to cool.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0BCA47858DDF184192B93B0703054DEA', X'C1B33E981992A046AFA1F2E33F994741', X'C1B33E981992A046AFA1F2E33F994741', 2, NULL, NULL, 2, NULL, 0, 'Cut jowls into 10-12 mm ( 3/8-1/2) cubes. Grind skins through 1/8` (3 mm) plate. Grind other meats through 1/8` (3 mm) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E54078F6CBC5C44A4FD2B1AA41C46A2', X'C1B33E981992A046AFA1F2E33F994741', X'C1B33E981992A046AFA1F2E33F994741', 3, NULL, NULL, 2, NULL, 0, 'Mix salt, spices, semolina or bread crumbs with ground meats and blood.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ADD04A000142514A91C7439E7C018AAC', X'C1B33E981992A046AFA1F2E33F994741', X'C1B33E981992A046AFA1F2E33F994741', 4, NULL, NULL, 2, NULL, 0, 'Stuffing. Sausage casings: pork ( middles, bladders or stomachs), veal (bungs), lamb (bungs), beef (large or medium bungs), 100 mm synthetic casings. Stuff casings loosely and sew (or tie) the ends with butcher twine.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09C36AE1FF0D72429972D02CF164CCAB', X'C1B33E981992A046AFA1F2E33F994741', X'C1B33E981992A046AFA1F2E33F994741', 5, NULL, NULL, 2, NULL, 0, 'Cook head cheeses in water at 82°C (180°F) for 90-120 min (depending on size) until the internal temperature of the meat reaches 68-70°C (154-158°F). Remove air with a needle from pieces that swim up to the surface.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'937B6D58EFC66840A8CAE49C3ACAB94B', X'C1B33E981992A046AFA1F2E33F994741', X'C1B33E981992A046AFA1F2E33F994741', 6, NULL, NULL, 2, NULL, 0, 'Spread head cheeses on a flat surface at 2-6°C (35-43°F) and let the steam out. Then flatten them with weight and cool them to below 6°C (43°F). If there is no proper cooler available, cool them down to less than 12°C (53°F)');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F315D80756589A42AB16B0A85EB96692', X'C1B33E981992A046AFA1F2E33F994741', X'C1B33E981992A046AFA1F2E33F994741', 7, NULL, NULL, 2, NULL, 0, 'After cooling clean head cheeses of any fat and aspic that accumulated on the surface, even them out and cut off excess twine.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B2E389E9A6F184418D1F9BE69B401A32', X'918526C551118E41AEE7A5EF2777351C', X'918526C551118E41AEE7A5EF2777351C', 0, NULL, NULL, 2, NULL, 0, 'Cook meats (except liver) in small amount of water until soft: snouts, lungs, jowls at 85°C (185°F); skins and heads at 95°C (203°F); fat at 85°C (185°F) until semi-soft.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'31262609AE9D6E47B540FD2AADAF317D', X'918526C551118E41AEE7A5EF2777351C', X'918526C551118E41AEE7A5EF2777351C', 1, NULL, NULL, 2, NULL, 0, 'Remove gristle from lungs and meat from heads when still warm. Spread apart on a flat surface to cool.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'08A53D1B688E364EBE8DBFB1BD22B78C', X'918526C551118E41AEE7A5EF2777351C', X'918526C551118E41AEE7A5EF2777351C', 2, NULL, NULL, 2, NULL, 0, 'Cut jowls and snouts into 3/8 -1/2 (10-12 mm) cubes. Remaining meats ground through 1/8 (3 mm) plate');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'805BF73DDF337F4EAB46FCF010310D49', X'918526C551118E41AEE7A5EF2777351C', X'918526C551118E41AEE7A5EF2777351C', 3, NULL, NULL, 2, NULL, 0, 'Mix all meats, salt, Cure #1 and spices well together.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C1BC2C9E191A14885C1181EE9760230', X'918526C551118E41AEE7A5EF2777351C', X'918526C551118E41AEE7A5EF2777351C', 4, NULL, NULL, 2, NULL, 0, 'Stuff loosely into stomach, bladder, pork middles or 100 mm synthetic strong casings. Cook in water at 82° C (180° F) for 90-150 min (depending on size) until meat reaches 68-70° C (154-158° F) internal temperature. Remove air with a needle from pieces that swim up to the surface.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4EDC5ED498FFB45906AE15C3639CC62', X'918526C551118E41AEE7A5EF2777351C', X'918526C551118E41AEE7A5EF2777351C', 5, NULL, NULL, 2, NULL, 0, 'Spread head cheeses on a flat surface and let the steam out. Flatten stomachs with weight and cool to 6°C (43°F) or less.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'250D4A68F7BF4548BADA2C27F6515257', X'918526C551118E41AEE7A5EF2777351C', X'918526C551118E41AEE7A5EF2777351C', 6, NULL, NULL, 2, NULL, 0, 'Clean head cheeses of any fat and aspic that accumulated on the surface, even them out and cut off excess twine. The shape of the finished product: flattened pork or beef bladders in a shape of irregular disk up to 10 (25 cm) diameter and 3  (8 cm) thick; flattened pork middles, 7-10 (18-25 cm) long, about 6 (15 cm) wide and 3 (7 cm) high.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BDD9F54642616F48A82C3116C14007D7', X'9B6544D94FAE0D4E8370E72647905FBF', X'9B6544D94FAE0D4E8370E72647905FBF', 0, NULL, NULL, 2, NULL, 0, 'Cook meats in a small amount of water: pork heads at 85°C (185°F), and pork skins at 95°C (203°F) until soft. Add soup greens for better flavor.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A9579C3BA9702478ED32DE0101F7236', X'9B6544D94FAE0D4E8370E72647905FBF', X'9B6544D94FAE0D4E8370E72647905FBF', 1, NULL, NULL, 2, NULL, 0, 'Spread meats on a flat surface to cool. Save meat stock. Separate meat from bones when still warm.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1AF19163FA3ADB4AB5920C6E16DA4028', X'9B6544D94FAE0D4E8370E72647905FBF', X'9B6544D94FAE0D4E8370E72647905FBF', 2, NULL, NULL, 2, NULL, 0, 'Cut meat into smaller pieces, cut skins into short strips. Mix all meats with spices adding 10% of meat stock in relation to the weight of pork heads with bones. The meat stock is the result of boiling meats.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD44EF401FE440499AB8F700BAD8F1A4', X'9B6544D94FAE0D4E8370E72647905FBF', X'9B6544D94FAE0D4E8370E72647905FBF', 3, NULL, NULL, 2, NULL, 0, 'Stuff mixture loosely into hog stomachs or beef bungs about 12 (30 cm) long.Cook in water at 82°C (180°F) for 90-150 min (depending on size) until the internal temperature of the meat reaches 154-158°F (68-70°C). Remove air with a needle from pieces that swim up to the surface.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0AC59209A09F14F83FA50F042F47F57', X'9B6544D94FAE0D4E8370E72647905FBF', X'9B6544D94FAE0D4E8370E72647905FBF', 4, NULL, NULL, 2, NULL, 0, 'Spread head cheeses on a flat surface at 2-6°C (35-43°F) and let the steam out. Place sausages between two boards and place heavy weight on the top. Leave undisturbed for 12 hours at cool temperature. After cooling clean head cheeses of any fat and aspic that accumulated on the surface. Store in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE7E864320E772469965AFE970794F3D', X'1AA61D126CD3ED42A60D9FC1949D4804', X'1AA61D126CD3ED42A60D9FC1949D4804', 0, NULL, NULL, 2, NULL, 0, 'Cut all meats and fat into 18 mm (3/4) pieces.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'378F11E15883314A9E3CF2EB5F7BF02D', X'1AA61D126CD3ED42A60D9FC1949D4804', X'1AA61D126CD3ED42A60D9FC1949D4804', 1, NULL, NULL, 2, NULL, 0, 'Mix with blood and all ingredients.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D409C1D065B5F241B7C15AC1908659FE', X'1AA61D126CD3ED42A60D9FC1949D4804', X'1AA61D126CD3ED42A60D9FC1949D4804', 2, NULL, NULL, 2, NULL, 0, 'Stuff into pork stomachs or bladders.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C9401DFF17B6F145813314AC1B0646C9', X'1AA61D126CD3ED42A60D9FC1949D4804', X'1AA61D126CD3ED42A60D9FC1949D4804', 3, NULL, NULL, 2, NULL, 0, 'Cook in water at 80-90° C (176-194° F) for 2-3 hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'598B18BEB26EC74DBC6B2F7D8DFDDB4F', X'1C0007EEAC0D3F459CE41AB5ACC9EDDF', X'1C0007EEAC0D3F459CE41AB5ACC9EDDF', 0, NULL, NULL, 2, NULL, 0, 'Making Broth: 1/2 pork picnic (front leg) or pork butt. The amount of boiled meat available for further processing will be about 2.2 lbs. (1 kg). Picnic meat is fattier with more connective tissues (collagen) and will produce more gelatin than a butt. The butt has only one little bone and its meat is leaner than a picnic and a commercial gelatin (natural product made from bones and skins) will have to be added. Two pig feet (hocks). If you use hocks and picnic enough natural gelatin (broth) should be produced to create jelly on cooling. If pork butt is the only meat used you will have to mix your own gelatin. 3 bay leaves 1 stalk celery 1 carrot Salt and pepper to taste 1 package of gelatin (if needed)');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F2751A15E2C884C8CF0AAC1B7761CA9', X'1C0007EEAC0D3F459CE41AB5ACC9EDDF', X'1C0007EEAC0D3F459CE41AB5ACC9EDDF', 1, NULL, NULL, 2, NULL, 0, 'Place meats and other ingredients in a pot and cover with about two inches of water. Cook below boiling point for about 3 hours or until meat separates easily from bones. Strain liquid and save for later.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2FC4D0F8FFFA4F478601365640691E4D', X'1C0007EEAC0D3F459CE41AB5ACC9EDDF', X'1C0007EEAC0D3F459CE41AB5ACC9EDDF', 2, NULL, NULL, 2, NULL, 0, 'Separate meats from bones. It is easier to perform this task when meats are still warm. Cut meats into smaller pieces. It is easier to cut them when they are chilled. Normally the leaner the meat the larger diameter the cut. Fatter meats and fat will be chopped into smaller pieces.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3690A2932407E24A8402425783849ECF', X'1C0007EEAC0D3F459CE41AB5ACC9EDDF', X'1C0007EEAC0D3F459CE41AB5ACC9EDDF', 3, NULL, NULL, 2, NULL, 0, 'Dissolve gelatin in 1/4 cup room temperature water and add to your hot meat broth.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'702FBB2D7635E04EA0143450E65AAB46', X'1C0007EEAC0D3F459CE41AB5ACC9EDDF', X'1C0007EEAC0D3F459CE41AB5ACC9EDDF', 4, NULL, NULL, 2, NULL, 0, 'Mix meats with meat broth/gelatin and add garlic and salt and pepper to taste. Head cheese does not require much salt and a low sodium product can be made.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7BF03D9A9EA18B45AFC91E96B8ED4434', X'1C0007EEAC0D3F459CE41AB5ACC9EDDF', X'1C0007EEAC0D3F459CE41AB5ACC9EDDF', 5, NULL, NULL, 2, NULL, 0, 'Now you can go two different ways: pour your head cheese into containers, let them sit for 2 hours at room temperature and then place in a refrigerator. Keep it there for 12 hours to give the head cheese time to set. stuff with a ladle into large diameter waterproof casings, clip the ends and cook (simmer) in hot water at about, 185º F (85º C). A rough estimate will be about 20 min for each ½ (12 mm) of diameter of a casing. Or using a thermometer cook to 160° F (72° C) internal meat temperature. Then place at room temperature for about 2 hours for gelatin to set. Place for 12 hours in a refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2B0C2E7DD7E94408F314FF761E0B102', X'4359C9889F93134CA05DACF6586CED66', X'4359C9889F93134CA05DACF6586CED66', 0, NULL, NULL, 2, NULL, 0, 'Wash pork heads and legs in cold running water.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A28D2EBB017A2442B51FDF6E78146F7D', X'4359C9889F93134CA05DACF6586CED66', X'4359C9889F93134CA05DACF6586CED66', 1, NULL, NULL, 2, NULL, 0, 'Poach heads and legs in separate containers at 185º F, (85º C) until meat can be removed from the bone (try not to cook the meat). Save some of the left over stock liquid for later. Spread all cuts on a flat surface to cool.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F26D87628367C246866C0B81483FBF41', X'4359C9889F93134CA05DACF6586CED66', X'4359C9889F93134CA05DACF6586CED66', 2, NULL, NULL, 2, NULL, 0, 'Cut head meat into 1/2 (12 mm) cubes. Grind leg meat with 3/16 (3 mm) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C765AFD2E22C76478A1C96B397AD9F3B', X'4359C9889F93134CA05DACF6586CED66', X'4359C9889F93134CA05DACF6586CED66', 3, NULL, NULL, 2, NULL, 0, 'Mix all meats with salt, spices, blood and add some of the saved stock.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B920A1ACC465F648BC815FCF11FA44B4', X'4359C9889F93134CA05DACF6586CED66', X'4359C9889F93134CA05DACF6586CED66', 4, NULL, NULL, 2, NULL, 0, 'Stuff mixture not too tight into hog stomachs or beef bungs about 12 (30 cm) long, 8 (20 cm ) wide and 3 (8 cm) thick. Saw the ends with butchers twine.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C3DCCAC486DBA644BB336911B91E326A', X'4359C9889F93134CA05DACF6586CED66', X'4359C9889F93134CA05DACF6586CED66', 5, NULL, NULL, 2, NULL, 0, 'Cook in water at 180º F, (82º C) for about 90-120 min (depends on a size) until internal temperature of 154º-158º F, (68º-70º C) is obtained. If stuffed bungs swim up to the surface prick them with a needle to release air.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'76AE7BBC917C1D4D85F57944DBC1D264', X'4359C9889F93134CA05DACF6586CED66', X'4359C9889F93134CA05DACF6586CED66', 6, NULL, NULL, 2, NULL, 0, 'Place head cheeses on a flat surface to cool them down and let more moisture evaporate. Then cool them down to below 42º F, (6º C), place in refrigerator and put some weight on top of it (determines the shape and the thickness of the finished product).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B39A81524DF6942AB18DC9F2ED6AD9F', X'4359C9889F93134CA05DACF6586CED66', X'4359C9889F93134CA05DACF6586CED66', 7, NULL, NULL, 2, NULL, 0, 'After cooling clean head cheese of any fat and aspic that accumulated on the surface, even them out and cut off excess twine.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'231404BBF53DCE40A7782D92DCF38980', X'4359C9889F93134CA05DACF6586CED66', X'4359C9889F93134CA05DACF6586CED66', 8, NULL, NULL, 2, NULL, 0, 'Store in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B9D32EC9C524E43881CC26CE68BE3FC', X'194AA99AFCE6854598D4A14856888BC4', X'194AA99AFCE6854598D4A14856888BC4', 0, NULL, NULL, 2, NULL, 0, 'The goose and calves feet are cleaned and well jointed, then put to boil in cold water with all other ingredients. When the goose is tender, take it out of the broth, remove all spices that may adhere to it, place it into a porcelain or stone jar. Let the calves feet cook until boiled down to 3 quarts, remove all fat and season with vinegar, meat extract and salt if necessary. Put in the crushed egg shells, beat the white of egg with water and add it to the bouillon, let it come to a boil and then set aside to simmer until the white of egg curdles. After one hour, strain the bouillon and pour it through a white cloth over the goose meat.*');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6183BAC4A2507C4FA54BF6993EC85022', X'194AA99AFCE6854598D4A14856888BC4', X'194AA99AFCE6854598D4A14856888BC4', 1, NULL, NULL, 2, NULL, 0, 'If you wish to prepare the head cheese quickly, omit the calves feet and use 35 pieces of gelatin (follow gelatin making instructions) dissolved in 3 quarts of bouillon, which is also clarified with white of egg and egg shells.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C33C0A42B6F6584BA1C9E36851697A09', X'194AA99AFCE6854598D4A14856888BC4', X'194AA99AFCE6854598D4A14856888BC4', 2, NULL, NULL, 2, NULL, 0, 'The meat of the calvess feet may be breaded in roll crumbs, fried in butter and served with a raisin gravy. If you wish to make this head cheese from duck, take 2 ducks.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4BFD21DD8673394D989D3FC6CBDB3D95', X'194AA99AFCE6854598D4A14856888BC4', X'194AA99AFCE6854598D4A14856888BC4', 3, NULL, NULL, 2, NULL, 0, '* If you want to produce a stuffed sausage follow the common procedure: stuff loosely into stomachs, bladders or 100 mm synthetic casings. Sew or tie the ends with twine.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E980A5FF0986D4B8553E382F6A9F1B2', X'194AA99AFCE6854598D4A14856888BC4', X'194AA99AFCE6854598D4A14856888BC4', 4, NULL, NULL, 2, NULL, 0, 'Cook in water at 85° C (185° F) for 90-180 min (depending on size) until meat reaches 68-70° C (154-158° F) internal temperature. Remove air with a needle from pieces that swim up to the surface. Spread head cheeses on a flat surface and let the steam out. Flatten stomachs with weight and cool to 6° C (43° F) or lower. Clean head cheeses of any fat and aspic that accumulated on the surface, even them out and cut off excess twine.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED630A297D571A49A39A0998787F37A8', X'A95F4A905705DA46B2D5AED2A2387519', X'A95F4A905705DA46B2D5AED2A2387519', 0, NULL, NULL, 2, NULL, 0, 'Cover meats with sufficient amount of water and cook at 95° C (203° F) until soft (about 2 hours). For best flavor add soup greens to water: celery, carrot, leek, parsley root, whole peppers, onion, allspice. The skins are done when they can be penetrated with a finger. Remove meat from bones when still warm. Save meat stock.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A8A90E6A0F8068478D397DF576C89820', X'A95F4A905705DA46B2D5AED2A2387519', X'A95F4A905705DA46B2D5AED2A2387519', 1, NULL, NULL, 2, NULL, 0, 'Chop onion and fry in fat until golden.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E076034563A1E64CB13693EA35033B81', X'A95F4A905705DA46B2D5AED2A2387519', X'A95F4A905705DA46B2D5AED2A2387519', 2, NULL, NULL, 2, NULL, 0, 'Cut lean meats 12 mm (1/2`) pieces.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A8725E3E7FCD0D40B89DAEFCA414436C', X'A95F4A905705DA46B2D5AED2A2387519', X'A95F4A905705DA46B2D5AED2A2387519', 3, NULL, NULL, 2, NULL, 0, 'Cut bellies into long strips.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'626191ECBFF58245B03DCEFD6AA3BDD7', X'A95F4A905705DA46B2D5AED2A2387519', X'A95F4A905705DA46B2D5AED2A2387519', 4, NULL, NULL, 2, NULL, 0, 'Grind skins and onions through 3 mm (1/8`) plate. Add a little meat stock if needed.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2EE7B1B946F3364D81E93645F531220B', X'A95F4A905705DA46B2D5AED2A2387519', X'A95F4A905705DA46B2D5AED2A2387519', 5, NULL, NULL, 2, NULL, 0, 'Mix all meats with emulsified skins, meat stock and spices.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'496B05EE617363459ECF11C52A6CD303', X'A95F4A905705DA46B2D5AED2A2387519', X'A95F4A905705DA46B2D5AED2A2387519', 6, NULL, NULL, 2, NULL, 0, 'Stuff into pork stomachs or 100 mm synthetic waterproof casings.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB603548DEBE6D449D9AB7D08A783FA9', X'A95F4A905705DA46B2D5AED2A2387519', X'A95F4A905705DA46B2D5AED2A2387519', 7, NULL, NULL, 2, NULL, 0, 'In a large pot bring the water to a boil. Insert sausages in hot water, lower the heat and cook pork stomachs at 80° C (176° F) for 180 minutes, 100 mm casings for 120 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EBE6A9FFE238784CADD4FB75B3085611', X'A95F4A905705DA46B2D5AED2A2387519', X'A95F4A905705DA46B2D5AED2A2387519', 8, NULL, NULL, 2, NULL, 0, 'Immerse sausages for 20 minutes in cold water, then place them on a flat surface to finish cooling. Roll over periodically the sausages stuffed into round synthetic casings to prevent jelly and fat from accumulating in one area.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E3ABB0A052B5A44398AD7DA5B1067576', X'A95F4A905705DA46B2D5AED2A2387519', X'A95F4A905705DA46B2D5AED2A2387519', 9, NULL, NULL, 2, NULL, 0, 'Refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7BD7B08BA7B47B429ABC3C6B31821FC2', X'EEC8DF884FDB614DA5BEA00A13A1F356', X'EEC8DF884FDB614DA5BEA00A13A1F356', 0, NULL, NULL, 2, NULL, 0, 'Poach meats in a small amount of water: pork heads at 85°C (185°F), beef head meat and pork skins at 95°C (203°F)');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'816876BDEE492C47BE03C46AB9F1ADB1', X'EEC8DF884FDB614DA5BEA00A13A1F356', X'EEC8DF884FDB614DA5BEA00A13A1F356', 1, NULL, NULL, 2, NULL, 0, 'After poaching remove meat from heads and spread all meats apart on a flat surface to cool. Save meat stock.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E0BA2BEB92B4943908D8F0397E67F01', X'EEC8DF884FDB614DA5BEA00A13A1F356', X'EEC8DF884FDB614DA5BEA00A13A1F356', 2, NULL, NULL, 2, NULL, 0, 'Boiled pork head meat: cut into strips 1.5 x 2 cm (? x ¾) by 7-10 cm (2 x 4); boiled beef head meat: cut into strips 1 x 5 cm (? x 2); boiled skins and tougher meat parts: grind with 3 mm (?) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'110BC7E5A52D8D498E5A34961548F21B', X'EEC8DF884FDB614DA5BEA00A13A1F356', X'EEC8DF884FDB614DA5BEA00A13A1F356', 3, NULL, NULL, 2, NULL, 0, 'Mix all meats with spices adding 10% of meat stock in relation to the weight of pork heads with bones. The meat stock is the result of boiling pork heads.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'64E0C8603963624FA2B9A8573EC56505', X'EEC8DF884FDB614DA5BEA00A13A1F356', X'EEC8DF884FDB614DA5BEA00A13A1F356', 4, NULL, NULL, 2, NULL, 0, 'Stuff mixture not too tight into hog stomachs or beef bungs about 12 (30 cm) long, 8 (20 cm ) wide and 3 (8 cm) thick. Saw the ends with butchers twine.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F0B0B09950879740B027F8285F47F5A4', X'EEC8DF884FDB614DA5BEA00A13A1F356', X'EEC8DF884FDB614DA5BEA00A13A1F356', 5, NULL, NULL, 2, NULL, 0, 'Poach head cheeses at 180°F (82°C) for 90-150 min (depending on size) until the internal temperature of the meat reaches 154-158°F (68-70°C). Remove air with a needle from pieces that swim up to the surface.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD05E7915E7CD64EBC1053A2C65B15E8', X'EEC8DF884FDB614DA5BEA00A13A1F356', X'EEC8DF884FDB614DA5BEA00A13A1F356', 6, NULL, NULL, 2, NULL, 0, 'Spread head cheeses on a flat surface at 2-6°C (35-43°F) and let the steam out. Then flatten them with weight and cool them to below 6°C (43°F).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03B685D26FCF844FB0E43BB2B900A9A8', X'EEC8DF884FDB614DA5BEA00A13A1F356', X'EEC8DF884FDB614DA5BEA00A13A1F356', 7, NULL, NULL, 2, NULL, 0, 'After cooling clean head cheeses of any fat and aspic that accumulated on the surface, even them out and cut off excess twine. Store in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48324CF7E972D249831D5A30F50DCFB0', X'5679E39EF05FF14FB9F71FBDD1512091', X'5679E39EF05FF14FB9F71FBDD1512091', 0, NULL, NULL, 2, NULL, 0, 'Rinse meats in cold running water.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9DEFFDB8160EDD4CB9B482653354A5EE', X'5679E39EF05FF14FB9F71FBDD1512091', X'5679E39EF05FF14FB9F71FBDD1512091', 1, NULL, NULL, 2, NULL, 0, 'Except spleen, brains and blood and skwarki (* see note), poach other meats in small amount of water: lungs, kidneys, veal heads, veal casings and pork trimmings at 85°C (185°F) for about 90 min beef trimmings, skins, heads, tripe, and udders at 95 C (203°F) for about 2 hrs until semi-soft. jowls at 85°C (185°F) until semi-soft.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B42084F9A480D46814FC3B1F7B75493', X'5679E39EF05FF14FB9F71FBDD1512091', X'5679E39EF05FF14FB9F71FBDD1512091', 2, NULL, NULL, 2, NULL, 0, 'After poaching remove gristle from lungs and meat from heads. Spread meats apart on a flat surface to cool. Soak skwarki (* see note) in stock (remaining after boiling meats) taking 40 liters of stock for 100 kg of skwarki (* see note).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E825A5673F42694EA554B6B7F0632EEE', X'5679E39EF05FF14FB9F71FBDD1512091', X'5679E39EF05FF14FB9F71FBDD1512091', 3, NULL, NULL, 2, NULL, 0, 'Cut jowls into 8-10 mm (3/8) cubes. Meats, spleen, kidneys, udders, tripe, sheep casings and veal head meat ground through 5 mm plate. Skwarki (* see note) and skins ground through 3 mm plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3FBBFAB0A63FA44A8487C40C3D91A0A0', X'5679E39EF05FF14FB9F71FBDD1512091', X'5679E39EF05FF14FB9F71FBDD1512091', 4, NULL, NULL, 2, NULL, 0, 'Mix all meats with salt, spices and blood together.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74D7F7BC885A8F48962BADEB6D21FDFC', X'5679E39EF05FF14FB9F71FBDD1512091', X'5679E39EF05FF14FB9F71FBDD1512091', 5, NULL, NULL, 2, NULL, 0, 'Stuffing materials: beef or veal bungs, middles, 60-100 mm synthetic casings. Butchers twine. Stuff casings loosely and tie the ends with twine.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD5F852DAE41B546BE2B1234FA27830C', X'5679E39EF05FF14FB9F71FBDD1512091', X'5679E39EF05FF14FB9F71FBDD1512091', 6, NULL, NULL, 2, NULL, 0, 'Poach head cheeses at 85°C (185°F) for 90-120 min (depending on size) until the internal temperature of the meat reaches 68-70°C (154-158°F). Remove air with a needle from pieces that swim up to the surface.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'46DB9A0CA3EABA4C8F8C5F15E4977256', X'5679E39EF05FF14FB9F71FBDD1512091', X'5679E39EF05FF14FB9F71FBDD1512091', 7, NULL, NULL, 2, NULL, 0, 'Spread head cheeses on a flat surface at 2-6°C (35-43°F) and let the steam out. Then flatten them with weight and cool them to below 6°C° (43°F). If there is no proper cooler available, cool them down to less than 12°C (53°F)');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75B226CC2144A54F8E93084E95348CF1', X'5679E39EF05FF14FB9F71FBDD1512091', X'5679E39EF05FF14FB9F71FBDD1512091', 8, NULL, NULL, 2, NULL, 0, 'After cooling clean head cheeses of any fat and aspic that accumulated on the surface, even them out and cut off excess twine.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD16264D9D70734F9D6BB365B1E130DC', X'F0B22C3586F65140B5273C9D1A423CF7', X'F0B22C3586F65140B5273C9D1A423CF7', 0, NULL, NULL, 2, NULL, 0, 'Cut back fat into 3 mm (1/8) strips, about 8 cm (3) long.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F14A86D16F95545B286972ED9E801F0', X'F0B22C3586F65140B5273C9D1A423CF7', X'F0B22C3586F65140B5273C9D1A423CF7', 1, NULL, NULL, 2, NULL, 0, 'Grind all meats through 2-3 mm (1/8) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB60AF6CFE658C4A97185A886A5F8B79', X'F0B22C3586F65140B5273C9D1A423CF7', X'F0B22C3586F65140B5273C9D1A423CF7', 2, NULL, NULL, 2, NULL, 0, 'Mix blood with salt and spices.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'127355E75733424EBA3574FE79D2CE33', X'F0B22C3586F65140B5273C9D1A423CF7', X'F0B22C3586F65140B5273C9D1A423CF7', 3, NULL, NULL, 2, NULL, 0, 'Mix ground meats, fat and blood well together.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4BFCF20D6616643A80476FEE3752BAC', X'F0B22C3586F65140B5273C9D1A423CF7', X'F0B22C3586F65140B5273C9D1A423CF7', 4, NULL, NULL, 2, NULL, 0, 'Stuff into pork blind caps (caecum), about 20 cm (8) long.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A542E4A57C9ADA4DB754D3A5ECBF2D40', X'F0B22C3586F65140B5273C9D1A423CF7', X'F0B22C3586F65140B5273C9D1A423CF7', 5, NULL, NULL, 2, NULL, 0, 'Cook in water at 80° C (176° F) for 60 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37E7AC19D3FB044D8BCB02CC4518A706', X'F0B22C3586F65140B5273C9D1A423CF7', X'F0B22C3586F65140B5273C9D1A423CF7', 6, NULL, NULL, 2, NULL, 0, 'Keep in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FDCBE7A6C9AD3F42BFE4E6CA92B6183B', X'753F53AB337749418D6AB1706AD6F397', X'753F53AB337749418D6AB1706AD6F397', 0, NULL, NULL, 2, NULL, 0, 'Pigs feet and calves tongues are cleaned well, put on to boil with the water, salt, pepper, onion, bay leaf. Cover the pot and boil until well done, then take the skin off the tongue and cut it with the rest of the meat into small pieces. Strain the bouillon and add vinegar, lemon juice, wine and meat extract. Put the meat into a dish, pour the bouillon on and set to cool. **');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B6B223091A4578418294005B0A423375', X'753F53AB337749418D6AB1706AD6F397', X'753F53AB337749418D6AB1706AD6F397', 1, NULL, NULL, 2, NULL, 0, 'Then turn it out on a platter and serve with head lettuce or potato salad, vinegar and oil, mayonnaise dressing or with fried potatoes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E89E5A93DFF454EAC5D6C15F469579D', X'D0FFAFA44D0CFB458E559BD9D7E0A66B', X'D0FFAFA44D0CFB458E559BD9D7E0A66B', 0, NULL, NULL, 2, NULL, 0, 'For pink color cure meats. Cut the meats into one inch pieces, mix with salt (12 g) and cure 1. Pack tightly and leave for 48 hours in refrigerator. Meats not cured are rinsed only.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4E90A51D71438469E0EE00F3314D7AB', X'D0FFAFA44D0CFB458E559BD9D7E0A66B', X'D0FFAFA44D0CFB458E559BD9D7E0A66B', 1, NULL, NULL, 2, NULL, 0, 'Poach meats in a small amount of water: pork heads,snouts and jowls, lungs and pork class IV at 85°C (185°F) until soft. skins, heads and legs at 95°C (203°F) until soft. dewlaps at 85°C (185°F) until semi-soft.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E4EC41A1CF004489E4A3CDCB9A880F8', X'D0FFAFA44D0CFB458E559BD9D7E0A66B', X'D0FFAFA44D0CFB458E559BD9D7E0A66B', 2, NULL, NULL, 2, NULL, 0, 'After poaching remove gristle from lungs and meat from heads and legs. Spread apart on a flat surface to cool.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC78A3909717004D821782190D33C5F0', X'D0FFAFA44D0CFB458E559BD9D7E0A66B', X'D0FFAFA44D0CFB458E559BD9D7E0A66B', 3, NULL, NULL, 2, NULL, 0, 'Cut boiled snouts and jowls and poached dewlaps into 15 mm (5/8) cubes. Except skwarki * grind all meats through 3 mm plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B493511179422140909F751423EFB455', X'D0FFAFA44D0CFB458E559BD9D7E0A66B', X'D0FFAFA44D0CFB458E559BD9D7E0A66B', 4, NULL, NULL, 2, NULL, 0, 'Mix ground meat with spices and blood.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8D4E5BF2A749549B9C43BBB6A385B11', X'D0FFAFA44D0CFB458E559BD9D7E0A66B', X'D0FFAFA44D0CFB458E559BD9D7E0A66B', 5, NULL, NULL, 2, NULL, 0, 'Stuffing. Pork middles, bladders or stomachs, beef, veal and lamb bungs, 60-100 mm synthetic casings. Stuff casings loosely and sew the ends with twine.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'714BAD0D79B0974A9E0FBF1D81998F09', X'D0FFAFA44D0CFB458E559BD9D7E0A66B', X'D0FFAFA44D0CFB458E559BD9D7E0A66B', 6, NULL, NULL, 2, NULL, 0, 'Poach head cheeses at 82°C (180°F) for 90-120 min (depending on size) until the internal temperature of the meat reaches 68-70°C (154-158°F). Remove air with a needle from pieces that swim up to the surface.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B243B98B0EB714F8B332F24AF3BB351', X'D0FFAFA44D0CFB458E559BD9D7E0A66B', X'D0FFAFA44D0CFB458E559BD9D7E0A66B', 7, NULL, NULL, 2, NULL, 0, 'Spread head cheeses on a flat surface at 2-6°C (35-43°F) and let the steam out. Then flatten them with weight and cool them to below 6°C (43°F). If there is no proper cooler available, cool them down to less than 12°C (53°).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B3714D2F486575489517B0F79D9ECDC3', X'D0FFAFA44D0CFB458E559BD9D7E0A66B', X'D0FFAFA44D0CFB458E559BD9D7E0A66B', 8, NULL, NULL, 2, NULL, 0, 'After cooling clean head cheeses of any fat and aspic that accumulated on the surface, even them out and cut off excess twine.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B9B1EC4DC7AF8743BF87A74F3D18A9EC', X'DAD69F68BE3D034DA995C88F112A3FB6', X'DAD69F68BE3D034DA995C88F112A3FB6', 0, NULL, NULL, 2, NULL, 0, 'The fried chicken is cut into small pieces, the pickles are peeled and sliced. The strong beef broth is heated and the gelatin i dissolved in it with wine, meat extract and salt added, then strained and tasted as to seasoning. Put 1/4 pint of the warm fluid into a porcelain dish, skim off all fat, let it get cold and put on the sliced pickles and a few tablespoons of broth, let it cool, arrange the chicken meat on it in the form of scales, pour the rest of the broth on and let it get cold and stiff. Then turn it out on a platter and garnish with parsley. Asparagus cut into small pieces may also be used in this gelatin.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13D2EFD99580EE4CA52A15B5C4489295', X'7587F485D1169F428D1B3A7879BB6642', X'7587F485D1169F428D1B3A7879BB6642', 0, NULL, NULL, 2, NULL, 0, 'Boil vegetables in 3-4 parts of water for 20 minutes. Drain vegetables and add the liquid to meats. Cook all meats in a little water at 80° C (176° F) until soft. Separate from bones when the meat is still warm. In home production meat is usually not ground. Save meat stock for aspic. Mix all ingredients together adding some meat stock. Stuff into 100 mm (4) artificial waterproof casings (presswurst was traditionally stuffed in pork stomachs).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C270BD8565193C4A9BB4208734B23013', X'7587F485D1169F428D1B3A7879BB6642', X'7587F485D1169F428D1B3A7879BB6642', 1, NULL, NULL, 2, NULL, 0, 'Cook in water at 80° C (176° F) for about 2 hours. Immerse in cold water for 15 minutes. Place on flat surface and stack sausages one on top of the other. Turn around every hour or so to prevent heavier ingredient settling in one area. When sufficiently cold place sausage in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'886F77891B15F649AE2A3C71E244D0EC', X'566FA28A2321274EB83CA7D5C3DAA2F9', X'566FA28A2321274EB83CA7D5C3DAA2F9', 0, NULL, NULL, 2, NULL, 0, 'Place all meats in a suitable pot, add leek, celery, parsnip, carrot, onion, peppercorns and cover with 1 inch of water and wine. Bring to a boil and simmer below boiling point until meats are soft and the bones are easily removable.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E9B19A55BDEFF408999C510BF563CC5', X'566FA28A2321274EB83CA7D5C3DAA2F9', X'566FA28A2321274EB83CA7D5C3DAA2F9', 1, NULL, NULL, 2, NULL, 0, 'Drain the meats, spread on the table and allow to cool. Save meat stock, discard vegetables.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8EE304B087070A499A219168ED131E0D', X'566FA28A2321274EB83CA7D5C3DAA2F9', X'566FA28A2321274EB83CA7D5C3DAA2F9', 2, NULL, NULL, 2, NULL, 0, 'When the meats are still warm, separate the meat from the bones. Cut larger chunks into smaller pieces, cut the skins into strips.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E00B32379F2F146BA7DB12ED3026827', X'566FA28A2321274EB83CA7D5C3DAA2F9', X'566FA28A2321274EB83CA7D5C3DAA2F9', 3, NULL, NULL, 2, NULL, 0, 'Mix meats with salt, garlic, oregano, cayenne adding about 1/2 cup (125 ml) of the meat stock. This meat stock is a naturally produced gelatin.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74F4D5F25F4D3144B76C7CF61D3E078E', X'566FA28A2321274EB83CA7D5C3DAA2F9', X'566FA28A2321274EB83CA7D5C3DAA2F9', 4, NULL, NULL, 2, NULL, 0, 'Using a ladle stuff the mixture loosely into pork stomachs or large diameter synthetic casings.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'303877D60CEDE44B82433BEF911DD3AF', X'566FA28A2321274EB83CA7D5C3DAA2F9', X'566FA28A2321274EB83CA7D5C3DAA2F9', 5, NULL, NULL, 2, NULL, 0, 'Cook in water at 80° C (176° F) for 90-120 min (depending on the size) until meat reaches 68-70° C (154-158° F) internal temperature.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1BC0C553F248034D87EEF52C7B364166', X'566FA28A2321274EB83CA7D5C3DAA2F9', X'566FA28A2321274EB83CA7D5C3DAA2F9', 6, NULL, NULL, 2, NULL, 0, 'Spread head cheeses on a flat surface and let the steam out. Flatten with weight and cool to 6° C (43° F) or lower.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4FB846519280AF4EA2D325FB2A3EB50D', X'566FA28A2321274EB83CA7D5C3DAA2F9', X'566FA28A2321274EB83CA7D5C3DAA2F9', 7, NULL, NULL, 2, NULL, 0, 'Clean head cheeses of any fat and aspic that accumulated on the surface and cut off excess twine.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57C65A0BD31A50459509F621F8AEB983', X'566FA28A2321274EB83CA7D5C3DAA2F9', X'566FA28A2321274EB83CA7D5C3DAA2F9', 8, NULL, NULL, 2, NULL, 0, 'Keep refrigerated.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A9F8B8ED32339B44A2157E4E469FC614', X'95328ACBAA6B8A4DA474CB0B6AF53CA0', X'95328ACBAA6B8A4DA474CB0B6AF53CA0', 0, NULL, NULL, 2, NULL, 0, 'Creating meat stock (gelatin). Immerse meats in pot, add soup greens, bay leaves and whole black peppers and cover with 5 cm (2`) of water. Bring to a boil and simmer for 2-3 until the meat can be separated from bones. If water level drops too much add a little water. Drain the meats and spread on the table to cool. Save the meat stock as this will become the natural gelatin that will hold meats together. Place meat stock in refrigerator. When the meat is still warm but comfortable to work with, separate all meats including snouts, ears, and skins from the bones. If the split heads come with tongue, use it as well. Cut all meats into smaller parts. Recover meat stock from refrigerator and discard any fat from the surface. Add salt, diced garlic, cumin, oregano and aji source to meat stock until you are satisfied with the result.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1302FCCDDA385443B6C98145A6CD3263', X'95328ACBAA6B8A4DA474CB0B6AF53CA0', X'95328ACBAA6B8A4DA474CB0B6AF53CA0', 1, NULL, NULL, 2, NULL, 0, 'Mixing. Start mixing meats gradually adding meat stock. Add some vinegar to taste, this is optional as most people eat head cheese with lemon or vinegar. You cannot spoil the product by adding not enough or too much of the meat stock. Adding more of the stock will produce head cheese with more gelatin, a kind of meat jelly. Some people like more jelly, some like less.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74EFAE4D1BF55A4D9401061D9B34B656', X'95328ACBAA6B8A4DA474CB0B6AF53CA0', X'95328ACBAA6B8A4DA474CB0B6AF53CA0', 2, NULL, NULL, 2, NULL, 0, 'Stuffing. Fill pork stomach or a large diameter synthetic casing with the mixture. It is usually done with a ladle.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51CA5E9B25C1664BA69811059F7CDA7E', X'95328ACBAA6B8A4DA474CB0B6AF53CA0', X'95328ACBAA6B8A4DA474CB0B6AF53CA0', 3, NULL, NULL, 2, NULL, 0, 'Cooking. Cook in water at 80° C (176° F) for 90-120 min (depending on the size) until meat reaches 68-70° C (154-158° F) internal temperature.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53E9C93A2F8D144CBF217D37647B324A', X'95328ACBAA6B8A4DA474CB0B6AF53CA0', X'95328ACBAA6B8A4DA474CB0B6AF53CA0', 4, NULL, NULL, 2, NULL, 0, 'Conditioning. Spread head cheeses on a flat surface and let the steam out. Flatten pork stomachs with weight and cool to 6° C (43° F) or lower. Round synthetic casings are not flattened.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04C36AE54BEFD44F991BF9EA29DD1423', X'95328ACBAA6B8A4DA474CB0B6AF53CA0', X'95328ACBAA6B8A4DA474CB0B6AF53CA0', 5, NULL, NULL, 2, NULL, 0, 'Storing. Clean head cheeses of any fat and aspic that accumulated on the surface. Keep refrigerated.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0007A976250F9E4C9CFCAB6DE0C40F0B', X'53CE1193D253834081F98A4EC4DD3733', X'53CE1193D253834081F98A4EC4DD3733', 0, NULL, NULL, 2, NULL, 0, 'In separate containers cook heads and legs in water at 85° C (185° F) until the meat can be separated from the bone. Save meat stock. Spread all pieces on a flat surface to cool.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DBD2C87D407A16449373975EDA316CB9', X'53CE1193D253834081F98A4EC4DD3733', X'53CE1193D253834081F98A4EC4DD3733', 1, NULL, NULL, 2, NULL, 0, 'Cut head meat into ½ (12 mm) cubes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CFD2887E7A719D45B6FA48E260CC696A', X'53CE1193D253834081F98A4EC4DD3733', X'53CE1193D253834081F98A4EC4DD3733', 2, NULL, NULL, 2, NULL, 0, 'Grind leg meat through 1/8 (3 mm) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C951F5CF632F5548BFDAF2BBC46938E9', X'53CE1193D253834081F98A4EC4DD3733', X'53CE1193D253834081F98A4EC4DD3733', 3, NULL, NULL, 2, NULL, 0, 'Mix all meats with salt, spices and blood together. Add some of the meat stock.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5ABE78E07DB9AC449A25C2CDE60CCF8C', X'53CE1193D253834081F98A4EC4DD3733', X'53CE1193D253834081F98A4EC4DD3733', 4, NULL, NULL, 2, NULL, 0, 'Stuff loosely into pork stomachs or large diameter synthetic casings.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'447AC467240B70439C80B33D0CA5FF4D', X'53CE1193D253834081F98A4EC4DD3733', X'53CE1193D253834081F98A4EC4DD3733', 5, NULL, NULL, 2, NULL, 0, 'Cook in water at 82° C (180° F) for 90-120 min (depending on the size) until meat reaches 68-70° C (154-158° F) internal temperature.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F79BBD8EB704EE4EB909239F8C84960B', X'53CE1193D253834081F98A4EC4DD3733', X'53CE1193D253834081F98A4EC4DD3733', 6, NULL, NULL, 2, NULL, 0, 'Spread head cheeses on a flat surface and let the steam out. Flatten with weight and cool to 6° C (43° F) or lower.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B7C2466535B12B4CB97DFE2B03D7F309', X'53CE1193D253834081F98A4EC4DD3733', X'53CE1193D253834081F98A4EC4DD3733', 7, NULL, NULL, 2, NULL, 0, 'Clean head cheeses of any fat and aspic that accumulated on the surface and cut off excess twine.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21D1D7FA1AD8E64398C305EA4D94006A', X'DC7D25195E26D844A18D48DE6AD2F869', X'DC7D25195E26D844A18D48DE6AD2F869', 0, NULL, NULL, 2, NULL, 0, 'Cure meat. Cut beef into 1-3/4` (50 mm) chunks, mix with salt and Cure #1, pack firmly in a container, cover with a cloth and refrigerate for 72 hours. The head is usually cured for 4 days in 16° Bé (60° SAL) cure solution with sodium nitrite added. Jowls can be cured in for 4 days in 16° Bé (60° SAL) cure solution with sodium nitrite added. * see the note below.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25D4CEF253FFAD48847D545FCFF19016', X'DC7D25195E26D844A18D48DE6AD2F869', X'DC7D25195E26D844A18D48DE6AD2F869', 1, NULL, NULL, 2, NULL, 0, 'Cook: pork heads in water at 203° F (95° C) until meat can be separated from bones. This may take from 2-4 hours. blanch jowls in a boiling water (212° F, 100° C) for 5-10 minutes. slow cook (185° F, 85° C) any connective tissue rich meat cuts (if used) until meat separates from bones. cook skins (if used) at 203° F (95° C) until soft.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9351BC1DDDC8484AA994CC43525F4C56', X'DC7D25195E26D844A18D48DE6AD2F869', X'DC7D25195E26D844A18D48DE6AD2F869', 2, NULL, NULL, 2, NULL, 0, 'Spread cooked meats on flat surface for cooling. Remove meat from bones when the cuts have cooled down enough to be handled.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'423FEC5FA28CC44B8484E57FFB3E8F1B', X'DC7D25195E26D844A18D48DE6AD2F869', X'DC7D25195E26D844A18D48DE6AD2F869', 3, NULL, NULL, 2, NULL, 0, 'When removing meat from bones try to maintain meat size of about 1 inch (25 mm).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E40F73DB73B1224EBBE7C586E67FACE3', X'DC7D25195E26D844A18D48DE6AD2F869', X'DC7D25195E26D844A18D48DE6AD2F869', 4, NULL, NULL, 2, NULL, 0, 'Cut jowls into strips about 4 inch (10 cm) long and 1/4 inch (5 mm) wide.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'84607A168E3E8143B09901B966C7FE2F', X'DC7D25195E26D844A18D48DE6AD2F869', X'DC7D25195E26D844A18D48DE6AD2F869', 5, NULL, NULL, 2, NULL, 0, 'Grind lean beef and any connective tissue rich meats (skins, hocks - if used) through 1/8` (2-3 mm) grinder plate. Grind the second time. You can use food processor to emulsify lean beef and any connective tissue rich meats, but you need to add a little cold water or finely crushed ice.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D82772B2EB79674B927F957D6493622A', X'DC7D25195E26D844A18D48DE6AD2F869', X'DC7D25195E26D844A18D48DE6AD2F869', 6, NULL, NULL, 2, NULL, 0, 'Mix all meats with spices.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0752DC7858B1DA42873221D7F626C6DB', X'DC7D25195E26D844A18D48DE6AD2F869', X'DC7D25195E26D844A18D48DE6AD2F869', 7, NULL, NULL, 2, NULL, 0, 'Stuffing materials: pork stomachs or bladders, beef bungs, large diameter (4 inch) fibrous casings, butcher''s twine. Stuff stomachs or bladders loosely and tie or sew the ends with twine.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A775680B66A404BA36BBD2055DAE381', X'DC7D25195E26D844A18D48DE6AD2F869', X'DC7D25195E26D844A18D48DE6AD2F869', 8, NULL, NULL, 2, NULL, 0, 'Poach head cheeses at 185-194° F(85-90° C) for 90-180 min (depending on size) until the internal temperature of the meat reaches 160°F (72°C). Prick with a needle any pieces that have swum up to the surface to allow air to escape. Clear liquid should be observed when fully cooked head cheese is pricked with a needle.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B4CD2C1A5961046A77DFB276A444C09', X'DC7D25195E26D844A18D48DE6AD2F869', X'DC7D25195E26D844A18D48DE6AD2F869', 9, NULL, NULL, 2, NULL, 0, 'Spread head cheeses on a flat surface at 2-6° C (35-43° F) and let the steam out. Then flatten them with a weighted board at 37-40° F (3-4° C). Leave them undisturbed for 10-12 hours. They are done when inside temperature reaches 50-53° F (10-12° C). Do not flatten head cheese stuffed in sausage casing.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'46A198DDAFBE8C4B8E66591EE8976731', X'DC7D25195E26D844A18D48DE6AD2F869', X'DC7D25195E26D844A18D48DE6AD2F869', 10, NULL, NULL, 2, NULL, 0, 'Insert stomachs or bladders for 1-2 minutes into boiling water to remove grease from the surface. (Scald the casings with hot water).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E503894F717DC048AFC2B2C885332F33', X'DC7D25195E26D844A18D48DE6AD2F869', X'DC7D25195E26D844A18D48DE6AD2F869', 11, NULL, NULL, 2, NULL, 0, 'Apply cold smoke (68-77° F, 20-25° C) for 6-12 hours. You may skip this time consuming step, however, traditionally made head cheese was smoked.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE55BAEB5B260D4EA297D4BDEC8B1A69', X'DC7D25195E26D844A18D48DE6AD2F869', X'DC7D25195E26D844A18D48DE6AD2F869', 12, NULL, NULL, 2, NULL, 0, 'Keep refrigerated.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE445C01210AAF43AF4EE0ED259A15D0', X'F976B742720A8B44AF64E16EB214A9D9', X'F976B742720A8B44AF64E16EB214A9D9', 0, NULL, NULL, 2, NULL, 0, 'Place pork butt and 6 liters (6 quarts) in a pot. Bring to a boil and cook below the boiling point for about 2 hours. Place the meat on a table, reserve the stock and when the meat is cool enough to handle, remove the blade bone. Chop the meat finely. Take 3 liters (3 quarts) of the meat stock, add salt, pepper and all spices and bring to a boil.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98EA6ACC40FBA849AB22DC831EAD1E19', X'F976B742720A8B44AF64E16EB214A9D9', X'F976B742720A8B44AF64E16EB214A9D9', 1, NULL, NULL, 2, NULL, 0, 'Gradually add cornmeal, stirring constantly until all is thorougly combined. The mixture should be thick enough to support a spoon standing on its own. If the mixture gets to thick, stir in more meat stock. Reduce heat and add all chopped meat and cook for an additional 15 minutes stirring occasionally.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A8A654D123113F4A8EF773B8B136383F', X'F976B742720A8B44AF64E16EB214A9D9', X'F976B742720A8B44AF64E16EB214A9D9', 2, NULL, NULL, 2, NULL, 0, 'Line up shallow baking pans with waxed paper so that the ends extend over the long sides. Pour the mixture into the pans, allow to cool, cover with foil and place in refrigerator to set and become solid. Slice into 1/4 - 1/2 (6 -12 mm) slices and fry.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F28871714B0744E821E7C45079747AC', X'4E2D94D6AE71F447AE806DF7311DE9BA', X'4E2D94D6AE71F447AE806DF7311DE9BA', 0, NULL, NULL, 2, NULL, 0, 'Place split pork heads in enough water to cover them and cook below the boiling point until meat separates easily from bones. Remove heads and place on a table to cool. Save meat stock. Remove the meat from the bones, the task is easily performed when the heads are still warm. Cover other meats with water and cook below the boiling point of water until done. Save meat stock.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA92667804AEAF49B1D338F448780020', X'4E2D94D6AE71F447AE806DF7311DE9BA', X'4E2D94D6AE71F447AE806DF7311DE9BA', 1, NULL, NULL, 2, NULL, 0, 'Chop all meats finely.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'00762876DD33394191A5137D0669EC2E', X'4E2D94D6AE71F447AE806DF7311DE9BA', X'4E2D94D6AE71F447AE806DF7311DE9BA', 2, NULL, NULL, 2, NULL, 0, 'Bring meat broth to a boil, add spices and start gradually adding the cornmeal, stirring constantly for the first 15 minutes, then reduce the heat and keep on cooking 15 minutes more until all is thorougly combined. The mixture should be thick enough to support a spoon standing on its own. If the mixture gets to thick, stir in more meat stock. Add all chopped meat and cook for an additional 15 minutes stirring occasionally.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E525748D885D6B469A4401512DCC5A0D', X'4E2D94D6AE71F447AE806DF7311DE9BA', X'4E2D94D6AE71F447AE806DF7311DE9BA', 3, NULL, NULL, 2, NULL, 0, 'Line up shallow baking pans with waxed paper so that the ends extend over the long sides. Pour the mixture into the pans, allow to cool, cover with foil and place in refrigerator to set and become solid. Slice into 1/4 - 1/2 (6 -12 mm) slices and fry.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F9B25B65B009F4FA7836300B6EB7FAC', X'895E38BBD9AAAF47973F475F30BE22BF', X'895E38BBD9AAAF47973F475F30BE22BF', 0, NULL, NULL, 2, NULL, 0, 'Wash meats, then slow cook in a small amount of water water until soft. heads at 85°C (185°F) skins at 95°C (203°F) After cooking remove meat from heads and spread it apart on a flat surface to cool. Spread skins and let them cool. Save meat stock.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A02F16241548BD498B26EF79AB860A31', X'895E38BBD9AAAF47973F475F30BE22BF', X'895E38BBD9AAAF47973F475F30BE22BF', 1, NULL, NULL, 2, NULL, 0, 'Cut meat into strips (7-10 cm, 3-4) x (1-1.5 cm, ½-¾). Grind boiled skins through 3 mm plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3CBDA762FD22EC44B7A586D2BEE5D923', X'895E38BBD9AAAF47973F475F30BE22BF', X'895E38BBD9AAAF47973F475F30BE22BF', 2, NULL, NULL, 2, NULL, 0, 'Add spices and 10% of the stock (left from boiling in relation to the weight of the head with the bone) to the meat and ground skins. Mix everything well together.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'455D9F24C94CD54ABB359B08D5743945', X'895E38BBD9AAAF47973F475F30BE22BF', X'895E38BBD9AAAF47973F475F30BE22BF', 3, NULL, NULL, 2, NULL, 0, 'Stuffing materials: pork stomachs, 100 mm synthetic casings. Stuff stomach or synthetic casings loosely and tie the ends with twine.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A9BACFF0AFB6F24B958E88D5AEA7E082', X'895E38BBD9AAAF47973F475F30BE22BF', X'895E38BBD9AAAF47973F475F30BE22BF', 4, NULL, NULL, 2, NULL, 0, 'Poach head cheeses at 82°C (180°F) for 90-150 min (depending on size) until the internal temperature of the meat reaches 68-70°C (154-158°F). Remove air with a needle from pieces that swim up to the surface.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1FB6F74B6EE1040AD6A36AA1DFF3500', X'895E38BBD9AAAF47973F475F30BE22BF', X'895E38BBD9AAAF47973F475F30BE22BF', 5, NULL, NULL, 2, NULL, 0, 'Spread head cheeses on a flat surface at 2-6°C (35-43°F) and let the steam out. Then flatten them with weight and cool them to below 6°C (43°F). If there is no proper cooler available, cool them down to less than 12°C (53°F). Do not flatten synthetic casings.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20C260773DC6494CB0D3C90A85495351', X'895E38BBD9AAAF47973F475F30BE22BF', X'895E38BBD9AAAF47973F475F30BE22BF', 6, NULL, NULL, 2, NULL, 0, 'After cooling, clean head cheeses of any fat and aspic that accumulated on the surface, even them out and cut off excess twine.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DDD97A2676A0AF45AF2318A08C9E60C5', X'E7A25C1C6EC6424893DD70933AE58EFE', X'E7A25C1C6EC6424893DD70933AE58EFE', 0, NULL, NULL, 2, NULL, 0, 'Place pigs feet, salt and pepper in a pot and cover with about two inches of water. Cook below the boiling point for about 2 hours or until meat separates easily from bones. Head cheese does not require much salt and a low sodium product can be made.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'417B88EBD02857429B67EB539FAB1678', X'E7A25C1C6EC6424893DD70933AE58EFE', X'E7A25C1C6EC6424893DD70933AE58EFE', 1, NULL, NULL, 2, NULL, 0, 'Strain liquid and save for later.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4BC96CD36CFC3947B0889E911D16EDB9', X'E7A25C1C6EC6424893DD70933AE58EFE', X'E7A25C1C6EC6424893DD70933AE58EFE', 2, NULL, NULL, 2, NULL, 0, 'Separate meats from bones. It is easier to perform this task when meats are still warm.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F90B39A01A5E344AB15C8CF5E0A5D35', X'E7A25C1C6EC6424893DD70933AE58EFE', X'E7A25C1C6EC6424893DD70933AE58EFE', 3, NULL, NULL, 2, NULL, 0, 'Cut meats into smaller pieces. It is easier to cut them when they are chilled.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB291B94B7334B42ACD60CA976E0FA67', X'E7A25C1C6EC6424893DD70933AE58EFE', X'E7A25C1C6EC6424893DD70933AE58EFE', 4, NULL, NULL, 2, NULL, 0, 'Now you can go two different ways: pour your souse into containers, let them sit for 2 hours at room temperature and then place in a refrigerator. Keep it there for 12 hours to give the souse time to set. stuff with a ladle into large diameter waterproof casings, clip the ends and cook in hot water below the boiling point at 185º F (about 85º C). A rough estimate will be about 20 min for each 1/2 (12 mm) of diameter of a casing. Then place at room temperature for about 2 hours for gelatin to set. Place for 12 hours into refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2EA402F3161EBD4AB72FE09BBC48AB10', X'595A00FEAC1A9D41837067B84FC028FD', X'595A00FEAC1A9D41837067B84FC028FD', 0, NULL, NULL, 2, NULL, 0, 'Place pigs feet, salt, pepper, bay leaf and spices in a pot and cover with about two inches of water. Cover and slow cook below the boiling point for about 2 hours or until meat separates easily from bones. Strain and save meat stock for later. Separate meats from bones. It is easier to perform this task when meats are still warm.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'316C516AA1557B49B6BC000AC52AE870', X'595A00FEAC1A9D41837067B84FC028FD', X'595A00FEAC1A9D41837067B84FC028FD', 1, NULL, NULL, 2, NULL, 0, 'Cut meats into smaller pieces.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A8042DB470A51B4AAEEAB3089AE030AF', X'595A00FEAC1A9D41837067B84FC028FD', X'595A00FEAC1A9D41837067B84FC028FD', 2, NULL, NULL, 2, NULL, 0, 'Mix meats with meat stock, vinegar, spices and smashed garlic.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10A4D864D1DDF441BCDCDBBB244DE08B', X'595A00FEAC1A9D41837067B84FC028FD', X'595A00FEAC1A9D41837067B84FC028FD', 3, NULL, NULL, 2, NULL, 0, 'Now you can go two different ways: pour your sulz into containers, let them sit for 2 hours at room temperature and then place in a refrigerator. Keep it there for 12 hours to give the souse time to set. Serve in containers OR stuff with a ladle into pork stomachs or large diameter waterproof casings, tie the ends and cook in water at 85º C (185º F). Cool partially in cold water and place at room temperature for about 2 hours for gelatin to set. Place for 12 hours into refrigerator before cutting.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36F117498738B14CBE39F8BCDA88C2AE', X'A37515C40D333C4A91CAF33E4AEDCFFA', X'A37515C40D333C4A91CAF33E4AEDCFFA', 0, NULL, NULL, 2, NULL, 0, 'Boil soup vegetables (if used) in 3-4 parts of water for 20 minutes. Drain vegetables and add the liquid to meats.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D5C8C7EE00AB849A648974EBC24E0D8', X'A37515C40D333C4A91CAF33E4AEDCFFA', X'A37515C40D333C4A91CAF33E4AEDCFFA', 1, NULL, NULL, 2, NULL, 0, 'Cook all meats in a little water (below boiling point) until soft. Separate meat from bones when still warm. In home production meat is usually not ground. Save meat stock for aspic. Mix all ingredients together.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B2EF8D1822B4C348A82642A152F0B962', X'A37515C40D333C4A91CAF33E4AEDCFFA', X'A37515C40D333C4A91CAF33E4AEDCFFA', 2, NULL, NULL, 2, NULL, 0, 'Place ingredients in a suitable container, arrange vegetables and carefully pour the hot meat stock which will become aspic (jelly) when it solidifies. Let it cool at room temperature and then refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B93B798DDA566642B1EB5DDE11664C57', X'98F0E2DC1C88C74E978786026B5CF8A8', X'98F0E2DC1C88C74E978786026B5CF8A8', 0, NULL, NULL, 2, NULL, 0, 'Cover meats with a little water and boil at 90° C (194° F) until softer. You can add soup greens (celery, carrot, leek, bay leaf) to the meats for better flavor and filter the stock when done.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B6C8BF1D463EE4290AEE9E6071507B2', X'98F0E2DC1C88C74E978786026B5CF8A8', X'98F0E2DC1C88C74E978786026B5CF8A8', 1, NULL, NULL, 2, NULL, 0, 'Remove meats and drain, filter and save the meat stock. Separate meat from bones when still warm.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89E80A4BB14AEB43B27C755B11DA46B5', X'98F0E2DC1C88C74E978786026B5CF8A8', X'98F0E2DC1C88C74E978786026B5CF8A8', 2, NULL, NULL, 2, NULL, 0, 'Grind skins through 3 mm (1/8`) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A22A3B15D66984D82EA5FADF323638B', X'98F0E2DC1C88C74E978786026B5CF8A8', X'98F0E2DC1C88C74E978786026B5CF8A8', 3, NULL, NULL, 2, NULL, 0, 'Cut meats into strips and cubes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F0DAD4638126374CBEB0EA530D392E75', X'98F0E2DC1C88C74E978786026B5CF8A8', X'98F0E2DC1C88C74E978786026B5CF8A8', 4, NULL, NULL, 2, NULL, 0, 'Mix all meats with spices adding meat stock.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ABEF67F450B6594FA81083A92607FCE8', X'98F0E2DC1C88C74E978786026B5CF8A8', X'98F0E2DC1C88C74E978786026B5CF8A8', 5, NULL, NULL, 2, NULL, 0, 'Stuff into 120 mm synthetic waterproof casings.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B46DE758AC1C446A90BD85FF6083C60', X'98F0E2DC1C88C74E978786026B5CF8A8', X'98F0E2DC1C88C74E978786026B5CF8A8', 6, NULL, NULL, 2, NULL, 0, 'Boil at 80° C (176° F) for 150 min (depending on size) until the internal temperature of the meat reaches 154-158° F (68-70° C). This can take 120-180 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F7EDE2EDDF67643A560FA391010E87F', X'98F0E2DC1C88C74E978786026B5CF8A8', X'98F0E2DC1C88C74E978786026B5CF8A8', 7, NULL, NULL, 2, NULL, 0, 'Spread head cheeses on a flat surface and let them steam out and cool.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'43C21A504219EB4DBC0EA51160BC26FB', X'98F0E2DC1C88C74E978786026B5CF8A8', X'98F0E2DC1C88C74E978786026B5CF8A8', 8, NULL, NULL, 2, NULL, 0, 'Roll sausages over once in a while while they are cooling off to prevent meat from collecting in one area.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'110847374B5D0B47B9179BA3AA408C2B', X'98F0E2DC1C88C74E978786026B5CF8A8', X'98F0E2DC1C88C74E978786026B5CF8A8', 9, NULL, NULL, 2, NULL, 0, 'Store in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F15F94947EE2B408CDB57C853C0CD34', X'6B499FFF16E54E45A22ACEF9024DE5C9', X'6B499FFF16E54E45A22ACEF9024DE5C9', 0, NULL, NULL, 2, NULL, 0, 'Boil split pork heads and all meats in a small amount of water: at 90° C (194° F) until meat can be separated from bones. You can add soup greens (celery, carrot, leek, bay leaf) to meats for better flavor. Remove meat from heads when still warm and spread apart on a flat surface to cool. Filter and save the meat stock.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'047C0AD16B092E499E69EF46E5EEDE10', X'6B499FFF16E54E45A22ACEF9024DE5C9', X'6B499FFF16E54E45A22ACEF9024DE5C9', 1, NULL, NULL, 2, NULL, 0, 'Cut pork head meat into strips 1.5 x 2 cm (5/8 x 3/4`) by 7-10 cm (2 x 4`).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8D93CE777A82645BCA564D290FF3BFC', X'6B499FFF16E54E45A22ACEF9024DE5C9', X'6B499FFF16E54E45A22ACEF9024DE5C9', 2, NULL, NULL, 2, NULL, 0, 'Grind tougher meat parts and skins with 3 mm (1/8`) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6F942852E42C6459DC834AE8E93B398', X'6B499FFF16E54E45A22ACEF9024DE5C9', X'6B499FFF16E54E45A22ACEF9024DE5C9', 3, NULL, NULL, 2, NULL, 0, 'Mix all meats with spices adding meat stock.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'556A0A39FB85B649913FB5D839EFA344', X'6B499FFF16E54E45A22ACEF9024DE5C9', X'6B499FFF16E54E45A22ACEF9024DE5C9', 4, NULL, NULL, 2, NULL, 0, 'Stuff mixture not too tight into hog stomachs or beef bungs about 12 (30 cm) long, 8 (20 cm ) wide and 3 (8 cm) thick. Saw the ends with butchers twine. You can stuff Sulzwurst into 120 mm synthetic waterproof casings.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1DAD025463877943BAEDB7A1EDA10726', X'6B499FFF16E54E45A22ACEF9024DE5C9', X'6B499FFF16E54E45A22ACEF9024DE5C9', 5, NULL, NULL, 2, NULL, 0, 'Boil at 80° C (176° F) for 150 min (depending on size) until the meat reaches 154-158°F (68-70°C) internal temperature.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D507E60FF8D5F43BA4CA64F52C62AF5', X'6B499FFF16E54E45A22ACEF9024DE5C9', X'6B499FFF16E54E45A22ACEF9024DE5C9', 6, NULL, NULL, 2, NULL, 0, 'Spread head cheeses on a flat surface and let them steam out and cool. Then flatten pork stomachs or beef bungs with weight on top and let them cool. They are usually left undisturbed overnight. After cooling clean off any fat or jelly that accumulated on the surface and cut off the excess twine.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9956F1622AF16844A8E5FF3D70BDB832', X'6B499FFF16E54E45A22ACEF9024DE5C9', X'6B499FFF16E54E45A22ACEF9024DE5C9', 7, NULL, NULL, 2, NULL, 0, 'Head cheeses stuffed in synthetic casings are not flattened with weight. It is recommended to roll them over once in a while while they are cooling off to prevent meat from collecting in one area. Store in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8DA28A4717D7D47959BBDBBA6F27B6F', X'86D763C8145F04488961EE7201203310', X'86D763C8145F04488961EE7201203310', 0, NULL, NULL, 2, NULL, 0, 'Wash meats, then slow cook in a small amount of water water until soft. heads at 85°C (185°F) skins at 95°C (203°F) After cooking remove meat from heads and spread it apart on a flat surface to cool. Spread skins and let them cool. Save meat stock.');
