INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72C3DFFDFA6F2047A6AD4F03FC08C017', X'86D763C8145F04488961EE7201203310', X'86D763C8145F04488961EE7201203310', 2, NULL, NULL, 2, NULL, 0, 'Add spices and 10% of the stock (left from boiling in relation to the weight of the head with the bone) to the meat and ground skins. Mix everything well together.');
