INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B09CE527ADB2024ABF7DB7BD7D608EC0', X'86D763C8145F04488961EE7201203310', X'86D763C8145F04488961EE7201203310', 4, NULL, NULL, 2, NULL, 0, 'Poach head cheeses at 82°C (180°F) for 90-150 min (depending on size) until the internal temperature of the meat reaches 68-70°C (154-158°F). Remove air with a needle from pieces that swim up to the surface.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F59C5199F1F7D743B723A52C3A9EB9D8', X'86D763C8145F04488961EE7201203310', X'86D763C8145F04488961EE7201203310', 5, NULL, NULL, 2, NULL, 0, 'Spread head cheeses on a flat surface at 2-6°C (35-43°F) and let the steam out. Then flatten them with weight and cool them to below 6°C (43°F). If there is no proper cooler available, cool them down to less than 12°C (53°F). Do not flatten synthetic casings.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4982A98CFBEDE9449F32C63867D9B18A', X'86D763C8145F04488961EE7201203310', X'86D763C8145F04488961EE7201203310', 6, NULL, NULL, 2, NULL, 0, 'After cooling, clean head cheeses of any fat and aspic that accumulated on the surface, even them out and cut off excess twine.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ABA53BC4E5179E40BF0E8DDF86636FB6', X'470946E4C05BD3489CB27F88540FDBE2', X'470946E4C05BD3489CB27F88540FDBE2', 0, NULL, NULL, 2, NULL, 0, 'Blanch tongues with hot water and remove the skin.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B582E217B109AF40B4F8E6B06F42C6CC', X'470946E4C05BD3489CB27F88540FDBE2', X'470946E4C05BD3489CB27F88540FDBE2', 1, NULL, NULL, 2, NULL, 0, 'Cut tongues into 1-1¾ (25-40 mm) cubes. Mix thoroughly with 1.0 g (1/5 tsp.) Cure #1, place in container, cover with a cloth and place in a refrigerator for 72 hours. You may skip this curing step but the tongues in your head cheese would be of a gray color instead of vivid pink.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'907F670CC2EA354EB4E4C40ABEB2540A', X'470946E4C05BD3489CB27F88540FDBE2', X'470946E4C05BD3489CB27F88540FDBE2', 2, NULL, NULL, 2, NULL, 0, 'Except liver, poach other meats in water until soft: tongues at 185º F (85º C). skins at 203º F, (95º C). jowls at 185º F (85º C).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4DCFD51BCCC5764C9A590A16EFE7AEDF', X'470946E4C05BD3489CB27F88540FDBE2', X'470946E4C05BD3489CB27F88540FDBE2', 3, NULL, NULL, 2, NULL, 0, 'Cut boiled skinless dewlap into 5 mm (¼) cubes. Dice or cut boiled pork or veal tongues into 4 cm (1¾) cubes (They were previously cut for curing, if this step was employed). Grind boiled skins through 2-3 mm plate. Grind raw pork liver through 2-3 mm plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'83BA04195C889F4299C7B6E0A328CB0C', X'470946E4C05BD3489CB27F88540FDBE2', X'470946E4C05BD3489CB27F88540FDBE2', 4, NULL, NULL, 2, NULL, 0, 'Add salt and spices to cut and ground meats and mix thoroughly with blood.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'670123522460DD43A4105DB24B1C482D', X'470946E4C05BD3489CB27F88540FDBE2', X'470946E4C05BD3489CB27F88540FDBE2', 5, NULL, NULL, 2, NULL, 0, 'Stuff into middles or bladders loosely and sew the ends with twine. You may use large diameter waterproof casings.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'372D5866A6295C42A003C389428E5575', X'470946E4C05BD3489CB27F88540FDBE2', X'470946E4C05BD3489CB27F88540FDBE2', 6, NULL, NULL, 2, NULL, 0, 'Poach at 82°C (180°F) for 90-120 min (depending on size) until the internal temperature of the meat reaches 68-70°C (154-158°F). Remove air with a needle from pieces that swim up to the surface.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68C0E4C8F0045947AFAF1D3C087E899B', X'470946E4C05BD3489CB27F88540FDBE2', X'470946E4C05BD3489CB27F88540FDBE2', 7, NULL, NULL, 2, NULL, 0, 'Spread head cheeses on a flat surface at 2-6°C (35-43°F) and let the steam out. Then flatten them with weight and cool them to below 6°C (43°F).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'54A17055BAD1C44686AE266921D05495', X'470946E4C05BD3489CB27F88540FDBE2', X'470946E4C05BD3489CB27F88540FDBE2', 8, NULL, NULL, 2, NULL, 0, 'After cooling clean head cheeses of any fat and aspic that accumulated on the surface, even them out and cut off excess twine.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FBCF56C236DAD94B83CE2F908CA6062E', X'470946E4C05BD3489CB27F88540FDBE2', X'470946E4C05BD3489CB27F88540FDBE2', 9, NULL, NULL, 2, NULL, 0, 'Store in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E588B7BDF5A2F3428A4D4739A7270D38', X'688FD70F07DB64498E511A9EB2475602', X'688FD70F07DB64498E511A9EB2475602', 0, NULL, NULL, 2, NULL, 0, 'The best quality head cheese is made from cured meats. Split heads and tongues are immersed in curing solution (salt and cure #1). The meats cured in this manner will have a beautiful red color and a longer shelf life. The fat does not contain myoglobin so it will remain white.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81F4EBD251D18B44AB060536CF4E79FA', X'688FD70F07DB64498E511A9EB2475602', X'688FD70F07DB64498E511A9EB2475602', 1, NULL, NULL, 2, NULL, 0, 'Cure split heads, tongues, skins and back fat for 4 days in 16° Bé (60° SAL) cure solution.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'421F5D6CE60CDE4FB28D5F986272EC2F', X'688FD70F07DB64498E511A9EB2475602', X'688FD70F07DB64498E511A9EB2475602', 2, NULL, NULL, 2, NULL, 0, '16° Bé (60° SAL) cure solution is made by mixing 1.32 lb (598 g) salt, 4.2 oz (120 g) cure #1 and one gallon of water. Use 1/2 part (by weight) of meat to 1 part curing solution: 100 g of meat to 200 g (200 ml) curing solution.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68AD46027DCC45469113830AA06CADE5', X'688FD70F07DB64498E511A9EB2475602', X'688FD70F07DB64498E511A9EB2475602', 3, NULL, NULL, 2, NULL, 0, 'Wash all meats in cold water. Slow boil meats at around 200° F (93° C) until meats separate from bones. Remove from the stock and spread on the table. Save the stock. Remove meat from bones when still warm.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57A65D395EE0E742A7692C73032CC3E6', X'688FD70F07DB64498E511A9EB2475602', X'688FD70F07DB64498E511A9EB2475602', 4, NULL, NULL, 2, NULL, 0, 'Cut heat meat into 1/2-3/4 (12-15 mm) pieces.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7AAD36113CFAAE49840C4E242C91EE3C', X'688FD70F07DB64498E511A9EB2475602', X'688FD70F07DB64498E511A9EB2475602', 5, NULL, NULL, 2, NULL, 0, 'Cut tongues into into strips.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0855B80C153F341863C21B1F6FD72C5', X'688FD70F07DB64498E511A9EB2475602', X'688FD70F07DB64498E511A9EB2475602', 6, NULL, NULL, 2, NULL, 0, 'Cut fat into 1 (25 mm) cubes.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FDDC36E5D347284B8D5D692934851C90', X'688FD70F07DB64498E511A9EB2475602', X'688FD70F07DB64498E511A9EB2475602', 7, NULL, NULL, 2, NULL, 0, 'Cut skins finely or grind with 1/8 (3 mm) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'940159C135D690499D51D4ACE5F3C6C3', X'688FD70F07DB64498E511A9EB2475602', X'688FD70F07DB64498E511A9EB2475602', 8, NULL, NULL, 2, NULL, 0, 'Mix all meats together with spices and starch. Keep on mixing and start slowly adding meat stock (from boiling meats) until meat absorbs no more.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'27E895A283BF764F83FDD8D0CB2A8A32', X'688FD70F07DB64498E511A9EB2475602', X'688FD70F07DB64498E511A9EB2475602', 9, NULL, NULL, 2, NULL, 0, 'Stuff into pork stomachs or beef bungs.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9266D6A822A0E245BF3A682656257BEB', X'688FD70F07DB64498E511A9EB2475602', X'688FD70F07DB64498E511A9EB2475602', 10, NULL, NULL, 2, NULL, 0, 'Cook in water at 80° C (176° F) for about 90 minutes until meat reaches 68-70° C (154-158° F) internal temperature.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'822744BA0E632C40A9F394B4FEC382BA', X'688FD70F07DB64498E511A9EB2475602', X'688FD70F07DB64498E511A9EB2475602', 11, NULL, NULL, 2, NULL, 0, 'Remove and partially cool in cold water. Place them on flat surface and press them down with boards with weights on them.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A9343324E4E5624D9B3D50FF6B693703', X'4F3755D3435C8D41A64A1A84E1803FB0', X'4F3755D3435C8D41A64A1A84E1803FB0', 0, NULL, NULL, 2, NULL, 0, 'Cover meats with water and boil at 90°C (194°F) until meats are softer. Add soup greens (celery, carrot, leek, bay leaf) for better flavor and filter the stock when done.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7FA2AB0CAA5AA44C92142AC141A0D161', X'4F3755D3435C8D41A64A1A84E1803FB0', X'4F3755D3435C8D41A64A1A84E1803FB0', 1, NULL, NULL, 2, NULL, 0, 'Remove meats and drain, filter and save the meat stock.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8B80B1BEA05DC489E059408DE7D8994', X'4F3755D3435C8D41A64A1A84E1803FB0', X'4F3755D3435C8D41A64A1A84E1803FB0', 2, NULL, NULL, 2, NULL, 0, 'Remove meat from hock bones. Grind the skins.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'918581FD5EB3EE46874909B5C21DD4FD', X'4F3755D3435C8D41A64A1A84E1803FB0', X'4F3755D3435C8D41A64A1A84E1803FB0', 3, NULL, NULL, 2, NULL, 0, 'Grind all skins through 3 mm (1/8`) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A14DEC7290B6BD448686DC10EC86DA87', X'4F3755D3435C8D41A64A1A84E1803FB0', X'4F3755D3435C8D41A64A1A84E1803FB0', 4, NULL, NULL, 2, NULL, 0, 'Cut all meats into smaller pieces.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5725AE096445FF4CB60A61D5C2F7E538', X'4F3755D3435C8D41A64A1A84E1803FB0', X'4F3755D3435C8D41A64A1A84E1803FB0', 5, NULL, NULL, 2, NULL, 0, 'Add one packet of gelatin to wine and meat stock and mix.*');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B900C98D70B8C46A1826A314C8ADF55', X'4F3755D3435C8D41A64A1A84E1803FB0', X'4F3755D3435C8D41A64A1A84E1803FB0', 6, NULL, NULL, 2, NULL, 0, 'Mix all meats with spices adding meat stock and wine.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'707E2852831CD74E98229B397B1D8841', X'4F3755D3435C8D41A64A1A84E1803FB0', X'4F3755D3435C8D41A64A1A84E1803FB0', 7, NULL, NULL, 2, NULL, 0, 'Stuff into 120 mm synthetic waterproof casings.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65E6FF985A0E974A9C8737530952BE2E', X'4F3755D3435C8D41A64A1A84E1803FB0', X'4F3755D3435C8D41A64A1A84E1803FB0', 8, NULL, NULL, 2, NULL, 0, 'Boil at 80° C (176° F) for 150 min.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD19353DA16E54419229D22BF696A331', X'4F3755D3435C8D41A64A1A84E1803FB0', X'4F3755D3435C8D41A64A1A84E1803FB0', 9, NULL, NULL, 2, NULL, 0, 'Spread head cheeses on a flat surface and let them steam out and cool.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E29712A28F1AFF49A5C2FF971F0C97C6', X'4F3755D3435C8D41A64A1A84E1803FB0', X'4F3755D3435C8D41A64A1A84E1803FB0', 10, NULL, NULL, 2, NULL, 0, 'Roll sausages over once in a while while they are cooling off.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D68AE4F96B7F24480226E07745A2F48', X'4F3755D3435C8D41A64A1A84E1803FB0', X'4F3755D3435C8D41A64A1A84E1803FB0', 11, NULL, NULL, 2, NULL, 0, 'Store in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E044607E52E6274196CCB3788576F573', X'F2E679B334BEB741BDAA65EEDF0634BC', X'F2E679B334BEB741BDAA65EEDF0634BC', 0, NULL, NULL, 2, NULL, 0, 'For best quality head cheese it is recommended to cure meats (see notes below). Place meats in boiling water but poach at 90°C (194°F) until semi-soft. Skins should be poached until soft. Spread on a flat surface for cooling.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D37D5D84C793A4980FA5935CC16F675', X'F2E679B334BEB741BDAA65EEDF0634BC', X'F2E679B334BEB741BDAA65EEDF0634BC', 1, NULL, NULL, 2, NULL, 0, 'Cut boiled pork head meat and hearts into 1.5 cm (5/8) strips. Take ¼ of the previously boiled skins and grind with onion through 3 mm plate. Cut remaining ¾ of skins into 5-6 mm (¼) strips about 5-6 mm (2-3) long.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'67FDA3808492F544A6C3670C83DE220E', X'F2E679B334BEB741BDAA65EEDF0634BC', X'F2E679B334BEB741BDAA65EEDF0634BC', 2, NULL, NULL, 2, NULL, 0, 'Mix spices thoroughly with all meats and blood.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'78C2BD75F0B1C44FB220853A94198A65', X'F2E679B334BEB741BDAA65EEDF0634BC', X'F2E679B334BEB741BDAA65EEDF0634BC', 3, NULL, NULL, 2, NULL, 0, 'Stuffing materials: pork stomachs or bladders, beef bungs, large diameter (4 inch) fibrous casings, butcher''s twine. Stuff stomachs or bladders loosely and tie or sew the ends with twine.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D386E3D33762E04498A980F865FA04BD', X'F2E679B334BEB741BDAA65EEDF0634BC', X'F2E679B334BEB741BDAA65EEDF0634BC', 4, NULL, NULL, 2, NULL, 0, 'Poach head cheeses at 85° C (185° F) for 90-180 min (depending on size) until the internal temperature of the meat reaches 68-70°C (154-158°F). Remove air with a needle from pieces that swim up to the surface.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8802265E93245945B88EF5D9E9CAC376', X'F2E679B334BEB741BDAA65EEDF0634BC', X'F2E679B334BEB741BDAA65EEDF0634BC', 5, NULL, NULL, 2, NULL, 0, 'Spread head cheeses on a flat surface at 2-6° C (35-43° F) and let the steam out. Then flatten them with weight and cool them to below 6° C (43° F). If there is no proper cooler available, cool them down to less than 12° C (53° F).');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B3A4181BD593441A9C6A07729787B06', X'F2E679B334BEB741BDAA65EEDF0634BC', X'F2E679B334BEB741BDAA65EEDF0634BC', 6, NULL, NULL, 2, NULL, 0, 'After cooling clean head cheeses of any fat and aspic that accumulated on the surface, even them out and cut off excess twine.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5205BFE0611E3B4BB18B2B8F18DEBEA4', X'2A4EB80045DE3740AE7169B7640E3282', X'2A4EB80045DE3740AE7169B7640E3282', 0, NULL, NULL, 2, NULL, 0, 'Place all meats with pepper, allspice and bay leaves in a pot and add enough water to over them. Cook on low heat 2 hours. Add vegetables and cook for about 60 minutes more until meats are easily separated from bones.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A01057F3DBA19468803443D71AA1D9D', X'2A4EB80045DE3740AE7169B7640E3282', X'2A4EB80045DE3740AE7169B7640E3282', 1, NULL, NULL, 2, NULL, 0, 'Drain the stock and save.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EFB6A0D91590B043BEC3E9EA0600B47F', X'2A4EB80045DE3740AE7169B7640E3282', X'2A4EB80045DE3740AE7169B7640E3282', 2, NULL, NULL, 2, NULL, 0, 'Separate meats from bones, cool, then grind with vegetables through ¼ (6 mm) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'083E29FF07C85F428DB18006D9419643', X'2A4EB80045DE3740AE7169B7640E3282', X'2A4EB80045DE3740AE7169B7640E3282', 3, NULL, NULL, 2, NULL, 0, 'Recombine ground meats and vegetables with stock, add spices and ½ vinegar and bring to boil. Taste and add more vinegar if needed.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A8C5683735CC6243BD9A45A86606A549', X'2A4EB80045DE3740AE7169B7640E3282', X'2A4EB80045DE3740AE7169B7640E3282', 4, NULL, NULL, 2, NULL, 0, 'Meat jelly: using ladle fill the bowls or jars with the mixture, let cool and then place in refrigerator. It is up to you how much jelly you want in a finished product. Before filling bowl, you may place on the bottom a slice of hard-boiled egg, carrot, lemon, green peas or fresh parsley as a decoration. When a solidified jelly is removed from the bowl and flipped over the decorations will be on top.');
INSERT INTO `VWR INTERNAL Meatsandsausages2 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2AD5A5C28EE26C4C91B1BB4831A6DD4B', X'2A4EB80045DE3740AE7169B7640E3282', X'2A4EB80045DE3740AE7169B7640E3282', 5, NULL, NULL, 2, NULL, 0, 'Head cheese: using ladle pour the mixture into 60-100 mm casings and cook in water at 80° C for 60 minutes. Add less stock than for jelly. Immerse in cold water for 15 minutes, then place on the table to cool. Keep in refrigerator overnight. Ready to eat when solidified.');
