INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeIngredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngredient) VALUES (X'C38FCBF8F4C93346AB22C1EA855A0F5B', X'A5C7C2D789A7544FA81CF27099116038', X'A5C7C2D789A7544FA81CF27099116038', 0, NULL, NULL, 2, NULL, 0, 'Gut and clean the fish. Soak in 20% brine for 60 minutes and then smoke at 100 F, 38 C for 90 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeIngredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngredient) VALUES (X'E285E1DFA7630B44B1A54142C5A4D9EB', X'A5C7C2D789A7544FA81CF27099116038', X'A5C7C2D789A7544FA81CF27099116038', 1, NULL, NULL, 2, NULL, 0, 'Cut the smoked fish to fit the cans.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeIngredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngredient) VALUES (X'5210F484159CFD44A7E4F32317399B41', X'A5C7C2D789A7544FA81CF27099116038', X'A5C7C2D789A7544FA81CF27099116038', 2, NULL, NULL, 2, NULL, 0, 'Pack the fish solidly leaving 1/4 inch headspace.');
