INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeIngredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngredient) VALUES (X'A4D8C2624CA09D479DFF8FBB68AA028E', X'A5C7C2D789A7544FA81CF27099116038', X'A5C7C2D789A7544FA81CF27099116038', 4, NULL, NULL, 2, NULL, 0, 'Place filled with fish open cans in the hot water in a single layer. The water should come up halfway up the outside of the cans. Do not cover cans with the lids. Covering the pot will result in moisture dripping into the cans.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeIngredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngredient) VALUES (X'96C6965AAFE25F43B6CBE770056C86B5', X'A5C7C2D789A7544FA81CF27099116038', X'A5C7C2D789A7544FA81CF27099116038', 5, NULL, NULL, 2, NULL, 0, 'Turn heat on high and bring water to boil. Adjust the heat to keep a steady boil.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeIngredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngredient) VALUES (X'A1FD3748E2645445A49D7EC359CDF52E', X'A5C7C2D789A7544FA81CF27099116038', X'A5C7C2D789A7544FA81CF27099116038', 6, NULL, NULL, 2, NULL, 0, 'Heat the pot until the fish reach a temperature of 1708 F, 778 C. Using a jar lifter remove cans from the pot and seal one can at a time.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeIngredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngredient) VALUES (X'354CE9E41F6E1B4C8B2D49E45614064D', X'A5C7C2D789A7544FA81CF27099116038', X'A5C7C2D789A7544FA81CF27099116038', 7, NULL, NULL, 2, NULL, 0, 'Place sealed cans on the rack in the canner. The canner should be already filled with about 3 inches of hot water.');
