INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeIngredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngredient) VALUES (X'81CA9C75628C884692E6C87432231704', X'BD5F31A6E49CF446918BC72C2259D0FD', X'BD5F31A6E49CF446918BC72C2259D0FD', 1, NULL, NULL, 2, NULL, 0, 'Wash the shrimp and place into 10% (40 salinometer) brine for 10 minutes. Stir the shrimp after 5 minutes. To make brine dissolve 1 lb of salt in 1 gallon of water.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeIngredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngredient) VALUES (X'CCDD87023F147A41B8842176C356FF8C', X'BD5F31A6E49CF446918BC72C2259D0FD', X'BD5F31A6E49CF446918BC72C2259D0FD', 2, NULL, NULL, 2, NULL, 0, 'Remove shrimp and drop them into a fresh boiling 10% brine for 6 minutes. Drain and place on the screen until the surface moisture evaporates away, then pack into containers without a delay.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeIngredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngredient) VALUES (X'F657067E9E54E948B89E46340CAF3A7B', X'BD5F31A6E49CF446918BC72C2259D0FD', X'BD5F31A6E49CF446918BC72C2259D0FD', 3, NULL, NULL, 2, NULL, 0, 'Pack shrimp into into glass jars to within 1-1/4 from the top. About 5 ounces of shrimp are put into 8 oz (1/2 pint) jar.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeIngredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngredient) VALUES (X'55076BE921CDE4429FE9D51F6EDC1195', X'BD5F31A6E49CF446918BC72C2259D0FD', X'BD5F31A6E49CF446918BC72C2259D0FD', 4, NULL, NULL, 2, NULL, 0, 'Fill glass jars with a hot 1% brine. To make brine dissolve 0.70 oz (20 g = 3 teaspoons) salt in 1 gallon of water.');
