INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A7912A5F3322746AA129F446950E8E1', X'959C62034D95B24CA9627D4270ADB6D1', X'959C62034D95B24CA9627D4270ADB6D1', 3, NULL, NULL, 2, NULL, 0, 'Restack the bacon slabs: top ones go on the bottom and the bottom ones on top. Apply the remaining 1/2 of curing solution when restacking the pieces. Cure for additional 48 hours at 40 F, 4 C.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC7A8632A5B5EB48A68DF028574FEE5A', X'959C62034D95B24CA9627D4270ADB6D1', X'959C62034D95B24CA9627D4270ADB6D1', 4, NULL, NULL, 2, NULL, 0, 'Make a hanging loop on one end and hang bacon for 2-3 hours in a cooler to drain and dry. When the surface feels dry, proceed to smoking.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ABF8023DB4808F49B44CB9D5870DF051', X'959C62034D95B24CA9627D4270ADB6D1', X'959C62034D95B24CA9627D4270ADB6D1', 5, NULL, NULL, 2, NULL, 0, 'Place bacon in a preheated to 122 F, 50 C, smokehouse and when bacon feels dry, apply warm smoke (86-104 F, 30-40 C) for about 2 hours until the skin becomes yellow.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D54883D1CD44DE49ACBB162041A42AD6', X'959C62034D95B24CA9627D4270ADB6D1', X'959C62034D95B24CA9627D4270ADB6D1', 6, NULL, NULL, 2, NULL, 0, 'Start preparing liquid stock broth before smoking bacon.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25EB99EB40C54A4285D50F85F8F7FCEA', X'959C62034D95B24CA9627D4270ADB6D1', X'959C62034D95B24CA9627D4270ADB6D1', 7, NULL, NULL, 2, NULL, 0, 'Pour gelatin powder (if used) on the bottom of the can.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7DAB242FF2C65842BB1333C0275C3107', X'959C62034D95B24CA9627D4270ADB6D1', X'959C62034D95B24CA9627D4270ADB6D1', 8, NULL, NULL, 2, NULL, 0, 'Cut bacon into strips that will fit the height of the can best (remember about headspace). They may be rolled (wrapped) around. Add 820 g (28 oz) of bacon, you should not have no more than 3 principal pieces. If more bacon is needed, cut a strip size that complement one of the main strips.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B63F3A801B8FE64F9E1AEE629B7DC53A', X'959C62034D95B24CA9627D4270ADB6D1', X'959C62034D95B24CA9627D4270ADB6D1', 9, NULL, NULL, 2, NULL, 0, 'Pack the bacon into the cans leaving 1/2` headspace. Add broth to 1/2 inch from top.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12C51C5739500D4F90711A08FEDAE145', X'959C62034D95B24CA9627D4270ADB6D1', X'959C62034D95B24CA9627D4270ADB6D1', 10, NULL, NULL, 2, NULL, 0, 'Exhaust cans to 170 F, 77 C meat temperature.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3ADAD502CC2CB14EBE376FC8945CAC8F', X'959C62034D95B24CA9627D4270ADB6D1', X'959C62034D95B24CA9627D4270ADB6D1', 11, NULL, NULL, 2, NULL, 0, 'Stir carefully removing the air from the bottom and sides, then add more hot liquid to 1/4` from top. Seal immediately and process at once at 240 F, 116 C:');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'219AE6BA8E81284892B3D47D68C5F1CE', X'7142FB6F0E5913479F5D8AD0E7ED57B2', X'7142FB6F0E5913479F5D8AD0E7ED57B2', 0, NULL, NULL, 2, NULL, 0, 'Soak dry peas in cold water for 12 hours. Stir often.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2FBD488A0D4CF347B8B16B5481B75546', X'7142FB6F0E5913479F5D8AD0E7ED57B2', X'7142FB6F0E5913479F5D8AD0E7ED57B2', 1, NULL, NULL, 2, NULL, 0, 'Add water to pot equipped with a steamer basked and bring to a boil. Place soaked peas in a steamer basket and cook for 3 minutes, shaking the pot a few times to make sure that all peas are scalded equally. Drain peas on screens.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D686F01C742BCE45A61546883138AC9C', X'7142FB6F0E5913479F5D8AD0E7ED57B2', X'7142FB6F0E5913479F5D8AD0E7ED57B2', 2, NULL, NULL, 2, NULL, 0, 'Dice onion and fry in lard until golden.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E420BF306FA790429C8A067C175C1E2B', X'7142FB6F0E5913479F5D8AD0E7ED57B2', X'7142FB6F0E5913479F5D8AD0E7ED57B2', 3, NULL, NULL, 2, NULL, 0, 'Cook smoked bacon for 3 minutes in boiling water. Cut bacon into pieces about 2-1/2 oz (70 g) each.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A16F2A46982B7A4382B4E300607331DC', X'7142FB6F0E5913479F5D8AD0E7ED57B2', X'7142FB6F0E5913479F5D8AD0E7ED57B2', 4, NULL, NULL, 2, NULL, 0, 'Mix peas with onion, salt and marjoram. Add peas and mix again.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A330DFE28DB51446ABF8D97DCDF2EB54', X'7142FB6F0E5913479F5D8AD0E7ED57B2', X'7142FB6F0E5913479F5D8AD0E7ED57B2', 5, NULL, NULL, 2, NULL, 0, 'Pack each can with one piece of smoked bacon, then fill with peas.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4A945FAF03CF7479756269509BC878B', X'7142FB6F0E5913479F5D8AD0E7ED57B2', X'7142FB6F0E5913479F5D8AD0E7ED57B2', 6, NULL, NULL, 2, NULL, 0, 'Add water, leaving 1/2 headspace.');
