INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA9F26CC7ECFAB4F98D64443186D9615', X'7142FB6F0E5913479F5D8AD0E7ED57B2', X'7142FB6F0E5913479F5D8AD0E7ED57B2', 8, NULL, NULL, 2, NULL, 0, 'Seal and process at once at 250 F, 121 C: 307x200.25 - 45 min');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'201A4B333B0F4B4F999D5871BBE2A852', X'0F2CE2D26613E14DA26F346F0D751EDF', X'0F2CE2D26613E14DA26F346F0D751EDF', 0, NULL, NULL, 2, NULL, 0, 'Soak dry peas in water for 3 hours. Stir.');
