INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1D61744236163479319D3F7E771998C', X'0F2CE2D26613E14DA26F346F0D751EDF', X'0F2CE2D26613E14DA26F346F0D751EDF', 3, NULL, NULL, 2, NULL, 0, 'Add 1 quart (1 liter) of water to a skillet and add bones. Bring to a boil and slow boil for 3 hours. Add all ingredients and boil additional hour (total boil 4 hours). Strain the stock and mix with fried onions.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A7E7AACE6C5EB942A6D50B371179FC57', X'0F2CE2D26613E14DA26F346F0D751EDF', X'0F2CE2D26613E14DA26F346F0D751EDF', 4, NULL, NULL, 2, NULL, 0, 'Place in each jar: 1-2 pieces of bacon, peas and fill with hot stock leaving 1 inch headspace. Seal and process at once at 250 F, 121 C:');
