INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'80EFC99D605DC44F8032066E77D013A1', X'A243D41AE854DF41B3531624E5F2F808', X'A243D41AE854DF41B3531624E5F2F808', 2, NULL, NULL, 2, NULL, 0, 'Clean heads from any remaining glands and bloody areas.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F34A8BEC3C2D8046B1B3BB5F3CA7853D', X'A243D41AE854DF41B3531624E5F2F808', X'A243D41AE854DF41B3531624E5F2F808', 3, NULL, NULL, 2, NULL, 0, 'Skins.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDC429FEF02DB2409A1B2D06D4531934', X'A243D41AE854DF41B3531624E5F2F808', X'A243D41AE854DF41B3531624E5F2F808', 4, NULL, NULL, 2, NULL, 0, 'Finely chop onion and fry in lard until golden.');
