INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F485576EBDE71B40B4C90AB7C830179B', X'A243D41AE854DF41B3531624E5F2F808', X'A243D41AE854DF41B3531624E5F2F808', 6, NULL, NULL, 2, NULL, 0, 'Mix all meats with spices well together until mixture becomes sticky.');
