INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3FE4C5FDB519FE4690B8C6342B7625DF', X'A243D41AE854DF41B3531624E5F2F808', X'A243D41AE854DF41B3531624E5F2F808', 8, NULL, NULL, 2, NULL, 0, 'Process at 250 F, 121 C:');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1AEAA16164B05489EAB39529E206752', X'8163D1311A34CC4BA5EAD5F09FDC6DD6', X'8163D1311A34CC4BA5EAD5F09FDC6DD6', 0, NULL, NULL, 2, NULL, 0, 'CFR 9,  319.304 Meat stews.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'50FF38C2D2833B49B4C3C870AB4BCA9D', X'8163D1311A34CC4BA5EAD5F09FDC6DD6', X'8163D1311A34CC4BA5EAD5F09FDC6DD6', 1, NULL, NULL, 2, NULL, 0, 'Meat stews such as `Beef Stew` or `Lamb Stew` shall contain not less than 25 percent of meat of the species named on the label, computed on the weight of the fresh meat.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B5F85D72588A5B40A6BC1C3D8AF9AB26', X'8163D1311A34CC4BA5EAD5F09FDC6DD6', X'8163D1311A34CC4BA5EAD5F09FDC6DD6', 2, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98F11C8C4D4F2C4BBD6D3C9D756D6D57', X'8163D1311A34CC4BA5EAD5F09FDC6DD6', X'8163D1311A34CC4BA5EAD5F09FDC6DD6', 3, NULL, NULL, 2, NULL, 0, 'Peel potatoes, onions and carrots, then dice them. Keep freshly peeled potatoes under water to prevent darkening.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D43D145D9CF99D42962E9D3B135718D2', X'8163D1311A34CC4BA5EAD5F09FDC6DD6', X'8163D1311A34CC4BA5EAD5F09FDC6DD6', 4, NULL, NULL, 2, NULL, 0, 'Trim beef, remove gristle and fat, cut meat into 1 inch (25 mm) cubes. Brown in a small amount of fat. Boil potatoes for 10 minutes. Mix flour with water, salt, and pepper to make gravy. Add hot meat and potatoes, vegetables and top with hot gravy leaving 1 headspace in glass jars and 1/4 headspace in cans. Remove air bubbles.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C05449F1BFB8F848B255447E6EA827A5', X'8163D1311A34CC4BA5EAD5F09FDC6DD6', X'8163D1311A34CC4BA5EAD5F09FDC6DD6', 5, NULL, NULL, 2, NULL, 0, 'Adjust lids in glass jars and seal the cans. Process at once at 240 F, 116 C.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38135084323F9A428F4CA04065CFA076', X'F0430D097F01B6498EDB176C624BD0C0', X'F0430D097F01B6498EDB176C624BD0C0', 0, NULL, NULL, 2, NULL, 0, 'Cut beef into 1 inch (25 mm) pieces. Cut jowl (bacon) into 1/2` (12 mm) pieces or grind through 1/2` (12 mm) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A90F0226F028041AEDBAE5ED6CE1330', X'F0430D097F01B6498EDB176C624BD0C0', X'F0430D097F01B6498EDB176C624BD0C0', 1, NULL, NULL, 2, NULL, 0, 'Cut onion into thin discs and fry in lard until golden.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B9F2F86B401162468DE33641131D33AE', X'F0430D097F01B6498EDB176C624BD0C0', X'F0430D097F01B6498EDB176C624BD0C0', 2, NULL, NULL, 2, NULL, 0, 'Place bones in 600 ml (20 oz) water and simmer at 194-203 F, 90-95 C for 3 hours. Add allspice, bay leaf, celery and parsley. Simmer additional 60 minutes. Strain the stock.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2AB9A019004AB1448209BAEF6A8860AB', X'F0430D097F01B6498EDB176C624BD0C0', X'F0430D097F01B6498EDB176C624BD0C0', 3, NULL, NULL, 2, NULL, 0, 'Wash buckwheat grouts. Place ground jowl/bacon in a pot and heat until there is some fat. Add buckwheat grouts, and 200 ml (7.76 oz) bone stock and slow cook for 60 minutes, stirring often. At the end, add fried onions and mix all together.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1713E46B99A434485D5A70FF6947F4C', X'F0430D097F01B6498EDB176C624BD0C0', X'F0430D097F01B6498EDB176C624BD0C0', 4, NULL, NULL, 2, NULL, 0, 'Add meat pieces to the pot and mix all together.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A0C8F317D436E45A8FFF6FEFDB31FEB', X'F0430D097F01B6498EDB176C624BD0C0', X'F0430D097F01B6498EDB176C624BD0C0', 5, NULL, NULL, 2, NULL, 0, 'Pack 120 g (4.23 oz) of the product into the can and pour 80 ml ( 2.71 oz) bone stock, leaving 1/2 headspace.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E021E54A2ECC746A4F39BB44F2CB568', X'F0430D097F01B6498EDB176C624BD0C0', X'F0430D097F01B6498EDB176C624BD0C0', 6, NULL, NULL, 2, NULL, 0, 'Exhaust cans to 170 F, 77 C meat temperature.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'457F62634B97D2479EA72ADEE68BA99A', X'F0430D097F01B6498EDB176C624BD0C0', X'F0430D097F01B6498EDB176C624BD0C0', 7, NULL, NULL, 2, NULL, 0, 'Seal and process at once at 250 F, 121 C: 307x200.25 - 45 min');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'744D1F400E2BDC45BE4ADD2B63A89793', X'CF192A5009C2E047B11E80D1E29BAE63', X'CF192A5009C2E047B11E80D1E29BAE63', 0, NULL, NULL, 2, NULL, 0, 'Place bones and ribs in a skillet and add enough water to cover. Bring to a boil, reduce heat, cover with a lid and simmer until the meat is semi-soft. Remove the ribs and separate the meat from the bones. Place bones back in the skillet and simmer for additional 3 hours. Remove bone and remove all meat. Filter the stock through a fine sieve. Cut the rib meat into 30 mm (3/4`) pieces.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51EF804365B9C6489E4CBC6C57CB1DCF', X'CF192A5009C2E047B11E80D1E29BAE63', X'CF192A5009C2E047B11E80D1E29BAE63', 1, NULL, NULL, 2, NULL, 0, 'Place cabbage in a pot and add equal by weight amount of the meat stock. Add caraway, bay leaf and cut up mushrooms and simmer everything together for 4 hours. Do not cover with the lid. Replace the meat stock lost during evaporation with more stock.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36A9FA96EAB24847AD871EFF04FD9B1F', X'CF192A5009C2E047B11E80D1E29BAE63', X'CF192A5009C2E047B11E80D1E29BAE63', 2, NULL, NULL, 2, NULL, 0, 'Melt 1/2 lard in a frying pan, then fry cut up onion just to soften it. Add beef and pork cubes and simmer for 30 minutes. Add bacon and sausage and simmer for additional 20 minutes. Stir often to prevent burning.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1555A554AAC82E4A8306C60E80C73DA8', X'CF192A5009C2E047B11E80D1E29BAE63', X'CF192A5009C2E047B11E80D1E29BAE63', 3, NULL, NULL, 2, NULL, 0, 'Add the following to the cooked cabbage: browned beef and pork, onion, salt, all boiled meat that was separated from the bones, and simmer for 60 minutes, stirring often. The stew should not be covered with liquid sauce, however, the sauce should come up when the stew is pressed with the spoon. Let it cool enough to be able to handle it. Add pepper and remix the stew.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'244DB2A252475748A84788CB8D6819CC', X'CF192A5009C2E047B11E80D1E29BAE63', X'CF192A5009C2E047B11E80D1E29BAE63', 4, NULL, NULL, 2, NULL, 0, 'Fill 307x200.25 mm cans with hot stew leaving 1/4 inch headspace.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D2352028878314CAEF464BF4998F205', X'CF192A5009C2E047B11E80D1E29BAE63', X'CF192A5009C2E047B11E80D1E29BAE63', 5, NULL, NULL, 2, NULL, 0, 'Process at once 240 F, 116 C for 60 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'31D9FDC29B5A0642A9C4F40586DE8EF9', X'D09FED263594B749BCF9BC3E7B48AED4', X'D09FED263594B749BCF9BC3E7B48AED4', 0, NULL, NULL, 2, NULL, 0, 'Wash beans thoroughly and place them in a 2 qt. saucepan. Add cold water to a level of 2 to 3 inches above the beans and soak 12 to 18 hours. Drain and discard water. Combine beans with 5O cups of fresh water and 2 teaspoons salt. Bring to a boil. Reduce heat and simmer 30 minutes. Drain and discard water. Brown ground beef, chopped onions, and peppers (if desired), in a skillet. Drain off fat and add 3 teaspoons of salt, pepper, chili powder, tomatoes and drained cooked beans. Simmer 5 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16A6D886D208DD4CACD0180918E69E7B', X'D09FED263594B749BCF9BC3E7B48AED4', X'D09FED263594B749BCF9BC3E7B48AED4', 1, NULL, NULL, 2, NULL, 0, 'CAUTION Do not thicken. Fill hot jars, leaving 1 inch headspace. Remove air bubbles and adjust headspace if needed. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E15F8BFF1DDAC47B5AB8A7EBBB9CBFB', X'D09FED263594B749BCF9BC3E7B48AED4', X'D09FED263594B749BCF9BC3E7B48AED4', 2, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see page Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'711B41D258B24B42BC10DE2B0CAC59C9', X'65C692363079DF4FB10C9E23B2386A87', X'65C692363079DF4FB10C9E23B2386A87', 0, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'274DE1D5FEB05D47950E795FBB44F63C', X'65C692363079DF4FB10C9E23B2386A87', X'65C692363079DF4FB10C9E23B2386A87', 1, NULL, NULL, 2, NULL, 0, 'Wash the corned beef, cut into pieces suited for packing.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7839BBDDA582D45B3E86EBCF7449C2D', X'65C692363079DF4FB10C9E23B2386A87', X'65C692363079DF4FB10C9E23B2386A87', 2, NULL, NULL, 2, NULL, 0, 'Cover meat with cold water and bring to boil. If broth tastes very salty, strain and cover meat with fresh water, and parboil again.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5032D842D0B31843B9AB69A1738E598A', X'65C692363079DF4FB10C9E23B2386A87', X'65C692363079DF4FB10C9E23B2386A87', 3, NULL, NULL, 2, NULL, 0, 'Pack hot meat. Leave about 1 inch above meat in glass jars for headspace. Leave 1-1/2 inch in cans.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C67D19855674B545A340C6D31498C659', X'65C692363079DF4FB10C9E23B2386A87', X'65C692363079DF4FB10C9E23B2386A87', 4, NULL, NULL, 2, NULL, 0, 'Cover meat with hot broth or hot water. Leave 1 inch for headspace in jars, fill cans to top.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C331C9C6868E8942848527747C6DD9BB', X'65C692363079DF4FB10C9E23B2386A87', X'65C692363079DF4FB10C9E23B2386A87', 5, NULL, NULL, 2, NULL, 0, 'Work out bubbles with knife.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'478317765B04FA4FAF6438344AB548D8', X'65C692363079DF4FB10C9E23B2386A87', X'65C692363079DF4FB10C9E23B2386A87', 6, NULL, NULL, 2, NULL, 0, 'Seal and process at once.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'895411E27C582C459B8D6F7A77B161F4', X'4A19FA434F09364CA05BFF3A9466030E', X'4A19FA434F09364CA05BFF3A9466030E', 0, NULL, NULL, 2, NULL, 0, 'cook suet and meat in water to avoid browning. Peel, core, and quarter apples. Put meat, suet, and apples through food grinder using a medium blade. Combine all ingredients in a large saucepan, and simmer 1 hour or until slightly thickened. Stir often. Fill jars with mixture without delay, leaving 1-inch headspace. Adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29EBD2F556B1A84FA8093B602548606F', X'4A19FA434F09364CA05BFF3A9466030E', X'4A19FA434F09364CA05BFF3A9466030E', 1, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'27C08E5EDB43254E8A70517C894C0BB7', X'F66E847C1604FA4BA03BB369350EE08D', X'F66E847C1604FA4BA03BB369350EE08D', 0, NULL, NULL, 2, NULL, 0, 'Because of flavor, it is best to can livers alone. Gizzards and hearts, may be canned together. Since they are ordinarily canned and used in small quantities, directions are given only for pint glass jars and no. 2 cans.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7290BC6EAA945046A77317F8AA76E520', X'F66E847C1604FA4BA03BB369350EE08D', X'F66E847C1604FA4BA03BB369350EE08D', 1, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E06A69D08181594A99E38FE2F6127347', X'F66E847C1604FA4BA03BB369350EE08D', X'F66E847C1604FA4BA03BB369350EE08D', 2, NULL, NULL, 2, NULL, 0, 'Put giblets in cooking pan. Cover with broth made from bony pieces, or hot water. Cover pan and precook giblets until medium done. Stir occasionally.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'61FE60F70D5A164FAB8FB098D8770D01', X'F66E847C1604FA4BA03BB369350EE08D', X'F66E847C1604FA4BA03BB369350EE08D', 3, NULL, NULL, 2, NULL, 0, 'If salt is desired, put level measure into clean, empty containers: 1/2 teaspoon in pint jar or No. 2 can.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BCD0DEBA658C5F4DA257EB7FDF7D0BCC', X'F66E847C1604FA4BA03BB369350EE08D', X'F66E847C1604FA4BA03BB369350EE08D', 4, NULL, NULL, 2, NULL, 0, 'Pack giblets hot. Leave about 1 inch above meat in glass jars for head space, 1/2 inch in cans.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B393621944EB80479CEB910A0797118A', X'F66E847C1604FA4BA03BB369350EE08D', X'F66E847C1604FA4BA03BB369350EE08D', 5, NULL, NULL, 2, NULL, 0, 'Cover giblets with hot broth or hot water. Leave 1 inch for head space in jars, fill cans to top.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'01FE44C2E39EB346831D53D725BD0CC7', X'F66E847C1604FA4BA03BB369350EE08D', X'F66E847C1604FA4BA03BB369350EE08D', 6, NULL, NULL, 2, NULL, 0, 'Work out air bubbles with knife. Add more liquid, if needed, to cover meat. Be sure to leave 1 inch headspace in jars and have cans filled to top.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A4E94C424D4F9E43993476FF1EF19BC7', X'F66E847C1604FA4BA03BB369350EE08D', X'F66E847C1604FA4BA03BB369350EE08D', 7, NULL, NULL, 2, NULL, 0, 'Adjust lids on glass jars, seal cans.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F27FEBBBB860F047A56BB6DA74D18A84', X'F66E847C1604FA4BA03BB369350EE08D', X'F66E847C1604FA4BA03BB369350EE08D', 8, NULL, NULL, 2, NULL, 0, 'Process at once in the steam pressure canner at 10 pounds pressure (240 F, 116 C).');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7FD156B106D0D4494230659AD098315', X'DF8356BD7B7CE148A878D6888ABF36F1', X'DF8356BD7B7CE148A878D6888ABF36F1', 0, NULL, NULL, 2, NULL, 0, 'Choose fresh, chilled meat. For venison, add one part high-quality pork fat to three or four parts venison before grinding. Season freshly made sausage with salt and cayenne pepper (sage may cause a bitter off-flavor).');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1E46E568B2E2C459EFCD08617E3CA53', X'DF8356BD7B7CE148A878D6888ABF36F1', X'DF8356BD7B7CE148A878D6888ABF36F1', 1, NULL, NULL, 2, NULL, 0, 'Bear, beef, lamb, pork, sausage, veal, venison.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10A46D0C4E83234685D06D27DBE44B77', X'DF8356BD7B7CE148A878D6888ABF36F1', X'DF8356BD7B7CE148A878D6888ABF36F1', 2, NULL, NULL, 2, NULL, 0, 'Hot Pack - shape chopped meat into patties or balls or cut cased sausage into 3 to 4 inch links. Cook until lightly browned. Ground meat may be sauteed without shaping. Remove excess fat. Fill hot jars with pieces. Add boiling meat broth, tomato juice, or water, leaving 1 inch headspace. Remove air bubbles, adjust headspace if needed. Add 1 teaspoon of salt per quart, 1/2 teaspoon per pint, if desired. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7AB7940EA25C6341AB15E712FEE9E5CF', X'DF8356BD7B7CE148A878D6888ABF36F1', X'DF8356BD7B7CE148A878D6888ABF36F1', 3, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D9B118FEF697848B3B153C91CE2A455', X'DF8356BD7B7CE148A878D6888ABF36F1', X'DF8356BD7B7CE148A878D6888ABF36F1', 4, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11D1568E85BFDB41BF7D85DBF19731CF', X'DF8356BD7B7CE148A878D6888ABF36F1', X'DF8356BD7B7CE148A878D6888ABF36F1', 5, NULL, NULL, 2, NULL, 0, '* Raw pack is suitable only for cans. It is difficult to remove ground meat out of glass jars when packed this way.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0104D0C0B365E54CA116BF2A06C37716', X'DF8356BD7B7CE148A878D6888ABF36F1', X'DF8356BD7B7CE148A878D6888ABF36F1', 6, NULL, NULL, 2, NULL, 0, 'Raw Pack - without forming cakes, pack raw ground meat solidly into cans level with top. Place cans in a pot with water about 2 inches below can rim. Exhaust (cook) at slow boil until meat registers 170 F, 77 C. Press meat down into cans leaving 1/2 inch headspace. Seal and process at once.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E69ED1779A122E4A9B11029BBAA08434', X'322EC5A83C35994FA4464E0975BEC71E', X'322EC5A83C35994FA4464E0975BEC71E', 0, NULL, NULL, 2, NULL, 0, 'Grind scraps of lean meat, or meat from less tender cuts. Season with 1 tsp. salt to 1 pound ground meat. Mix well.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16D6FE95D85E91468845BC69A27E3349', X'322EC5A83C35994FA4464E0975BEC71E', X'322EC5A83C35994FA4464E0975BEC71E', 1, NULL, NULL, 2, NULL, 0, 'Raw Pack* - Suitable only for cans.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C006BDC8F92CC4FB0FB4B2CDE188D99', X'322EC5A83C35994FA4464E0975BEC71E', X'322EC5A83C35994FA4464E0975BEC71E', 2, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0414AE29A5F6FA469EFBF5C7185BF0AC', X'322EC5A83C35994FA4464E0975BEC71E', X'322EC5A83C35994FA4464E0975BEC71E', 3, NULL, NULL, 2, NULL, 0, 'Form meat into small fairly thin, flat cakes, uniform in thickness (not humped in middle). Make them of a size to pack into the containers without breaking.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BAD530D95E81484B868C9A9609EFA1D0', X'322EC5A83C35994FA4464E0975BEC71E', X'322EC5A83C35994FA4464E0975BEC71E', 4, NULL, NULL, 2, NULL, 0, 'Place meat cakes in heavy baking pan, oiled slightly if necessary to prevent sticking. Precook in moderate oven until medium done. Cakes are heated enough when they show almost no red color at center when cut.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A67D8A79C9D014C851164C77B492502', X'322EC5A83C35994FA4464E0975BEC71E', X'322EC5A83C35994FA4464E0975BEC71E', 5, NULL, NULL, 2, NULL, 0, 'Pack cakes hot. Leave one-inch space above meat in glass jars; 1/2 inch in tin cans.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'522FC9838F2DFC40BCEC36B9282E1614', X'322EC5A83C35994FA4464E0975BEC71E', X'322EC5A83C35994FA4464E0975BEC71E', 6, NULL, NULL, 2, NULL, 0, 'Cover meat with hot liquid (pan drippings with fat skimmed off, meat broth, or water). Again leave 1 inch space above liquid in glass jars. Fill tin cans to top.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'269D780E7FC048429D2960C2DABF1D33', X'322EC5A83C35994FA4464E0975BEC71E', X'322EC5A83C35994FA4464E0975BEC71E', 7, NULL, NULL, 2, NULL, 0, 'Work out air bubbles. Add more liquid if necessary; leave 1 inch above liquid in glass jars, fill cans to top.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0856F3B280277547BEB1DCAB07846C79', X'322EC5A83C35994FA4464E0975BEC71E', X'322EC5A83C35994FA4464E0975BEC71E', 8, NULL, NULL, 2, NULL, 0, 'Seal and process at once.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE9F755F1756C741A6B35F83A03D4D1F', X'322EC5A83C35994FA4464E0975BEC71E', X'322EC5A83C35994FA4464E0975BEC71E', 9, NULL, NULL, 2, NULL, 0, 'Pack ground raw meat solidly into tin cans, level with the top.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6F543817022E649AEC146CDBC1AA263', X'322EC5A83C35994FA4464E0975BEC71E', X'322EC5A83C35994FA4464E0975BEC71E', 10, NULL, NULL, 2, NULL, 0, 'Place open can in large vessel of water and heat as directed above for raw pack of cut pieces of meat.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'198FB5CC841C9842A45A3D1C36231960', X'322EC5A83C35994FA4464E0975BEC71E', X'322EC5A83C35994FA4464E0975BEC71E', 11, NULL, NULL, 2, NULL, 0, 'Press meat down about 1/2 inch below rim of can. Seal can and process at once.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6390B2F05E704A4AB77EE57B3932D572', X'D38274C11CD3254985A13D4D479187E6', X'D38274C11CD3254985A13D4D479187E6', 0, NULL, NULL, 2, NULL, 0, 'Heart - remove thick connective tissue before cutting into pieces.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'71C6B939AF9E3E4B826A41F23C72B093', X'D38274C11CD3254985A13D4D479187E6', X'D38274C11CD3254985A13D4D479187E6', 1, NULL, NULL, 2, NULL, 0, 'Tongue - drop tongue into boiling water and simmer about 45 minutes or until skin can be removed, before cutting into pieces.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D752E794D95D7E42B05DD9298BBE75B1', X'D38274C11CD3254985A13D4D479187E6', X'D38274C11CD3254985A13D4D479187E6', 2, NULL, NULL, 2, NULL, 0, 'The heart and tongue are generally used as fresh meat. If you do wish to can them follow directions for beef, veal, pork, lamb as hot packed.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1488DDB2BCA7049B760E69870F7B66A', X'D38274C11CD3254985A13D4D479187E6', X'D38274C11CD3254985A13D4D479187E6', 3, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'348A8D2DEB34A14DB2F0BCA811FADEE3', X'D38274C11CD3254985A13D4D479187E6', X'D38274C11CD3254985A13D4D479187E6', 4, NULL, NULL, 2, NULL, 0, 'Place hearts in boiling water. Simmer 15-20 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B3E75C8AF03A148AD8AA7BDF474BEB0', X'D38274C11CD3254985A13D4D479187E6', X'D38274C11CD3254985A13D4D479187E6', 5, NULL, NULL, 2, NULL, 0, 'Reheat the prepared tongues in the broth or hot water.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EBB10DB2ECC08E4486E27F95EF71DF5A', X'D38274C11CD3254985A13D4D479187E6', X'D38274C11CD3254985A13D4D479187E6', 6, NULL, NULL, 2, NULL, 0, 'Add 1 teaspoon of salt per quart to the jar, 1/2 teaspoon to pints, if desired.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'740B77255C913A4A9874164CA096725E', X'D38274C11CD3254985A13D4D479187E6', X'D38274C11CD3254985A13D4D479187E6', 7, NULL, NULL, 2, NULL, 0, 'Pack heart or tongue hot. Leave one inch headspace in glass jars, 1/2 inch in cans.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A9143770411E424DB7E3A00D3D3F77D7', X'D38274C11CD3254985A13D4D479187E6', X'D38274C11CD3254985A13D4D479187E6', 8, NULL, NULL, 2, NULL, 0, 'Cover meat with the hot pre-cooking liquid. Leave one inch space above liquid in glass jars, fill cans to top. Work out bubbles with a knife.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F080DEE21244E4FAFC97638EAC2F6F8', X'D38274C11CD3254985A13D4D479187E6', X'D38274C11CD3254985A13D4D479187E6', 9, NULL, NULL, 2, NULL, 0, 'Seal and process at once at 10 pounds pressure (240 F, 116 C).');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C1F8BC9E654FA49BDAC08404E7C2B1A', X'53C713F3FDB9F4438E609A278BD481FA', X'53C713F3FDB9F4438E609A278BD481FA', 0, NULL, NULL, 2, NULL, 0, 'Soak beans for 12 hours in water. Stir. Dry beans must gain 85% in weight (use scale). If more weight gain is needed soak beans longer.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F72D6317D38CD44B711E7C479CD3ECD', X'53C713F3FDB9F4438E609A278BD481FA', X'53C713F3FDB9F4438E609A278BD481FA', 1, NULL, NULL, 2, NULL, 0, 'Cut mutton into 1 inch (25 mm) pieces. Mix with salt, pepper and garlic.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'294F3C7043BC03438F528687856117D5', X'53C713F3FDB9F4438E609A278BD481FA', X'53C713F3FDB9F4438E609A278BD481FA', 2, NULL, NULL, 2, NULL, 0, 'Place pork bones in pot, cover with 600 ml (20 oz) water and boil for 4 hours. Strain the stock.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BCF9BCC1FB2FAE4189C572102C4CE071', X'53C713F3FDB9F4438E609A278BD481FA', X'53C713F3FDB9F4438E609A278BD481FA', 3, NULL, NULL, 2, NULL, 0, 'Making the roux. Heat the lard in a frying pan, adding flour. Stir continuously until light brown. Add 160 ml (2/3 cup) of pork stock (leftover liquid from boiling bones) and mix together. Add vinegar and mix.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55AF018AEC564448BA0A3489983F4CE9', X'53C713F3FDB9F4438E609A278BD481FA', X'53C713F3FDB9F4438E609A278BD481FA', 4, NULL, NULL, 2, NULL, 0, 'Mix diced meats, ground skins, diced onion, spices and the roux together.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F9C2E7B5E538DE4F92D4D74787A61A31', X'53C713F3FDB9F4438E609A278BD481FA', X'53C713F3FDB9F4438E609A278BD481FA', 5, NULL, NULL, 2, NULL, 0, 'a. Pack about 160 g meat (5.64 oz) and cover with roux-stock liquid. Add about 200 g (7.05 oz) beans into pint jars. Fill with hot boiling stock leaving 1 inch headspace. b. Pack about 100 g of meat (3.52 oz) and cover with roux-stock liquid. Add about 120 g (4.23 oz) of beans. Fill with hot boiling stock leaving 1 inch headspace.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0BED2CF7697CD948A9302B82B26A63BB', X'53C713F3FDB9F4438E609A278BD481FA', X'53C713F3FDB9F4438E609A278BD481FA', 6, NULL, NULL, 2, NULL, 0, 'Remove air bubbles, adjust lids and process at once at 250 F, 121 C: 1/2 Pint jars - 50 min Pint jars - 60 min');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D75E2DEF0C734140A8B7B57BFB7A3209', X'46C4D744DB7CEF4F9D88A9D27451B362', X'46C4D744DB7CEF4F9D88A9D27451B362', 0, NULL, NULL, 2, NULL, 0, 'Cut mutton into 1 inch (25 mm) pieces.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65F40584617CE14C9B3003AAE0303C74', X'46C4D744DB7CEF4F9D88A9D27451B362', X'46C4D744DB7CEF4F9D88A9D27451B362', 1, NULL, NULL, 2, NULL, 0, 'Fry meat pieces in lard until medium cooked.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB85138869835742B16495259F8A7609', X'46C4D744DB7CEF4F9D88A9D27451B362', X'46C4D744DB7CEF4F9D88A9D27451B362', 2, NULL, NULL, 2, NULL, 0, 'Remove meat pieces and mix with salt, spices and tomato sauce. Save the meat sauce.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12650096CEC4354BB0DB43B6C651A847', X'46C4D744DB7CEF4F9D88A9D27451B362', X'46C4D744DB7CEF4F9D88A9D27451B362', 3, NULL, NULL, 2, NULL, 0, 'Place pork bones in pot, cover with 1 liter (1 quart) of water and boil for 4 hours. Strain the stock. Add meat sauce (from frying meat) and mix with pork stock.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75675CFA0040B441A80A6A8CBC2EBC84', X'46C4D744DB7CEF4F9D88A9D27451B362', X'46C4D744DB7CEF4F9D88A9D27451B362', 4, NULL, NULL, 2, NULL, 0, 'Wash the raw rice.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C2CF4EA9C602A5489787576C60A15A02', X'46C4D744DB7CEF4F9D88A9D27451B362', X'46C4D744DB7CEF4F9D88A9D27451B362', 5, NULL, NULL, 2, NULL, 0, 'Pack about 140 g meat (5 oz) and 100 g (3.5 oz) of rice into pint jars. Fill with hot boiling stock leaving 1 inch headspace. Pack about 85 g of meat (3 oz) and 50 g (1.76 oz) of rice. Fill with hot boiling stock leaving 1 inch headspace.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E2DE17B0165EB469B1C874BA5B53039', X'46C4D744DB7CEF4F9D88A9D27451B362', X'46C4D744DB7CEF4F9D88A9D27451B362', 6, NULL, NULL, 2, NULL, 0, 'Remove air bubbles, adjust lids and process at once at 150 F, 121 C: 1/2 Pint jars - 40 min Pint jars - 50 min');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18D1BDF6F27A89429786E1999A6ADB1A', X'C3E48F57780ED04192D4296916E48BB5', X'C3E48F57780ED04192D4296916E48BB5', 0, NULL, NULL, 2, NULL, 0, 'NOTE there is no thermal processing involved.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21A8C65F652F744BBB709BADC6266AC9', X'C3E48F57780ED04192D4296916E48BB5', X'C3E48F57780ED04192D4296916E48BB5', 1, NULL, NULL, 2, NULL, 0, 'There were very few products that were not submitted to thermal process. The products were lard and smoked bacon, both packed in cans. Smoked sausages were often packed in lard to extend their shelf life. The practice was not limited to Eastern European countries, but was common in other areas, for example in Cuba. Such products were kept in root cellars or in cool pantries. Fresh fat is a poor medium for bacteria to grow as it contains very few nutrients and little moisture (15%) as compared to meat which contains proteins, vitamins, minerals and 75% moisture. During production of lard most of the moisture is evaporated during heating and this is the reason lard keeps so well.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DBFC0B587A13204AA60FAE7B983149A2', X'C3E48F57780ED04192D4296916E48BB5', X'C3E48F57780ED04192D4296916E48BB5', 2, NULL, NULL, 2, NULL, 0, 'Dice back fat into 1/4 inch cubes or grind through a 3/8` plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C02183956FF35144894B0437AC7709CB', X'C3E48F57780ED04192D4296916E48BB5', X'C3E48F57780ED04192D4296916E48BB5', 3, NULL, NULL, 2, NULL, 0, 'Put skillet on a heat source and add diced/ground back fat. Apply heat and start melting the fat, stirring often. Any indication of smoke signifies that the temperature is too high, turn the heat down.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF05DCD12BD491459054FDE1835D81A8', X'C3E48F57780ED04192D4296916E48BB5', X'C3E48F57780ED04192D4296916E48BB5', 4, NULL, NULL, 2, NULL, 0, 'Remove cracklings by filtering the lard through a strainer.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1D175D1C0479F4F8F475FB2C87B5727', X'C3E48F57780ED04192D4296916E48BB5', X'C3E48F57780ED04192D4296916E48BB5', 5, NULL, NULL, 2, NULL, 0, 'When the temperature of the lard drops to 72 F, 22 C, pour the lard into No. 2.5, 401 x 411 or No. 2, 307 x 409 cans. Place in a cool area so the lard will set. If needed, you can add more warm (liquid) lard to the container.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B174EEEEE7CAF64DB40DDE10CF91FB8B', X'C3E48F57780ED04192D4296916E48BB5', X'C3E48F57780ED04192D4296916E48BB5', 6, NULL, NULL, 2, NULL, 0, 'Seal the cans.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1EA701FDE4774E449A1A68E897F11809', X'C3E48F57780ED04192D4296916E48BB5', X'C3E48F57780ED04192D4296916E48BB5', 7, NULL, NULL, 2, NULL, 0, 'Clean the cans and store.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'150F36C9D84A36459DEAE3B6F5B7100D', X'63387E9078E48B468BEB95F168E57AA6', X'63387E9078E48B468BEB95F168E57AA6', 0, NULL, NULL, 2, NULL, 0, 'Grind back fat through 1/4` (5 mm) plate adding salt.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9CACDEE41115E04D9469C53433CFC5AC', X'63387E9078E48B468BEB95F168E57AA6', X'63387E9078E48B468BEB95F168E57AA6', 1, NULL, NULL, 2, NULL, 0, 'Mix the ground fat to distribute uniformly salt.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D8C9DA6D80EBE4EBE188368849CF30C', X'63387E9078E48B468BEB95F168E57AA6', X'63387E9078E48B468BEB95F168E57AA6', 2, NULL, NULL, 2, NULL, 0, 'Warm up the fat slightly (100 F, 38 C) then pour into warm jars leaving 1 inch headspace.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A777A30F95B48941AA942DB3E0F6F1F4', X'63387E9078E48B468BEB95F168E57AA6', X'63387E9078E48B468BEB95F168E57AA6', 3, NULL, NULL, 2, NULL, 0, 'Process without delay at 250 F, 121 C: 1/2 Pint jar - 50 min Pint jar - 60 min');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6885A24C2499B34E9CAE03254F7C9296', X'83CC3D1D0DCBBD40B2FE674C8B9D892A', X'83CC3D1D0DCBBD40B2FE674C8B9D892A', 0, NULL, NULL, 2, NULL, 0, 'Dice fresh garlic finely, then mix/grind with a small amount of salt. Use a spoon and the cup or mortar and pestle for that purpose.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC6703ACE850414B9C2DB4023A6F8B40', X'83CC3D1D0DCBBD40B2FE674C8B9D892A', X'83CC3D1D0DCBBD40B2FE674C8B9D892A', 1, NULL, NULL, 2, NULL, 0, 'Grind fresh back fat and smoked back fat together through 1/4` (5-6 mm) plate adding all ingredients.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'840F40034914EF4A81F7A7355B9DF99C', X'83CC3D1D0DCBBD40B2FE674C8B9D892A', X'83CC3D1D0DCBBD40B2FE674C8B9D892A', 2, NULL, NULL, 2, NULL, 0, 'Remix ground fat to distribute uniformly the ingredients.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F9FE3CAD4AD3441A3A15725280807E7', X'83CC3D1D0DCBBD40B2FE674C8B9D892A', X'83CC3D1D0DCBBD40B2FE674C8B9D892A', 3, NULL, NULL, 2, NULL, 0, 'Warm up the fat slightly (100 F, 38 C) then pour into warm jars leaving 1 inch headspace.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D9523C2C5835146B59A5702A7B862B6', X'83CC3D1D0DCBBD40B2FE674C8B9D892A', X'83CC3D1D0DCBBD40B2FE674C8B9D892A', 4, NULL, NULL, 2, NULL, 0, 'Process without delay at 250 F, 121 C: 1/2 Pint jar - 50 min Pint jar - 60 min');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D263265107F15438AB439A527844E7C', X'B41F96696237224C907175ACF654C0A9', X'B41F96696237224C907175ACF654C0A9', 0, NULL, NULL, 2, NULL, 0, 'Peel the onions, slice into discs and fry in fat until golden (don''t brown them).');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33A3777FFC73854BB51A5209C4D71C18', X'B41F96696237224C907175ACF654C0A9', X'B41F96696237224C907175ACF654C0A9', 1, NULL, NULL, 2, NULL, 0, 'Grind back fat through 1/4` (5-6 mm) plate together with salt and onion.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A39981C59711A74CB83F9039C1556CE5', X'B41F96696237224C907175ACF654C0A9', X'B41F96696237224C907175ACF654C0A9', 2, NULL, NULL, 2, NULL, 0, 'Mix the ground fat to distribute uniformly the ingredients.');
