INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F9329F1419456F41905E76C5B2515F5E', X'B41F96696237224C907175ACF654C0A9', X'B41F96696237224C907175ACF654C0A9', 4, NULL, NULL, 2, NULL, 0, 'Process without delay at 250 F, 121 C: 1/2 Pint jar - 50 min Pint jar - 60 min');
