INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6FC88257750A194586A9E58712475D43', X'9AF2C8FF49969E4C9363A9D49966FE12', X'9AF2C8FF49969E4C9363A9D49966FE12', 1, NULL, NULL, 2, NULL, 0, 'Curing mix. Mix salt with cure #1');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B153947DAAECC64A9F3FDAA75ED9E76F', X'9AF2C8FF49969E4C9363A9D49966FE12', X'9AF2C8FF49969E4C9363A9D49966FE12', 2, NULL, NULL, 2, NULL, 0, 'Cut all meat into 1 inch pieces, but keep both meat grades in separate containers.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E5EC2681FED724FA9CD03CD9B8C4D68', X'9AF2C8FF49969E4C9363A9D49966FE12', X'9AF2C8FF49969E4C9363A9D49966FE12', 3, NULL, NULL, 2, NULL, 0, 'Mix pork grade I with ? of curing mix. Mix pork grade III with ? of curing mix. Pack tightly in separate containers and cover meat with a clean cloth.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F213FF8F7FF85B4F94AA703A03E01DDB', X'9AF2C8FF49969E4C9363A9D49966FE12', X'9AF2C8FF49969E4C9363A9D49966FE12', 4, NULL, NULL, 2, NULL, 0, 'Cure for 72 hours in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26B4BD5BF89F0E4EA82E184D3B6CF09E', X'9AF2C8FF49969E4C9363A9D49966FE12', X'9AF2C8FF49969E4C9363A9D49966FE12', 5, NULL, NULL, 2, NULL, 0, 'Grind pork grade III through ?` (3 mm) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'78341568BDC2CA4BA1F7F6799A21C50A', X'9AF2C8FF49969E4C9363A9D49966FE12', X'9AF2C8FF49969E4C9363A9D49966FE12', 6, NULL, NULL, 2, NULL, 0, 'Mix (knead) pork grade I until it becomes sticky. During mixing start adding potato flour. When meat is thoroughly mixed with the flour, add 30 ml of cold water and remix all together.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC1AA56727DFA748966D0CFC8934A1E4', X'9AF2C8FF49969E4C9363A9D49966FE12', X'9AF2C8FF49969E4C9363A9D49966FE12', 7, NULL, NULL, 2, NULL, 0, 'Fill cans leaving  inch headspace.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA1AA66F274EFF4E9D15E2850944BEB2', X'9AF2C8FF49969E4C9363A9D49966FE12', X'9AF2C8FF49969E4C9363A9D49966FE12', 8, NULL, NULL, 2, NULL, 0, 'Exhaust cans to 170 F, 77 C meat temperature.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6DF0373471617046831A23CEFF613BC6', X'9AF2C8FF49969E4C9363A9D49966FE12', X'9AF2C8FF49969E4C9363A9D49966FE12', 9, NULL, NULL, 2, NULL, 0, 'Process at once at 250 F, 121 C: No.2, 307x409 - 80 min');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B5B55580F05275479D6B95519479408D', X'AC8295916A723E48A137302BE5A29B82', X'AC8295916A723E48A137302BE5A29B82', 0, NULL, NULL, 2, NULL, 0, 'Cut pork into 1` (25 mm) pieces.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F9E06898D28704EBDD97FCC7C430C03', X'AC8295916A723E48A137302BE5A29B82', X'AC8295916A723E48A137302BE5A29B82', 1, NULL, NULL, 2, NULL, 0, 'Simmer the skins with bay leaf for 25 minutes at 194-203 F, 90-95 C, until semi-soft, then strain and place on screen/table to drain and cool. Save the liquid.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D2AFA3A8A2015B4CACBD6CA46C367A6E', X'AC8295916A723E48A137302BE5A29B82', X'AC8295916A723E48A137302BE5A29B82', 2, NULL, NULL, 2, NULL, 0, 'Grind the skins through 1/8` (3 mm) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DDA0E02834B23F489BB5F01D850C1BB0', X'AC8295916A723E48A137302BE5A29B82', X'AC8295916A723E48A137302BE5A29B82', 3, NULL, NULL, 2, NULL, 0, 'Making the roux. Heat the lard in a frying pan, adding flour. Stir continuously until light brown. Add 160 ml (2/3 cup) of cold skin stock (leftover liquid from boiling skins) and mix together.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5BEC4DA0BD7CA3498663429499943E4B', X'AC8295916A723E48A137302BE5A29B82', X'AC8295916A723E48A137302BE5A29B82', 4, NULL, NULL, 2, NULL, 0, 'Mix diced meats, ground skins, diced onion, spices and the roux together.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'556FA2B96B5E3445A59073FEC96C8A6E', X'AC8295916A723E48A137302BE5A29B82', X'AC8295916A723E48A137302BE5A29B82', 5, NULL, NULL, 2, NULL, 0, 'Pack the cans, leaving 1/2` headspace.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'80C1B3130966D54CBEB3D3C8DE86A006', X'AC8295916A723E48A137302BE5A29B82', X'AC8295916A723E48A137302BE5A29B82', 6, NULL, NULL, 2, NULL, 0, 'Exhaust cans to 170 F, 77 C meat temperature and remove air bubbles.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0BB248EC0D1CA946A52E08D51CB081F1', X'AC8295916A723E48A137302BE5A29B82', X'AC8295916A723E48A137302BE5A29B82', 7, NULL, NULL, 2, NULL, 0, 'Fill the cans with hot boiling skin stock to 1/4` from top.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AEB63F879DDB344A82D0A1CFC970BCD2', X'AC8295916A723E48A137302BE5A29B82', X'AC8295916A723E48A137302BE5A29B82', 8, NULL, NULL, 2, NULL, 0, 'Process at once at 240 F, 116 C: No.1 (picnic), 211x400 - 55 min.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DDA0B2753060074D9EF238CC744E74BB', X'71223F3FAFED1D4D8396DDEBC76D3E51', X'71223F3FAFED1D4D8396DDEBC76D3E51', 0, NULL, NULL, 2, NULL, 0, 'Beef: Saw or crack fresh trimmed beef bones to enhance extraction of flavor. Rinse bones and place in a large stockpot or kettle, cover bones with water, add pot cover, and simmer 3 to 4 hours. Remove bones, cool broth, and pick off meat. Skim off fat, add meat trimmings removed from bones to broth, and reheat to boiling. Fill hot jars, leaving 1 inch headspace. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7EA64E42C3722F419BD3C87EEC3153DC', X'71223F3FAFED1D4D8396DDEBC76D3E51', X'71223F3FAFED1D4D8396DDEBC76D3E51', 1, NULL, NULL, 2, NULL, 0, 'Chicken or turkey: Place large carcass bones (with most of meat removed) in a large stock pot, add enough water to cover bones, cover pot, and simmer 30 to 45 minutes or until remaining attached meat can be easily stripped from bones. Remove bones and pieces, cool broth, strip meat, discard excess fat, and return meat trimmings to broth. Reheat to boiling and fill jars, leaving 1 inch headspace. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'887757AD9A9F7A46B173FEB81550BDED', X'71223F3FAFED1D4D8396DDEBC76D3E51', X'71223F3FAFED1D4D8396DDEBC76D3E51', 2, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA964AD25C229147A453BB7203D69EFE', X'71223F3FAFED1D4D8396DDEBC76D3E51', X'71223F3FAFED1D4D8396DDEBC76D3E51', 3, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD19A1257E54364B833954492201C474', X'DDAAB3AFDE34844F9C209DFE74D2B0C7', X'DDAAB3AFDE34844F9C209DFE74D2B0C7', 0, NULL, NULL, 2, NULL, 0, 'Combine ingredients. Yield is 7 quarts or 16 pints.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F4DFC0F65987646BDBCC04F2995FDF3', X'DDAAB3AFDE34844F9C209DFE74D2B0C7', X'DDAAB3AFDE34844F9C209DFE74D2B0C7', 1, NULL, NULL, 2, NULL, 0, 'Glass jars. Fill jars to top with raw meat-vegetable mixture. Add salt if desired: 1/2 teaspoon per pint or 1 teaspoon per quart. Adjust lids and process at once at 10 pounds pressure (240 F).');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'06C7AF98012A654CAFEC020EB2E177B1', X'DDAAB3AFDE34844F9C209DFE74D2B0C7', X'DDAAB3AFDE34844F9C209DFE74D2B0C7', 2, NULL, NULL, 2, NULL, 0, 'Metal cans. Fill cans to top with raw meat-vegetable mixture. Do not add liquid. Add salt if desired: 1/2 teaspoon to No. 2 cans or 1 teaspoon to No. 2.5 cans. To exhaust air, cook stew at slow boil to 170 F., or until medium done (about 50 minutes). Seal cans and process at once at 10 pounds pressure (240 F).');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D2BB292DEC89C47AC3D572E4146D0D0', X'DDAAB3AFDE34844F9C209DFE74D2B0C7', X'DDAAB3AFDE34844F9C209DFE74D2B0C7', 3, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16A356518169144395D8081C3A4F97C8', X'B1A0A66B2C95F54F812C45F2996AEF95', X'B1A0A66B2C95F54F812C45F2996AEF95', 0, NULL, NULL, 2, NULL, 0, 'Wash the liver, cut it across and then scald in hot water (167 F, 75 C) until no more blood is released.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D7800D9CF2893428FA608E10F8118E8', X'B1A0A66B2C95F54F812C45F2996AEF95', X'B1A0A66B2C95F54F812C45F2996AEF95', 1, NULL, NULL, 2, NULL, 0, 'Wash the skins and feet meat. Simmer in water (194 F, 90 C) for two hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A8D455836A129B41996F73023E23533E', X'B1A0A66B2C95F54F812C45F2996AEF95', X'B1A0A66B2C95F54F812C45F2996AEF95', 2, NULL, NULL, 2, NULL, 0, 'Simmer all pork and fat trimmings with 1/2 bay leaf in a little water (194 F, 90 C) for about 30 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33EFDB3B3077514D8ACC8BB0F33C8B36', X'B1A0A66B2C95F54F812C45F2996AEF95', X'B1A0A66B2C95F54F812C45F2996AEF95', 3, NULL, NULL, 2, NULL, 0, 'Drain and leave on the screen/table to cool down.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'010F4738BC9D7A429822D57C3C9FEE82', X'B1A0A66B2C95F54F812C45F2996AEF95', X'B1A0A66B2C95F54F812C45F2996AEF95', 4, NULL, NULL, 2, NULL, 0, 'Cut the onion in discs and fry in fat until golden.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5FB368019DCBE409870E1AD4ACD6C76', X'B1A0A66B2C95F54F812C45F2996AEF95', X'B1A0A66B2C95F54F812C45F2996AEF95', 5, NULL, NULL, 2, NULL, 0, 'Grind liver, meat, fat trimmings and onion through 1/8` (3 mm) plate. Add all spices and either grind again or process in food processor, until one smooth paste is obtained.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FCBBA5CC8D170648A3275BFE8EAAB6CA', X'B1A0A66B2C95F54F812C45F2996AEF95', X'B1A0A66B2C95F54F812C45F2996AEF95', 6, NULL, NULL, 2, NULL, 0, 'Pack into glass jars leaving 1 inch headspace.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7EEBFB42FE9A364B8DAA87B6B12978E8', X'B1A0A66B2C95F54F812C45F2996AEF95', X'B1A0A66B2C95F54F812C45F2996AEF95', 7, NULL, NULL, 2, NULL, 0, 'Place jars into warm water and heat up to about 100 F, 38 C.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ACDF4EEA4BB20F4286327AA10C9D95AF', X'B1A0A66B2C95F54F812C45F2996AEF95', X'B1A0A66B2C95F54F812C45F2996AEF95', 8, NULL, NULL, 2, NULL, 0, 'Seal jars, and process at once at 250 F, 121 C: 1/2 Pint - 50 min Pint - 60 min');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E69B021CB3E8D14EBB457DFBA0685C75', X'63EFA645D39DCE468CE6B83BA52FC62A', X'63EFA645D39DCE468CE6B83BA52FC62A', 0, NULL, NULL, 2, NULL, 0, 'Continue after step 4:');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2A7A48467D57443924C8E0AF37FD219', X'63EFA645D39DCE468CE6B83BA52FC62A', X'63EFA645D39DCE468CE6B83BA52FC62A', 1, NULL, NULL, 2, NULL, 0, 'Wash the liver, cut it across and then scald it in hot water (167 F, 75 C) until no more blood is released.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89DE5F9BB8295E4EA47FCF5555A2B1A6', X'63EFA645D39DCE468CE6B83BA52FC62A', X'63EFA645D39DCE468CE6B83BA52FC62A', 2, NULL, NULL, 2, NULL, 0, 'Dice onion, take 10 g of fat trimmings and fry onion until golden.');
