INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'086F01C9F9955B419B6460E1B55E4A53', X'63EFA645D39DCE468CE6B83BA52FC62A', X'63EFA645D39DCE468CE6B83BA52FC62A', 5, NULL, NULL, 2, NULL, 0, 'Pack into glass jars leaving 1 inch headspace.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70EE1A497A672F409BAF814FD600771A', X'63EFA645D39DCE468CE6B83BA52FC62A', X'63EFA645D39DCE468CE6B83BA52FC62A', 6, NULL, NULL, 2, NULL, 0, 'Place jars into warm water and heat up to about 100 F, 38 C.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C219FF66408ED40B13C79B94CCA631D', X'63EFA645D39DCE468CE6B83BA52FC62A', X'63EFA645D39DCE468CE6B83BA52FC62A', 7, NULL, NULL, 2, NULL, 0, 'Seal jars, and process at once at 250 F, 121 C: 1/2 Pint - 50 min Pint - 60 min');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BF9CF63118D34441A62948EAFF1333A7', X'614D1ADC32B5574E9AD0B0E5506DFA31', X'614D1ADC32B5574E9AD0B0E5506DFA31', 0, NULL, NULL, 2, NULL, 0, 'Dry cure mix. Mix salt with cure #1.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6AB610F7D0845743A0E74640268CB3D3', X'614D1ADC32B5574E9AD0B0E5506DFA31', X'614D1ADC32B5574E9AD0B0E5506DFA31', 1, NULL, NULL, 2, NULL, 0, 'Cut pork grade I into 1 inch pieces. Add 1/3 of dry cure mix during grinding. Grind pork grade II through 1/2` (12 mm) plate. Add 1/3 of dry cure mix during grinding. Grind pork grade III through 1/8 plate (3 mm). Add 1/3 tsp of dry cure mix during grinding. Re-grind again adding 6 ml (1 tsp) of water.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4C34E9353FCD34CAADD0CDB3D340288', X'614D1ADC32B5574E9AD0B0E5506DFA31', X'614D1ADC32B5574E9AD0B0E5506DFA31', 2, NULL, NULL, 2, NULL, 0, 'Mix all meats together adding pepper. When the mixture feels sticky add gradually potato flour and keep on mixing.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'470E2A0990935B478FF3DC16A760579E', X'614D1ADC32B5574E9AD0B0E5506DFA31', X'614D1ADC32B5574E9AD0B0E5506DFA31', 3, NULL, NULL, 2, NULL, 0, 'Pack cans with about 425 g (15 oz) leaving 1/4 inch headspace.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D49AFA1161A4642872CD468BC4344B8', X'614D1ADC32B5574E9AD0B0E5506DFA31', X'614D1ADC32B5574E9AD0B0E5506DFA31', 4, NULL, NULL, 2, NULL, 0, 'Exhaust cans to 170 F, 77 C meat temperature.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CFD83EB26418D64EA52F1EC1B40C8BCB', X'614D1ADC32B5574E9AD0B0E5506DFA31', X'614D1ADC32B5574E9AD0B0E5506DFA31', 5, NULL, NULL, 2, NULL, 0, 'Process at once at 240 F, 116 C: 301x408 - 55 min');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B8A87C50479B642A534B107C6051026', X'245F1161FB8C4440AB85685A3D4EFCCA', X'245F1161FB8C4440AB85685A3D4EFCCA', 0, NULL, NULL, 2, NULL, 0, 'Steps 1-4 remain the same.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5D4011BAA957D469D9665A97B963C58', X'245F1161FB8C4440AB85685A3D4EFCCA', X'245F1161FB8C4440AB85685A3D4EFCCA', 1, NULL, NULL, 2, NULL, 0, 'Curing solution 14 Be, 55 SAL. Dissolve 1.15 lb (523 g) of salt in 1 gallon of cold water. Add 120 g (4.2 oz) of cure #1 (there is 112 g of salt in cure #1 and it is included in the calculation).');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB2BEB43DF0AB340BF51D0E56C1466D1', X'245F1161FB8C4440AB85685A3D4EFCCA', X'245F1161FB8C4440AB85685A3D4EFCCA', 2, NULL, NULL, 2, NULL, 0, 'Remove bones from hocks using as few cuts as possible. Using meat pump inject each meat cut with curing solution. Place pumped meats in a container and fill with curing solution. Use 60% solution to 40% of meat by weight, for example: 400 g of meat and 600 ml solution. Cure for 48-72 hours in refrigerator. Place for 8 hours on screens to drain at refrigerator temperature (40 F, 4 C).');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ADE12F6850D4C04697A69893947377BD', X'245F1161FB8C4440AB85685A3D4EFCCA', X'245F1161FB8C4440AB85685A3D4EFCCA', 3, NULL, NULL, 2, NULL, 0, 'Simmer meats at 194-203 F, 90-95 C for 30 minutes. Cut meat to smaller pieces, about 120 g (4 oz).');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'393A6E4533D7BD4E96874E87926F1231', X'245F1161FB8C4440AB85685A3D4EFCCA', X'245F1161FB8C4440AB85685A3D4EFCCA', 4, NULL, NULL, 2, NULL, 0, 'Dissolve commercial gelatin in cold water, then bring to a boil adding bay leaf and Maggi liquid seasoning. Simmer for 30 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66A7C811787D7C44ADEE09C4BA168725', X'245F1161FB8C4440AB85685A3D4EFCCA', X'245F1161FB8C4440AB85685A3D4EFCCA', 5, NULL, NULL, 2, NULL, 0, 'Pack 2-3 pieces of meat, about 400 g (14 oz) total weight into pint jar, add a few mustard seeds and a piece of bay leaf. Pack 1-2 pieces of meat, about 200 g (7 oz) into 1/2 pint jar, adding a few mustard seeds and a part of bay leaf. Pour hot gelatin solution, leave 1 inch headspace, remove air bubbles, adjust lids and process at once at 250 F, 121 C: 1/2 Pint Jar - 30 min Pint jar - 40 min');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B53AA2E9F02C6479DE74F9919C88703', X'245F1161FB8C4440AB85685A3D4EFCCA', X'245F1161FB8C4440AB85685A3D4EFCCA', 6, NULL, NULL, 2, NULL, 0, 'No. 2.5 can, 401 x 411: Pack 800 g meat (28.22 oz) and fill 75 ml (2.5 oz) gelatin solution. Add 12 mustard seeds. 301 x 408 can: Pack 400 g meat (14.11 oz) and fill with 30 ml (1 oz) gelatin solution. Add 6 mustard seeds. Pack cans leaving 1/2 inch headspace.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6FAB062234AAB40BBE1B67421BE8487', X'245F1161FB8C4440AB85685A3D4EFCCA', X'245F1161FB8C4440AB85685A3D4EFCCA', 7, NULL, NULL, 2, NULL, 0, 'Exhaust cans to 170 F, 77 C meat temperature. Remove air bubbles. Add hot gelatin solution to 1/4 from top.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD7D8F6F709C9F4DA1EDC00688BEEE11', X'245F1161FB8C4440AB85685A3D4EFCCA', X'245F1161FB8C4440AB85685A3D4EFCCA', 8, NULL, NULL, 2, NULL, 0, 'Seal and process at once at 250 F, 121 C: 301x408 - 60 min 401x411 - 75 min');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3F1BBE3571F6C49B571D76F3879E91C', X'1EB2255B632CE84C835C66B274412264', X'1EB2255B632CE84C835C66B274412264', 0, NULL, NULL, 2, NULL, 0, 'The final product consists from pieces of meat bound together with natural gelatin. When kept cold, it should remain in one piece.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB6AD265D72F634A9E42E1607B3D80E5', X'078310E5DCF0FC46954C9D1702CA0CDF', X'078310E5DCF0FC46954C9D1702CA0CDF', 0, NULL, NULL, 2, NULL, 0, 'Soak beans for 12 hours in water. Stir. Dry beans must gain 80% in weight (use scale). If more weight gain is needed soak beans longer. Scald soaked beans in boiled water for 3 minutes. Drain and spread on table to cool.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E74F60B289F86C408F31FB3677B5C4AD', X'078310E5DCF0FC46954C9D1702CA0CDF', X'078310E5DCF0FC46954C9D1702CA0CDF', 1, NULL, NULL, 2, NULL, 0, 'Cut pork grade II into 1 inch (25 mm) pieces. Grind pork grade III through 1/8` (3 mm) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FED2C95C5FE7404A8FA4F93F7DE8B747', X'078310E5DCF0FC46954C9D1702CA0CDF', X'078310E5DCF0FC46954C9D1702CA0CDF', 2, NULL, NULL, 2, NULL, 0, 'Mix ground pork grade III with 6 g salt (1 tsp), then add pieces of pork grade II, 12 g salt (2 tsp) and keep on mixing until sticky. Add potato flour and remix.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6771560E42548B4FB8579016ABB1071C', X'078310E5DCF0FC46954C9D1702CA0CDF', X'078310E5DCF0FC46954C9D1702CA0CDF', 3, NULL, NULL, 2, NULL, 0, 'Making roux. Heat the lard in a frying pan, adding flour. Stir continuously until light brown.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A6CEA992397344582C12268A148E381', X'078310E5DCF0FC46954C9D1702CA0CDF', X'078310E5DCF0FC46954C9D1702CA0CDF', 4, NULL, NULL, 2, NULL, 0, 'Sauce. Add to roux one pint (373 ml) of tomato sauce and mix together. Add all spices, Maggi, vinegar and bring to a boil, stirring often. Turn the heat off.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3BC39047D048F3429ECAD7A608BBCDD3', X'078310E5DCF0FC46954C9D1702CA0CDF', X'078310E5DCF0FC46954C9D1702CA0CDF', 5, NULL, NULL, 2, NULL, 0, 'Pack cans with 70 g (2.47 oz) meat mass, add 80 (2.82) g soaked and scalded beans and fill with sauce leaving 1/2 inch headspace.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9587BCCB90497F4FB5AB4232E70B9063', X'078310E5DCF0FC46954C9D1702CA0CDF', X'078310E5DCF0FC46954C9D1702CA0CDF', 6, NULL, NULL, 2, NULL, 0, 'Exhaust cans to 170 F, 77 C meat temperature. Fill with hot sauce to 1/4 inch from top.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CFDBA5D20EF0344A48413086E7F163C', X'078310E5DCF0FC46954C9D1702CA0CDF', X'078310E5DCF0FC46954C9D1702CA0CDF', 7, NULL, NULL, 2, NULL, 0, 'Seal and process at once at 250 F, 121 C: 307x200.25 can - 45 min');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B1BBA5F465EBA4A9AA633BC3267AD0E', X'9E252FE2D148E643B056B6AEBEC060B0', X'9E252FE2D148E643B056B6AEBEC060B0', 0, NULL, NULL, 2, NULL, 0, 'Choose freshly killed and dressed, healthy animals. Large chickens are more flavorful than fryers. Dressed chicken should be chilled for 6 to 12 hours before canning. Remove excess fat. Cut the meat into suitable sizes for canning. Can with or without bones. Strong flavored game birds like water fowl may be soaked for 1 hour in a brine made from 1 tablespoon salt and 1 quart of water. If you soak game birds, dont add salt when packing jars.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C3805440751FFC4BB1462953C0B7C5FB', X'9E252FE2D148E643B056B6AEBEC060B0', X'9E252FE2D148E643B056B6AEBEC060B0', 1, NULL, NULL, 2, NULL, 0, 'Chicken-Duck-Goose-Turkey & Game Birds.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11EFCF91C37A924CBD0249B8D8677EE6', X'9E252FE2D148E643B056B6AEBEC060B0', X'9E252FE2D148E643B056B6AEBEC060B0', 2, NULL, NULL, 2, NULL, 0, 'Hot Pack - Boil, steam, or bake meat until about two-thirds done. Add 1 tsp salt per quart, 1/2 tsp per pint, if desired. Fill hot jars with pieces and hot broth, leaving 1 inch headspace. Remove air bubbles.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3EE56E295CE45A46927B44FFACE3B0D7', X'9E252FE2D148E643B056B6AEBEC060B0', X'9E252FE2D148E643B056B6AEBEC060B0', 3, NULL, NULL, 2, NULL, 0, 'Raw Pack - Add 1 tsp salt per quart, 1/2 tsp per pint if desired. Fill hot jars loosely with raw meat pieces, leaving 1 inch headspace. Do not add liquid. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B74CBF1B8B3ADF4EAC6A7B0296349F3F', X'9E252FE2D148E643B056B6AEBEC060B0', X'9E252FE2D148E643B056B6AEBEC060B0', 4, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0FC4F0CED8932B469E7EC9F9A93B4320', X'9E252FE2D148E643B056B6AEBEC060B0', X'9E252FE2D148E643B056B6AEBEC060B0', 5, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC101C0AC035D2448DB8625417952E53', X'832DB5B0975A4A45A6A01401521B97BB', X'832DB5B0975A4A45A6A01401521B97BB', 0, NULL, NULL, 2, NULL, 0, 'Cut rabbit into suitable sizes for canning. Soak meat for 1 hour in water containing 1 tablespoon of salt per quart, and rinse. Prepare the meaty pieces, with or without bones, and pack and process as for poultry, omitting the salt.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0C13DFA60A94914190768FC78697307A', X'832DB5B0975A4A45A6A01401521B97BB', X'832DB5B0975A4A45A6A01401521B97BB', 1, NULL, NULL, 2, NULL, 0, 'Hot Pack - Boil, steam, or bake meat until about two-thirds done. Add 1 tsp salt per quart, 1/2 tsp per pint, if desired. Fill hot jars with pieces and hot broth, leaving 1 inch headspace. Remove air bubbles.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C9DBA0032B214C4A85BBB2DA28297B16', X'832DB5B0975A4A45A6A01401521B97BB', X'832DB5B0975A4A45A6A01401521B97BB', 2, NULL, NULL, 2, NULL, 0, 'Raw pack - Add 1 tsp salt per quart, 1/2 tsp per pint if desired. Fill hot jars loosely with raw meat pieces, leaving 1-1/4 inch headspace. Do not add liquid. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'35ECC9CF681CE6418D0C9EBDBD8E0294', X'832DB5B0975A4A45A6A01401521B97BB', X'832DB5B0975A4A45A6A01401521B97BB', 3, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2BDD8FB70B94B345A681B6EA110E13E8', X'832DB5B0975A4A45A6A01401521B97BB', X'832DB5B0975A4A45A6A01401521B97BB', 4, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'32A02735E79C09498AA16AF86827CE66', X'2922C5B77CFFBF49B25EA7A0FA040F42', X'2922C5B77CFFBF49B25EA7A0FA040F42', 0, NULL, NULL, 2, NULL, 0, 'Any good sausage recipe may be used. It is best to go easy on the spices because of flavor changes which may develop during canning and storing. Mold sausage into smooth, round flat cakes. Precook, pack and process the same as hamburger, hot pack. The following recipe for sausage is suggested:');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA603E0905F9A848861010AF595FFC2A', X'2922C5B77CFFBF49B25EA7A0FA040F42', X'2922C5B77CFFBF49B25EA7A0FA040F42', 1, NULL, NULL, 2, NULL, 0, 'For four pounds ground pork use:');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E679999F233C2848A73F481B618C70DA', X'2922C5B77CFFBF49B25EA7A0FA040F42', X'2922C5B77CFFBF49B25EA7A0FA040F42', 2, NULL, NULL, 2, NULL, 0, 'For 100 pounds ground pork use:');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F8B1B74D962F3B439CD2FCBA9CB3EC95', X'2922C5B77CFFBF49B25EA7A0FA040F42', X'2922C5B77CFFBF49B25EA7A0FA040F42', 3, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'46CFC012F4E3FC499385352BB28C69F8', X'A34275EEDEB1404CB0345B58BD2785AA', X'A34275EEDEB1404CB0345B58BD2785AA', 0, NULL, NULL, 2, NULL, 0, 'Do not increase the proportion of onions, peppers, or mushrooms.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF4F809BF20010489A8B0DC754D62181', X'A34275EEDEB1404CB0345B58BD2785AA', X'A34275EEDEB1404CB0345B58BD2785AA', 1, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'695BB408F0AF60499589569E21E0CB60', X'6DAB283CCB60F3408BE621FC630BBED5', X'6DAB283CCB60F3408BE621FC630BBED5', 0, NULL, NULL, 2, NULL, 0, 'Soak meat for 1 hour in water containing 1 tablespoon of salt per quart, and then rinse. Use processing times for poultry, omitting the salt.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4012219719AA1F45BDA6C62AB85F7B78', X'6DAB283CCB60F3408BE621FC630BBED5', X'6DAB283CCB60F3408BE621FC630BBED5', 1, NULL, NULL, 2, NULL, 0, 'Hot Pack - Boil, steam, or bake meat until about two-thirds done. Add 1 tsp salt per quart, 1/2 tsp per pint, if desired. Fill hot jars with pieces and hot broth, leaving 1-1/4 inch headspace. Remove air bubbles.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C13B4FCEAD3854CADC83F1BA0B0AC7F', X'6DAB283CCB60F3408BE621FC630BBED5', X'6DAB283CCB60F3408BE621FC630BBED5', 2, NULL, NULL, 2, NULL, 0, 'Raw Pack - Add 1 tsp salt per quart, 1/2 tsp per pint if desired. Fill hot jars loosely with raw meat pieces, leaving 1-1/4 inch headspace. Do not add liquid. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7265BBA11B6DC741900C16AB3A16D562', X'6DAB283CCB60F3408BE621FC630BBED5', X'6DAB283CCB60F3408BE621FC630BBED5', 3, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A09A293E37A8DD4A9B7C9D5CD23626D8', X'6DAB283CCB60F3408BE621FC630BBED5', X'6DAB283CCB60F3408BE621FC630BBED5', 4, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'219D9577004A7445B6B1CE521B548085', X'D5C66C8FF930DF4589E3EE6FBFCC315C', X'D5C66C8FF930DF4589E3EE6FBFCC315C', 0, NULL, NULL, 2, NULL, 0, 'Choose quality chilled meat. Remove excess fat. Soak strong-flavored wild meats for 1 hour in brine water containing 1 tablespoon of salt per quart of water. Rinse. Remove large bones.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'877A5F0E9A20A54CB02B2CDD880DC144', X'D5C66C8FF930DF4589E3EE6FBFCC315C', X'D5C66C8FF930DF4589E3EE6FBFCC315C', 1, NULL, NULL, 2, NULL, 0, 'Bear, beef, lamb, pork, veal, venison.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DCB6A3115FC8F446AA12E89CCD34F3A8', X'D5C66C8FF930DF4589E3EE6FBFCC315C', X'D5C66C8FF930DF4589E3EE6FBFCC315C', 2, NULL, NULL, 2, NULL, 0, 'Hot Pack - Precook meat until rare by roasting, stewing, or browning in a small amount of fat. Add 1 teaspoon of salt per quart to the jar, 1/2 teaspoon to pints, if desired. Fill hot jars with pieces and add boiling broth, meat drippings, water, or tomato juice (especially with wild game), leaving 1 inch headspace. Remove air bubbles and wipe jar rims.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5B768A7A30E9F4FBDE43D882DB63364', X'D5C66C8FF930DF4589E3EE6FBFCC315C', X'D5C66C8FF930DF4589E3EE6FBFCC315C', 3, NULL, NULL, 2, NULL, 0, 'Raw Pack - Add 1 teaspoon of salt per quart to the jar, 1/2 teaspoon to pints, if desired. Pack raw meat in hot jars, leaving 1 inch headspace. Do not add liquid. Wipe rims of jars. Adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4FDDC627104E1499A5BFA9931F8133F', X'D5C66C8FF930DF4589E3EE6FBFCC315C', X'D5C66C8FF930DF4589E3EE6FBFCC315C', 4, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'366B9498BDD6A6459CDE5BE41607B3D6', X'D5C66C8FF930DF4589E3EE6FBFCC315C', X'D5C66C8FF930DF4589E3EE6FBFCC315C', 5, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36F8A1B3C8EF314888BF9DFA931918B2', X'BB6FB376BA51DA428E96BB722CFF0890', X'BB6FB376BA51DA428E96BB722CFF0890', 0, NULL, NULL, 2, NULL, 0, 'Both beef and pork stomachs are prepared in the same manner. Clean stomachs well, than soak for 12 hours in cold water. A few water changes are needed. Cur stomachs into two parts, place in water and bring to a boil. Cook for 15 minutes. Remove from water and check for a smell. If an odor is still detected, boil stomachs for additional 15 minutes and the odor will go away. Place stomachs on screens or tables to cool.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B15A73A54018C24E860AF79606026ED2', X'BB6FB376BA51DA428E96BB722CFF0890', X'BB6FB376BA51DA428E96BB722CFF0890', 1, NULL, NULL, 2, NULL, 0, 'Wash the skins and simmer at 194-203 F, 90-95 C for 60 minutes in about 120 ml (1/2 cup) of water. Spread on table to cool down.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E0826982E971B47A84849B3EDE5D4A2', X'BB6FB376BA51DA428E96BB722CFF0890', X'BB6FB376BA51DA428E96BB722CFF0890', 2, NULL, NULL, 2, NULL, 0, 'Place bones in pot, add 5 liters (5 quarts and one cup) of water and bring to a boil. Cook for 3 hours, add parsley, celery and carrots and cook one more hour (total 4 hours). Strain the stock.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9965A230D676C7489CDB516617FE7313', X'BB6FB376BA51DA428E96BB722CFF0890', X'BB6FB376BA51DA428E96BB722CFF0890', 3, NULL, NULL, 2, NULL, 0, 'Cut stomachs into strips 2 x 1/4` (50 x 6 mm). Grind skins through 1/8` (3 mm) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6EE14A503B246943A52A71A347525FA4', X'BB6FB376BA51DA428E96BB722CFF0890', X'BB6FB376BA51DA428E96BB722CFF0890', 4, NULL, NULL, 2, NULL, 0, 'Mix tripe (stomach strips) with ground skins adding salt, remaining spices and melted lard.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D1CC70255FE27409F6AD5D2A1DEBE6C', X'BB6FB376BA51DA428E96BB722CFF0890', X'BB6FB376BA51DA428E96BB722CFF0890', 5, NULL, NULL, 2, NULL, 0, 'Place 280 g (10 oz) tripe mixture in pint jar, and 140 g (5 oz) tripe in 1/2 pint jars. Add boiling hot beef stock leaving 1 inch headspace.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B17AA915FF4D7C4981EAA942552E3141', X'BB6FB376BA51DA428E96BB722CFF0890', X'BB6FB376BA51DA428E96BB722CFF0890', 6, NULL, NULL, 2, NULL, 0, 'Process at once at 250 F, 121 C: 1/2 Pint jars - 50 min Pint jars - 60 min');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25B9D7B95DA0A84BBA1652C3EA1F68B9', X'FE05F3BB1D611A4CB214073163D8413C', X'FE05F3BB1D611A4CB214073163D8413C', 0, NULL, NULL, 2, NULL, 0, 'Both beef and pork stomachs are prepared in the same manner. Clean stomachs well, than soak for 12 hours in cold water. A few water changes are needed. Cur stomachs into two parts, place in water and bring to a boil. Cook for 15 minutes. Remove from water and check for a smell. If an odor is still detected, boil stomachs for additional 15 minutes and the odor will go away. Place stomachs on screens or tables to cool.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'90EFE38632B3F040817CE34B18CBBB4D', X'FE05F3BB1D611A4CB214073163D8413C', X'FE05F3BB1D611A4CB214073163D8413C', 1, NULL, NULL, 2, NULL, 0, 'Wash the skins and simmer at 194-203 F, 90-95 C for 60 minutes in about 120 ml (1/2 cup) of water. Spread on table to cool down. Grind through 1/8` (3 mm) plate or smaller.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'56985BC2D87D964A96DF9E16C5ABA39C', X'FE05F3BB1D611A4CB214073163D8413C', X'FE05F3BB1D611A4CB214073163D8413C', 2, NULL, NULL, 2, NULL, 0, 'Place bones in pot, add 5 liters (5 quarts and one cup) of water and bring to a boil. Cook for 3 hours, add parsley, celery and carrots and cook one more hour (total 4 hours). Strain the stock.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24D51B963384834FB97D3E79D7D509F5', X'FE05F3BB1D611A4CB214073163D8413C', X'FE05F3BB1D611A4CB214073163D8413C', 3, NULL, NULL, 2, NULL, 0, 'Cut stomachs into strips 2 x 1/4` (50 x 6 mm). Grind skins through 1/8` (3 mm) plate. Mix the tripe and ground skins together adding salt, remaining spices and melted lard.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'101F2650372A314E8AF943B9E1F12A09', X'FE05F3BB1D611A4CB214073163D8413C', X'FE05F3BB1D611A4CB214073163D8413C', 4, NULL, NULL, 2, NULL, 0, 'Place 280 g (10 oz) tripe mixture in pint jar, and 140 g (5 oz) tripe in 1/2 pint jars. Add boiling hot beef stock leaving 1 inch headspace.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BBF5039342FC2B45BA6CC7495723C93C', X'FE05F3BB1D611A4CB214073163D8413C', X'FE05F3BB1D611A4CB214073163D8413C', 5, NULL, NULL, 2, NULL, 0, 'Process at once at 250 F, 121 C: 1/2 Pint jars - 40 min Pint jars - 60 min');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33E7B6D606DDC54BAA87C22BA0056812', X'A6F684BEFFBEC849A6131DFC280AEEC7', X'A6F684BEFFBEC849A6131DFC280AEEC7', 0, NULL, NULL, 2, NULL, 0, 'Whole or minced.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3721A97E18E282478145A3BBC43DB440', X'A6F684BEFFBEC849A6131DFC280AEEC7', X'A6F684BEFFBEC849A6131DFC280AEEC7', 1, NULL, NULL, 2, NULL, 0, 'Hot Pack - keep clams live on ice until ready to can. Scrub shells thoroughly and rinse, steam 5 minutes, and open. Remove clam meat. Collect and save clam juice. Wash clam meat in water containing 1 teaspoon of salt per quart. Rinse and cover clam meat with boiling water containing 2 tablespoons of lemon juice or 1/2 teaspoon of citric acid per gallon. Boil 2 minutes and drain. To make minced clams, grind clams with a meat grinder or food processor. Fill hot jars loosely with pieces and add hot clam juice and boiling water if needed, leaving 1 inch headspace. Remove air bubbles and adjust headspace if needed. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C71B679656D1E429B9C96486921B4CB', X'A6F684BEFFBEC849A6131DFC280AEEC7', X'A6F684BEFFBEC849A6131DFC280AEEC7', 2, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'955E4F3949917F42B9595929DEB2472A', X'B24D1FEA5CE3204090F56D3CF08CDDAB', X'B24D1FEA5CE3204090F56D3CF08CDDAB', 0, NULL, NULL, 2, NULL, 0, 'Use fillets and all fish meat that can be scraped off the bones.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F2E40C2FB285840BEF4181D943472E4', X'B24D1FEA5CE3204090F56D3CF08CDDAB', X'B24D1FEA5CE3204090F56D3CF08CDDAB', 1, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21F0564AF1314D4597616E6E6782AB13', X'0151A1C66D28C440BBEAE542791964D2', X'0151A1C66D28C440BBEAE542791964D2', 0, NULL, NULL, 2, NULL, 0, 'If the fish is frozen, thaw it in the refrigerator before canning. Rinse the fish in cold water. You can add vinegar to the water (2 tablespoons per quart) to help remove slime. Remove head, tail, fins, and scales; it is not necessary to remove the skin. You can leave the bones in most fish because the bones become very soft and are a good source of calcium. For halibut, remove the head, tail, fins, skin, and the bones. Wash and remove all blood. Refrigerate all fish until you are ready to pack in jars. Split fish lengthwise, if desired. Cut cleaned fish into 3-1/2-inch lengths. If the skin has been left on the fish, pack the fish skin out, for a nicer appearance or skin in, for easier jar cleaning. Fill hot pint jars, leaving 1-inch headspace. Add 1 teaspoon of salt per pint, if desired. Do not add liquids. Carefully clean the jar rims with a clean, damp paper towel; wipe with a dry paper towel to remove any fish oil. Adjust lids and process. Fish in half-pint or 12-ounce jars would be processed for the same amount of time as pint jars.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'559BEE90A6662247B25D6D904370C171', X'0151A1C66D28C440BBEAE542791964D2', X'0151A1C66D28C440BBEAE542791964D2', 1, NULL, NULL, 2, NULL, 0, 'Blue, mackerel, salmon, steelhead, trout, and other fatty fish except tuna.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'605455B24AD62D4294F06C0B8DDC47F6', X'0151A1C66D28C440BBEAE542791964D2', X'0151A1C66D28C440BBEAE542791964D2', 2, NULL, NULL, 2, NULL, 0, 'CAUTION Bleed and eviscerate fish immediately after catching, never more than 2 hours after they are caught. Keep cleaned fish on ice until ready to can.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D3FFF3560FBDD4B8AAA0B7B6C3DCD4C', X'0151A1C66D28C440BBEAE542791964D2', X'0151A1C66D28C440BBEAE542791964D2', 3, NULL, NULL, 2, NULL, 0, 'NOTE Glass-like crystals of struvite, or magnesium ammonium phosphate, sometime form in canned salmon. There is no way for the home canner to prevent these crystals from forming, but they usually dissolve when heated and are safe to eat.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74273CDECD8D84429AEC95664A23ED63', X'0151A1C66D28C440BBEAE542791964D2', X'0151A1C66D28C440BBEAE542791964D2', 4, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C79646FEE78CE443BA07B59A7564A132', X'8C93AA55501B1440ADE71FD0DA9BA9ED', X'8C93AA55501B1440ADE71FD0DA9BA9ED', 0, NULL, NULL, 2, NULL, 0, 'If the fish is frozen, thaw it in the refrigerator before canning. Rinse the fish in cold water. You can add vinegar to the water (2 tablespoons per quart) to help remove slime. Remove head, tail, fins, and scales; it is not necessary to remove the skin. You can leave the bones in most fish because the bones become very soft and are a good source of calcium. For halibut, remove the head, tail, fins, skin, and the bones. Wash and remove all blood. Refrigerate all fish until you are ready to pack in jars. Cut the fish into jar-length filets or chunks of any size. The one-quart straight-sided mason type jar is recommended. If the skin has been left on the fish, pack the fish skin out, for a nicer appearance or skin in, for easier jar cleaning. Pack solidly into hot quart jars, leaving 1 inch headspace. If desired, run a plastic knife around the inside of the jar to align the product; this allows firm packing of fish. For most fish, no liquid, salt, or spices need to be added, although seasonings or salt may be added for flavor (1 to 2 teaspoons salt per quart, or amount desired). For halibut, add up to 4 tablespoons of vegetable or olive oil per quart jar if you wish. The canned product will seem moister. However, the oil will increase the caloric value of the fish. Carefully clean the jar rims with a clean, damp paper towel; wipe with a dry paper towel to remove any fish oil. Adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38B13ACF6EA99543ADE846457E13A61C', X'8C93AA55501B1440ADE71FD0DA9BA9ED', X'8C93AA55501B1440ADE71FD0DA9BA9ED', 1, NULL, NULL, 2, NULL, 0, 'The directions for operating the pressure canner during processing of quart jars are different from those for processing pint jars, so please read the following carefully. It is critical to product safety that the processing directions are followed exactly. When you are ready to process your jars of fish, add 3 quarts of water to the pressure canner. Put the rack in the bottom of canner and place closed jars on the rack. Fasten the canner cover securely, but do not close the lid vent. Heat the canner on high for 20 minutes. If steam comes through the open vent in a steady stream at the end of 20 minutes, allow it to escape for an additional 10 minutes. If steam does not come through the open vent in a steady stream at the end of 20 minutes, keep heating the canner until it does. Then allow the steam to escape for an additional 10 minutes to vent the canner. This step removes air from inside the canner so the temperature is the same throughout the canner. The total time it takes to heat and vent the canner should never be less than 30 minutes. The total time may be more than 30 minutes if you have tightly packed jars, cold fish, or larger sized canners. For safety''s sake, you must have a complete, uninterrupted 160 minutes (2 hours and 40 minutes) at a minimum pressure required for your altitude. Write down the time at the beginning of the process and the time when the process will be finished.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB74688CE5F9A441A9059D193F13D386', X'8C93AA55501B1440ADE71FD0DA9BA9ED', X'8C93AA55501B1440ADE71FD0DA9BA9ED', 2, NULL, NULL, 2, NULL, 0, 'Blue, mackerel, salmon, steelhead, trout, and other fatty fish except tuna.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'924F6AB3898759479B3A8D7AA80588B8', X'8C93AA55501B1440ADE71FD0DA9BA9ED', X'8C93AA55501B1440ADE71FD0DA9BA9ED', 3, NULL, NULL, 2, NULL, 0, 'NOTE Glass-like crystals of struvite, or magnesium ammonium phosphate, sometime form in canned salmon. There is no way for the home canner to prevent these crystals from forming, but they usually dissolve when heated and are safe to eat.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14DB564D39E3C34796F5B4EDC398081D', X'8C93AA55501B1440ADE71FD0DA9BA9ED', X'8C93AA55501B1440ADE71FD0DA9BA9ED', 4, NULL, NULL, 2, NULL, 0, 'CAUTION Bleed and eviscerate fish immediately after catching, never more than 2 hours after they are caught. Keep cleaned fish on ice until ready to can.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'201152A14DEA7C4BA0D2CE9CE9FEB674', X'8C93AA55501B1440ADE71FD0DA9BA9ED', X'8C93AA55501B1440ADE71FD0DA9BA9ED', 5, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D03B6D73058044498AE41671CBBBD2C3', X'812F5357845D6B4BBE0CCFA06D4424B9', X'812F5357845D6B4BBE0CCFA06D4424B9', 0, NULL, NULL, 2, NULL, 0, 'Mullet, mackerel or other fish.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7739A19DB3B6414CB05711EC8F6AC8BD', X'812F5357845D6B4BBE0CCFA06D4424B9', X'812F5357845D6B4BBE0CCFA06D4424B9', 1, NULL, NULL, 2, NULL, 0, 'Pack warm smoked fillets solidly into the jars. Fillets can be rolled before packing. Leave one inch of headspace between the fish and the top of the jar. Place 2-3 inches of warm water in the canner, and the rack and place the jars. Process at once.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B53658F5EC3F6A4CAE3E75869F3E9EC4', X'812F5357845D6B4BBE0CCFA06D4424B9', X'812F5357845D6B4BBE0CCFA06D4424B9', 2, NULL, NULL, 2, NULL, 0, 'Exhaust. Place a suitable pot or pan on the stove with some hot water in it. Have more hot water nearby. Place warm smoked fillets solidly into cans. Leave one inch headspace. Place the open cans in the boiling water in a single layer. The water should come up halfway up the outside of the cans. Do not cover cans with the lids. Heat the uncovered pot until the fish reach a temperature of 170 F, 77 C. Covering the pot will result in moisture dripping into the cans. Remove cans from the pot and seal. Process at once.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4ED5506D2F817140A1EBC5CF48F2CDB2', X'812F5357845D6B4BBE0CCFA06D4424B9', X'812F5357845D6B4BBE0CCFA06D4424B9', 3, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C68CC0A182572B44B8D0198444ACC6EC', X'812F5357845D6B4BBE0CCFA06D4424B9', X'812F5357845D6B4BBE0CCFA06D4424B9', 4, NULL, NULL, 2, NULL, 0, 'NOTE no additional fat or oil is needed for canning oily fish like mullet, mackerel, salmon or herring. When canning lean fish, up to 2 tablespoons of olive/vegetable oil may be added to 1/2 pint jar or 1/2 lb can.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A63883F7BE3FD42A64F55327A15B6D2', X'5C9043D4DD050042B3B01364C915FA86', X'5C9043D4DD050042B3B01364C915FA86', 0, NULL, NULL, 2, NULL, 0, 'Cook all ingredients until reduced to about half the original volume. Put through a fine strainer. This sauce may be put over fish in the jars before processing or served with fresh cooked or plain canned fish.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39345B1FB7E98E47930751DCACD52CDF', X'5C9043D4DD050042B3B01364C915FA86', X'5C9043D4DD050042B3B01364C915FA86', 1, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41EDA67CBC7FE442B1AAFE68428143EA', X'5C9043D4DD050042B3B01364C915FA86', X'5C9043D4DD050042B3B01364C915FA86', 2, NULL, NULL, 2, NULL, 0, 'Fish for canning should be strictly fresh. It is well to stick fish with a knife to drain out the blood as soon as they are caught. The viscera should also be removed as soon as possible.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'566110579D9E7C4791C4515C152B5E42', X'5C9043D4DD050042B3B01364C915FA86', X'5C9043D4DD050042B3B01364C915FA86', 3, NULL, NULL, 2, NULL, 0, 'Scale or wash carefully. Scales are easier to remove if fish are dipped into boiling water and then into cold water. If skins are tough, skin the fish.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8FA3886A7D44AF49AC5034E9B7E648C0', X'5C9043D4DD050042B3B01364C915FA86', X'5C9043D4DD050042B3B01364C915FA86', 4, NULL, NULL, 2, NULL, 0, 'In order to draw out the blood before canning, place fish in a brine made by using 1 tablespoon salt to each quart of water. Let fish soak 10 minutes to one hour, according to thickness. Small trout need only 10 minutes soaking. This soaking is not absolutely essential but makes for a better looking product. It tends to make fish firmer.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7216CF9FEDAA3647BADA913509D5E2BB', X'5C9043D4DD050042B3B01364C915FA86', X'5C9043D4DD050042B3B01364C915FA86', 5, NULL, NULL, 2, NULL, 0, 'Remove from brine, wash and drain on clean towels.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C35F82DEE7428F46AC0F59582A044A07', X'5C9043D4DD050042B3B01364C915FA86', X'5C9043D4DD050042B3B01364C915FA86', 6, NULL, NULL, 2, NULL, 0, 'Leave whole or cut into convenient, uniform pieces.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8EBB8B6C92E2EE4989BBBBCDA9326033', X'5C9043D4DD050042B3B01364C915FA86', X'5C9043D4DD050042B3B01364C915FA86', 7, NULL, NULL, 2, NULL, 0, 'Season fish before frying if they were not soaked in brine.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BAAAD5FFD425AA439EF290CE07B26A23', X'5C9043D4DD050042B3B01364C915FA86', X'5C9043D4DD050042B3B01364C915FA86', 8, NULL, NULL, 2, NULL, 0, 'Fry in deep fat till nicely browned. Drain and place on brown paper to remove excess fat.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'103DA17A0AB3D345982B15A4D311DDFD', X'5C9043D4DD050042B3B01364C915FA86', X'5C9043D4DD050042B3B01364C915FA86', 9, NULL, NULL, 2, NULL, 0, 'Pack into clean hot jars. Add about 2 tablespoon hot liquid. Add hot tomato sauce if desired and in that case use glass jars or R-enameled cans.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3E648D3BC6BDC4E94C7782EA2AB9C8A', X'5C9043D4DD050042B3B01364C915FA86', X'5C9043D4DD050042B3B01364C915FA86', 10, NULL, NULL, 2, NULL, 0, 'Seal and process at once.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B61070412F5D514CB53E9B8BE765C4D4', X'9F5B0C133CD5F04F866B041BA16860D9', X'9F5B0C133CD5F04F866B041BA16860D9', 0, NULL, NULL, 2, NULL, 0, 'An excess quantity of fish caught in spring and summer may be canned for use in other seasons. Fish for canning must be strictly fresh. Fish deteriorates rapidly in quality and flavor, so quick work is essential for a high grade product.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AEA2D5614CC7FB46B59F77885E513634', X'9F5B0C133CD5F04F866B041BA16860D9', X'9F5B0C133CD5F04F866B041BA16860D9', 1, NULL, NULL, 2, NULL, 0, 'Procedure:');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FBBB29E145ECFC4FBAFE36ABB2DFE974', X'9F5B0C133CD5F04F866B041BA16860D9', X'9F5B0C133CD5F04F866B041BA16860D9', 2, NULL, NULL, 2, NULL, 0, 'NOTE Fish canned in this manner will resemble canned salmon in texture.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'32BD01F16CA3704685B00FF1A37DBEEB', X'9F5B0C133CD5F04F866B041BA16860D9', X'9F5B0C133CD5F04F866B041BA16860D9', 3, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55A24DDA60157E409FCF0E05CADC13EA', X'9F5B0C133CD5F04F866B041BA16860D9', X'9F5B0C133CD5F04F866B041BA16860D9', 4, NULL, NULL, 2, NULL, 0, 'Fish for canning should be strictly fresh. It is well to stick fish with a knife to drain out the blood as soon as they are caught. The viscera should also be removed as soon as possible.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96CB7A9B7B55CD42BEC1672EC5008486', X'9F5B0C133CD5F04F866B041BA16860D9', X'9F5B0C133CD5F04F866B041BA16860D9', 5, NULL, NULL, 2, NULL, 0, 'Scale or wash carefully. Scales are easier to remove if fish are dipped into boiling water and then into cold water. If skins are tough, skin the fish.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5614B075388F7A4484E4C6FC62481982', X'9F5B0C133CD5F04F866B041BA16860D9', X'9F5B0C133CD5F04F866B041BA16860D9', 6, NULL, NULL, 2, NULL, 0, 'In order to draw out the blood before canning, place fish in a brine made by using 1 tablespoon salt to each quart of water. Let fish soak 10 minutes to one hour, according to thickness. Small trout need only 10 minutes soaking. This soaking is not absolutely essential but makes for a better looking product. It tends to make fish firmer.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'58E2E1C9889B7C43A8A9BB2CC1DBF871', X'9F5B0C133CD5F04F866B041BA16860D9', X'9F5B0C133CD5F04F866B041BA16860D9', 7, NULL, NULL, 2, NULL, 0, 'Pack fish into clean hot jars or tin cans, packing in `up and down` or `circular` fashion to make a good looking jar. Large fish will need to be cut into convenient, uniform pieces.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'481FD1F9DB930D46A36CAEB1FE6613DC', X'9F5B0C133CD5F04F866B041BA16860D9', X'9F5B0C133CD5F04F866B041BA16860D9', 8, NULL, NULL, 2, NULL, 0, 'Add 1 teaspoon salt per pint jar if fish have not been previously soaked in brine. Do not add water. Hot tomato sauce may be poured over the fish in the jars or enameled cans.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB10AAB61CEB034EAE838DE112AA63F9', X'9F5B0C133CD5F04F866B041BA16860D9', X'9F5B0C133CD5F04F866B041BA16860D9', 9, NULL, NULL, 2, NULL, 0, 'Exhaust tin cans till steaming hot. Seal and process at once.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ABA517B3A242304FAE0DCC19944DB4A4', X'1A5D5FE3920C6540A2B3F685BDDB3E7C', X'1A5D5FE3920C6540A2B3F685BDDB3E7C', 0, NULL, NULL, 2, NULL, 0, 'If smoked fish has been frozen, thaw in the refrigerator until no ice crystals remain before canning. If not done prior to smoking, cut fish into pieces that will fit vertically into pint canning jars, leaving 1 inch headspace. Pack smoked fish vertically into hot jars, leaving 1 inch headspace between the pieces and the top rim of the jar. The fish may be packed either loosely or tightly.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB1004B142AEE547B8C079CE61C96FC7', X'1A5D5FE3920C6540A2B3F685BDDB3E7C', X'1A5D5FE3920C6540A2B3F685BDDB3E7C', 1, NULL, NULL, 2, NULL, 0, 'Do not add liquid to the jars. Clean jar rims with a clean, damp paper towel. Adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7EC0E2F80413FB4FAADA6974FA1217A3', X'1A5D5FE3920C6540A2B3F685BDDB3E7C', X'1A5D5FE3920C6540A2B3F685BDDB3E7C', 2, NULL, NULL, 2, NULL, 0, 'The directions for filling the pressure canner for processing smoked fish are different than those for other pressure canning, so please read the following carefully. It is critical to product safety that the processing directions are followed exactly. When you are ready to process your jars of smoked fish, measure 4 quarts (16 cups) of cool tap water and pour into the pressure canner. (Note: The water level probably will reach the screw bands of pint jars.) Do not decrease the amount of water or heat the water before processing begins. Place prepared, closed jars on the rack in the bottom of the canner, and proceed as with usual pressure canning instructions.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F50C045DA7B42E4B8D34B5CA2EF0C751', X'1A5D5FE3920C6540A2B3F685BDDB3E7C', X'1A5D5FE3920C6540A2B3F685BDDB3E7C', 3, NULL, NULL, 2, NULL, 0, 'Salmon, rockfish and flatfish (sole, cod, flounder) and other fish.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD01FEFDFA85294B889CB5624F97419E', X'1A5D5FE3920C6540A2B3F685BDDB3E7C', X'1A5D5FE3920C6540A2B3F685BDDB3E7C', 4, NULL, NULL, 2, NULL, 0, 'CAUTION Safe processing times for other smoked seafoods have not been determined. Those products should be frozen. Smoking of fish should be done by tested methods. Lightly smoked fish is recommended for canning because the smoked flavor will become stronger and the flesh drier after processing. However, because it has not yet been cooked, do not taste lightly smoked fish before canning. Follow these recommended canning instructions carefully. Use a 16 to 22 quart pressure canner for this procedure; do not use smaller pressure saucepans. Safe processing times have not been determined. Do not use jars larger than one pint. Half-pints could be safely processed for the same length of time as pints, but the quality of the product may be less acceptable.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E762C01CEF12A24CBE56C9CFD9023159', X'1A5D5FE3920C6540A2B3F685BDDB3E7C', X'1A5D5FE3920C6540A2B3F685BDDB3E7C', 5, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'86ABBD4F50064E4DA23928311027EF96', X'1A5D5FE3920C6540A2B3F685BDDB3E7C', X'1A5D5FE3920C6540A2B3F685BDDB3E7C', 6, NULL, NULL, 2, NULL, 0, 'All fish can be smoked but the oily fish such as salmon, trout, herring, mackerel, eel, blue fish, and mullet taste the best.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D2D5728B4B4CEA46BA8426FD7E2B035A', X'A5C7C2D789A7544FA81CF27099116038', X'A5C7C2D789A7544FA81CF27099116038', 0, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E2E9215E579154AB3D0AF7C223969EC', X'DC97CF385E6D964A9717A00E931B999F', X'DC97CF385E6D964A9717A00E931B999F', 0, NULL, NULL, 2, NULL, 0, 'Keep live crabs on ice until ready to can. Wash crabs thoroughly, using several changes of cold water. Simmer crabs 20 minutes in water containing cup of lemon juice and 2 tablespoons of salt (or up to 1 cup of salt, if desired) per gallon. Cool in cold water, drain, remove back shell, then remove meat from body and claws. Soak meat 2 minutes in cold water containing 2 cups of lemon juice or 4 cups of white vinegar, and 2 tablespoons of salt (or up to 1 cup of salt, if desired) per gallon. Drain and squeeze crab meat to remove excess moisture. Fill hot half-pint jars with 6 ounces of crab meat and pint jars with 12 ounces, leaving 1 inch headspace. Add O teaspoon of citric acid or 2 tablespoons of lemon juice to each half-pint jar, or 1 teaspoon of citric acid or 4 tablespoons of lemon juice per pint jar. Cover with fresh boiling water, leaving 1 inch headspace. Remove air bubbles and adjust headspace if needed. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15EE413B08DABD459B0A74BCE3AAE527', X'DC97CF385E6D964A9717A00E931B999F', X'DC97CF385E6D964A9717A00E931B999F', 1, NULL, NULL, 2, NULL, 0, 'It is recommended that blue crab meat be frozen instead of canned for best quality. Crab meat canned according to the following procedure may have a distinctly acidic flavor and freezing is the preferred method of preservation at this time.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1FF2F59D8015D4888F23EBFDEC680C8', X'DC97CF385E6D964A9717A00E931B999F', X'DC97CF385E6D964A9717A00E931B999F', 2, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E593305E629C640B3E5E8A295623323', X'90D6582C316F0C4D89BE001130B9B92C', X'90D6582C316F0C4D89BE001130B9B92C', 0, NULL, NULL, 2, NULL, 0, 'Keep live oysters on ice until ready to can. Wash shells. Heat 5 to 7 minutes in preheated oven at 400 F (204 C). Cool briefly in ice water. Drain, open shell, and remove meat. Wash meat in water containing O cup salt per gallon. Drain. Add O teaspoon salt to each pint, if desired. Fill hot half-pint or pint jars with drained oysters and cover with fresh boiling water, leaving 1 inch headspace. Remove air bubbles and adjust headspace if needed. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F386D5BD62C8D4429A1240490F8051C0', X'90D6582C316F0C4D89BE001130B9B92C', X'90D6582C316F0C4D89BE001130B9B92C', 1, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8460FD6A28D92142A6DB95BDDEB4F732', X'BD5F31A6E49CF446918BC72C2259D0FD', X'BD5F31A6E49CF446918BC72C2259D0FD', 0, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA580C207F832741878187D4302A75B2', X'30F9701178CCD4418E20BBDA997913AF', X'30F9701178CCD4418E20BBDA997913AF', 0, NULL, NULL, 2, NULL, 0, 'Keep tuna on ice until ready to can. Remove viscera and wash fish well in cold water. Allow blood to drain from stomach cavity. Place fish belly down on a rack or metal tray in the bottom of a large baking pan. Cut tuna in half crosswise, if necessary. Precook fish by baking at 250 F (121 C) for 2-1/2 to 4 hours (depending on size) or at 350 F (177 C) for 1 hour. The fish may also be cooked in a steamer for 2 to 4 hours. If a thermometer is used, cook to a 165 to 175 F (74 - 80 C) internal temperature. Refrigerate cooked fish overnight to firm the meat. Peel off the skin with a knife, removing blood vessels and any discolored flesh. Cut meat away from bones; cut out and discard all bones, fin bases, and dark flesh. Quarter. Cut quarters crosswise into lengths suitable for half-pint or pint jars. Fill into hot jars, pressing down gently to make a solid pack. Tuna may be packed in water or oil, whichever is preferred. Add water or oil to jars, leaving 1 inch headspace. Remove air bubbles and adjust headspace if needed. Add 1/2 teaspoon of salt per half-pint or 1 teaspoon of salt per pint, if desired. Carefully clean the jar rims with a clean, damp paper towel; wipe with a dry paper towel to remove any fish oil. Adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7DE732F2037E44F92AB5ECE3D71ECC2', X'30F9701178CCD4418E20BBDA997913AF', X'30F9701178CCD4418E20BBDA997913AF', 1, NULL, NULL, 2, NULL, 0, 'Tuna may be canned either precooked or raw. Precooking removes most of the strong-flavored oils. The strong flavor of dark tuna flesh affects the delicate flavor of white flesh. Many people prefer not to can dark flesh. It may be used as pet food.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E31D85808E14214DAC22EDDEF05986CC', X'30F9701178CCD4418E20BBDA997913AF', X'30F9701178CCD4418E20BBDA997913AF', 2, NULL, NULL, 2, NULL, 0, 'NOTE glass-like crystals of struvite, or magnesium ammonium phosphate, sometime form in canned tuna. There is no way for the home canner to prevent these crystals from forming, but they usually dissolve when heated and are safe to eat.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C3309D10E53114A85FBF15DB39D0EA4', X'30F9701178CCD4418E20BBDA997913AF', X'30F9701178CCD4418E20BBDA997913AF', 3, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustment.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'94EA4E5F07FB3F419D96AA566F7D6DDD', X'3D7AAA01237E72449CF05434657E6318', X'3D7AAA01237E72449CF05434657E6318', 0, NULL, NULL, 2, NULL, 0, 'Wash asparagus and trim off tough scales. Break off tough stems and wash again. Cut into 1-inch pieces or can whole.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4159CD0DFFD10D41923CE554FB2E0F5C', X'3D7AAA01237E72449CF05434657E6318', X'3D7AAA01237E72449CF05434657E6318', 1, NULL, NULL, 2, NULL, 0, 'Cover asparagus with boiling water. Boil 2 or 3 minutes. Loosely fill hot jars with hot asparagus, leaving 1-inch headspace.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7FDA3B9CE93A843AEB0635FE7025C94', X'3D7AAA01237E72449CF05434657E6318', X'3D7AAA01237E72449CF05434657E6318', 2, NULL, NULL, 2, NULL, 0, 'Fill hot jars with raw asparagus, packing as tightly as possible without crushing, leaving 1-inch headspace.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'513C1530A69EB64DBBF268E5900D4F6F', X'3D7AAA01237E72449CF05434657E6318', X'3D7AAA01237E72449CF05434657E6318', 3, NULL, NULL, 2, NULL, 0, 'Use tender, tight-tipped spears, 4 to 6 inches long.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7061AAAF941A942A9CFA1903C335F94', X'3D7AAA01237E72449CF05434657E6318', X'3D7AAA01237E72449CF05434657E6318', 4, NULL, NULL, 2, NULL, 0, 'Add 1 teaspoon of salt per quart to the jars, if desired. Add boiling water, leaving 1-inch headspace. Remove air bubbles and adjust headspace if needed. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7F62933157784448C0A94D49379BF87', X'3D7AAA01237E72449CF05434657E6318', X'3D7AAA01237E72449CF05434657E6318', 5, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6DE67CFF6567674D9202E97CB103BB9E', X'D60B49B719D24647B3288AB9DFF0E029', X'D60B49B719D24647B3288AB9DFF0E029', 0, NULL, NULL, 2, NULL, 0, 'Procedure: Soak and boil beans and prepare molasses sauce.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CDB5971D3ECC1945BAD4812F31DDE175', X'D60B49B719D24647B3288AB9DFF0E029', X'D60B49B719D24647B3288AB9DFF0E029', 1, NULL, NULL, 2, NULL, 0, 'Molasses Sauce - Mix 4 cups water or cooking liquid from beans, 3 tablespoons dark molasses, 1 tablespoon vinegar, 2 teaspoons salt, and 3/4 teaspoon powered dry mustard. Heat to a boiling.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D052EF3A3FAB940BB4F2B6558AE3991', X'D60B49B719D24647B3288AB9DFF0E029', X'D60B49B719D24647B3288AB9DFF0E029', 2, NULL, NULL, 2, NULL, 0, 'Place seven 3/4-inch pieces of pork, ham, or bacon in an earthenware crock, a large casserole, or a pan. Add beans and enough molasses sauce to cover beans. Cover and bake 4 to 5 hours at 350 F, 177 C. Add water as needed-about every hour. Fill hot jars, leaving 1-inch headspace. Remove air bubbles, adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'580EB00209E764438F3304E1CDFF248F', X'D60B49B719D24647B3288AB9DFF0E029', X'D60B49B719D24647B3288AB9DFF0E029', 3, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B1197E192CADE41939F0A81D33E444E', X'D60B49B719D24647B3288AB9DFF0E029', X'D60B49B719D24647B3288AB9DFF0E029', 4, NULL, NULL, 2, NULL, 0, 'NOTE the thermal process for beans in sauce depends in part upon the sauce formulation. Changing the sauce formula will affect the processing time.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5BB9B902B85F0040892F71BFF7636862', X'22EAFCDA4DF36445BA258F02CD4AEEFC', X'22EAFCDA4DF36445BA258F02CD4AEEFC', 0, NULL, NULL, 2, NULL, 0, 'Place dried beans in a large pot and cover with water. Soak 12 to 18 hours in a cool place. Drain water. To quickly hydrate beans, you may cover sorted and washed beans with boiling water in a saucepan. Boil 2 minutes, remove from heat, soak 1 hour and drain. Cover beans soaked by either method with fresh water and boil 30 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE2F23BECA976F459C6B3B8324F6C649', X'22EAFCDA4DF36445BA258F02CD4AEEFC', X'22EAFCDA4DF36445BA258F02CD4AEEFC', 1, NULL, NULL, 2, NULL, 0, 'All varieties');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4D13F90500F1A428D76695E634EDEA1', X'22EAFCDA4DF36445BA258F02CD4AEEFC', X'22EAFCDA4DF36445BA258F02CD4AEEFC', 2, NULL, NULL, 2, NULL, 0, 'Select mature, dry seeds. Sort out and discard discolored seeds.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7978FB21BDFAA42A273D0D39F346BB9', X'22EAFCDA4DF36445BA258F02CD4AEEFC', X'22EAFCDA4DF36445BA258F02CD4AEEFC', 3, NULL, NULL, 2, NULL, 0, 'Add 1/2 teaspoon of salt per pint or 1 teaspoon per quart to the jar, if desired. Fill hot jars with beans and cooking water, leaving 1-inch headspace. Remove air bubbles, adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29734BCE1C7470428BABDBE3F82A0C2B', X'22EAFCDA4DF36445BA258F02CD4AEEFC', X'22EAFCDA4DF36445BA258F02CD4AEEFC', 4, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0928650A4A4304B93063EF4280F9088', X'6706146248185B45BA932FD9D2CEA66D', X'6706146248185B45BA932FD9D2CEA66D', 0, NULL, NULL, 2, NULL, 0, 'Sort and wash dry beans. Add 3 cups of water for each cup of dried beans or peas. Boil 2 minutes, remove from heat and soak 1 hour and drain. Heat to boiling in fresh water, and save liquid for making sauce. Make your choice of the following sauces:');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B63E8E928638C4C8D5EF9598F89274A', X'6706146248185B45BA932FD9D2CEA66D', X'6706146248185B45BA932FD9D2CEA66D', 1, NULL, NULL, 2, NULL, 0, 'Select mature, dry seeds. Sort out and discard discolored seeds.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4438B8ED739E0C4DA6D8DB5A62E84B9E', X'6706146248185B45BA932FD9D2CEA66D', X'6706146248185B45BA932FD9D2CEA66D', 2, NULL, NULL, 2, NULL, 0, 'Tomato Sauce 1 - Mix 1 quart tomato juice, 3 tablespoons sugar, 2 teaspoons salt, 1 tablespoon chopped onion, and 1/4 teaspoon each of ground cloves, allspice, mace, and cayenne pepper. Heat to boiling.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE31E0CB21DC98419EEC123AAB6B73BC', X'6706146248185B45BA932FD9D2CEA66D', X'6706146248185B45BA932FD9D2CEA66D', 3, NULL, NULL, 2, NULL, 0, 'Tomato Sauce 2 - Mix 1 cup tomato ketchup with 3 cups of cooking liquid from beans. Heat to boiling.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2ED6EFB939102944BCB0C76A1917EFF1', X'6706146248185B45BA932FD9D2CEA66D', X'6706146248185B45BA932FD9D2CEA66D', 4, NULL, NULL, 2, NULL, 0, 'Molasses Sauce - Mix 4 cups water or cooking liquid from beans, 3 tablespoons dark molasses, 1 tablespoon vinegar, 2 teaspoons salt, and 3/4 teaspoon powered dry mustard. Heat to boiling.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EAAC7F0EEC624141B2835811FB53DE24', X'6706146248185B45BA932FD9D2CEA66D', X'6706146248185B45BA932FD9D2CEA66D', 5, NULL, NULL, 2, NULL, 0, 'Fill hot jars three-fourths full with hot beans. Add one 3/4-inch cube of pork, ham, or bacon to each jar, if desired. Fill jars with heated sauce, leaving 1-inch headspace. Remove air bubbles, adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D68BCB0337B5A40A948C5F8FE2A7ABD', X'6706146248185B45BA932FD9D2CEA66D', X'6706146248185B45BA932FD9D2CEA66D', 6, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D6CFD013453314694C982CDDD1EF198', X'6706146248185B45BA932FD9D2CEA66D', X'6706146248185B45BA932FD9D2CEA66D', 7, NULL, NULL, 2, NULL, 0, 'NOTE the thermal process for beans in sauce depends in part upon the sauce formulation. Changing the sauce formula will affect the processing time. Do not add more meat or bacon.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'074CA51264783641A5C0A2795CB3DC91', X'ADE6551E49D2B6449F50B0A05FAAA2BB', X'ADE6551E49D2B6449F50B0A05FAAA2BB', 0, NULL, NULL, 2, NULL, 0, 'Shell beans and wash thoroughly.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6D76916AF2DB241BB5396F99A9B7D3F', X'ADE6551E49D2B6449F50B0A05FAAA2BB', X'ADE6551E49D2B6449F50B0A05FAAA2BB', 1, NULL, NULL, 2, NULL, 0, 'Cover beans with boiling water and heat to boil. Fill hot jars loosely, leaving 1-inch headspace. Add 1/2 teaspoon of salt to pints, 1 teaspoon to quarts, if desired. Fill with boiling water to 1 inch from top. Remove air bubbles, adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'831A4245B06ECD479B5FA076554043D1', X'ADE6551E49D2B6449F50B0A05FAAA2BB', X'ADE6551E49D2B6449F50B0A05FAAA2BB', 2, NULL, NULL, 2, NULL, 0, 'Fill hot jars loosely with raw beans. Do not press or shake down.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4681115005128408B9C3C1C5E1CD805', X'ADE6551E49D2B6449F50B0A05FAAA2BB', X'ADE6551E49D2B6449F50B0A05FAAA2BB', 3, NULL, NULL, 2, NULL, 0, 'Select well-filled pods with green seeds. Discard insect-damaged and diseased seeds.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B6EA6496066D494CA1A82E1CB44C7A9D', X'ADE6551E49D2B6449F50B0A05FAAA2BB', X'ADE6551E49D2B6449F50B0A05FAAA2BB', 4, NULL, NULL, 2, NULL, 0, 'Add 1/2 teaspoon of salt to pints, 1 teaspoon to quarts, if desired. Add boiling water, leaving the same headspaces listed above. Remove air bubbles, adjust lids and process.');
