INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E15A3BF7DAE2B8429B4F2884358F916B', X'68290A10D508FB40A085E59F97133532', X'68290A10D508FB40A085E59F97133532', 0, NULL, NULL, 2, NULL, 0, 'Wash beans and trim ends. Leave whole or cut or snap into 1-inch pieces.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A7226BC6C9BF9A4CA3CA424B4556227A', X'68290A10D508FB40A085E59F97133532', X'68290A10D508FB40A085E59F97133532', 1, NULL, NULL, 2, NULL, 0, 'Cover with boiling water; boil 5 minutes. Fill hot jars, loosely leaving 1-inch headspace. Add 1/2 teaspoon of salt to pints, 1 teaspoon to quarts, if desired. Fill with boiling water to 1 inch from top. Remove air bubbles, adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E6052A6AFE0954A8C7963DAA1D4FE75', X'68290A10D508FB40A085E59F97133532', X'68290A10D508FB40A085E59F97133532', 2, NULL, NULL, 2, NULL, 0, 'Fill hot jars tightly with raw beans, leaving 1-inch headspace.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6898C3ABAFC364BA8EB871A1643BC19', X'68290A10D508FB40A085E59F97133532', X'68290A10D508FB40A085E59F97133532', 3, NULL, NULL, 2, NULL, 0, 'Green and wax');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1FE34C58FC30CE45AC3E926006C47CF6', X'68290A10D508FB40A085E59F97133532', X'68290A10D508FB40A085E59F97133532', 4, NULL, NULL, 2, NULL, 0, 'Select filled but tender, crisp pods. Remove and discard diseased and rusty pods.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'857E4D93D057474F8FFD5A3A67918FEA', X'68290A10D508FB40A085E59F97133532', X'68290A10D508FB40A085E59F97133532', 5, NULL, NULL, 2, NULL, 0, 'Add 1/2 teaspoon of salt to pints, 1 teaspoon to quarts, if desired. Add boiling water, leaving 1-inch headspace. Remove air bubbles, adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A867E4DC313B9458BCD62B387749B28', X'68290A10D508FB40A085E59F97133532', X'68290A10D508FB40A085E59F97133532', 6, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'681EC18AD2D7F84EB718A104948DBD76', X'C3FA570B4E09BD4F8C0441E37772700A', X'C3FA570B4E09BD4F8C0441E37772700A', 0, NULL, NULL, 2, NULL, 0, 'Cut off beet tops, leaving an inch of stem and roots to reduce loss of color. Scrub well. Cover with boiling water. Boil until skins slip off easily; about 15 to 25 minutes depending on size. Cool, remove skins, and trim off stems and roots. Leave baby beets whole. Cut medium or large beets into 1/2-inch cubes or slices. Halve or quarter very large slices. Pack beets into hot jars leaving 1 inch headspace. Add 1/2 teaspoon of salt to pints, 1 teaspoon to quarts, if desired. Fill jars with hot water to 1 inch from top. Remove air bubbles, adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE7A6A736FE485468620610060C78DBD', X'C3FA570B4E09BD4F8C0441E37772700A', X'C3FA570B4E09BD4F8C0441E37772700A', 1, NULL, NULL, 2, NULL, 0, 'Beets with a diameter of 1 to 2 inches are preferred for whole packs. Beets larger than 3 inches in diameter are often fibrous.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'481C4D649161EF448FFB367C7C364FD6', X'C3FA570B4E09BD4F8C0441E37772700A', X'C3FA570B4E09BD4F8C0441E37772700A', 2, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'834530E378022941917DBC9E7D69BA51', X'AB8E97697C3E674285242FDAF06D7D32', X'AB8E97697C3E674285242FDAF06D7D32', 0, NULL, NULL, 2, NULL, 0, 'Wash, peel, and rewash carrots. Slice or dice.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DDDB70A2CBEB3C46B116861F98E27B8C', X'AB8E97697C3E674285242FDAF06D7D32', X'AB8E97697C3E674285242FDAF06D7D32', 1, NULL, NULL, 2, NULL, 0, 'Cover carrots with boiling water; bring to boil and simmer for 5 minutes. Fill hot jars, leaving 1-inch of headspace. Add 1/2 teaspoon of salt to pints, 1 teaspoon to quarts, if desired. Fill jars with hot water to 1 inch from top.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0841284DD3F5634EBE7C53CCE566B44D', X'AB8E97697C3E674285242FDAF06D7D32', X'AB8E97697C3E674285242FDAF06D7D32', 2, NULL, NULL, 2, NULL, 0, 'Fill hot jars tightly with raw carrots, leaving 1-inch headspace.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0674F5ED8B234F4080548F2F14728E0D', X'AB8E97697C3E674285242FDAF06D7D32', X'AB8E97697C3E674285242FDAF06D7D32', 3, NULL, NULL, 2, NULL, 0, 'Select small carrots, preferably 1 to 1-1/4 inches in diameter. Larger carrots are often too fibrous.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2DD4FEA1B95E84FAD9E683A37DC4146', X'AB8E97697C3E674285242FDAF06D7D32', X'AB8E97697C3E674285242FDAF06D7D32', 4, NULL, NULL, 2, NULL, 0, 'Add 1/2 teaspoon of salt to pints, 1 teaspoon to quarts, if desired. Add hot cooking liquid or water, leaving 1-inch headspace. Remove air bubbles and adjust headspace if needed. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'757737F5AE83E84FB074EB9B74BA73E7', X'AB8E97697C3E674285242FDAF06D7D32', X'AB8E97697C3E674285242FDAF06D7D32', 5, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'346066F81C924348A0C11BEA695B73A0', X'B1054E13A465F2479403EEBB3B873AD8', X'B1054E13A465F2479403EEBB3B873AD8', 0, NULL, NULL, 2, NULL, 0, 'Husk corn, remove silk, and wash ears. Blanch ears 4 minutes in boiling water. Cut corn from cob at the center of kernel. Scrape remaining corn from cobs with a table knife.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0926E2812BA71D4D80CB4D5127D3D833', X'B1054E13A465F2479403EEBB3B873AD8', X'B1054E13A465F2479403EEBB3B873AD8', 1, NULL, NULL, 2, NULL, 0, 'Add 1 cup of boiling water to 2 cups of corn. Heat to a boil. Fill hot pint jar with hot corn, leaving 1-inch headspace. Add 1/2 teaspoon salt to each pint jar, if desired. Remove air bubbles, adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9AABF7E750228941AE020D1FBF20160B', X'B1054E13A465F2479403EEBB3B873AD8', X'B1054E13A465F2479403EEBB3B873AD8', 2, NULL, NULL, 2, NULL, 0, 'Select ears containing slightly immature kernels, or of ideal quality for eating fresh.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D457D16B78AB848B648A9BA33D3B452', X'B1054E13A465F2479403EEBB3B873AD8', X'B1054E13A465F2479403EEBB3B873AD8', 3, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF6FCD7EC4B2CF49A5B058C713CC06B0', X'6BFC1F13D84A1F4AB4D1BDF7127A6E5F', X'6BFC1F13D84A1F4AB4D1BDF7127A6E5F', 0, NULL, NULL, 2, NULL, 0, 'Select ears containing slightly immature kernels or of ideal quality for eating fresh. Canning of some sweeter varieties or too immature kernels may cause browning. Can a small amount, check color and flavor before canning large quantities.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'91CD073B050AE6448794F0579EBCD833', X'6BFC1F13D84A1F4AB4D1BDF7127A6E5F', X'6BFC1F13D84A1F4AB4D1BDF7127A6E5F', 1, NULL, NULL, 2, NULL, 0, 'Procedure: Husk corn, remove silk, and wash. Blanch 3 minutes in boiling water. Cut corn from cob at about 3/4 the depth of kernel.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE48610784B3E0438529F36151B975FE', X'6BFC1F13D84A1F4AB4D1BDF7127A6E5F', X'6BFC1F13D84A1F4AB4D1BDF7127A6E5F', 2, NULL, NULL, 2, NULL, 0, 'CAUTION Do not scrape cob.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D79BBDF8433F1448FA15245273F35B0', X'6BFC1F13D84A1F4AB4D1BDF7127A6E5F', X'6BFC1F13D84A1F4AB4D1BDF7127A6E5F', 3, NULL, NULL, 2, NULL, 0, 'Hot Pack - To each clean quart of kernels in a saucepan, add 1 cup of hot water, heat to boiling and simmer 5 minutes. Fill hot jars with corn and cooking liquid, leaving 1-inch headspace. Add 1/2 teaspoon of salt to pints, 1 teaspoon to quarts, if desired. Fill jars with hot water to 1 inch from top. Remove air bubbles, adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19BBBFABD68E0A45A56B75DE8DE3DF28', X'6BFC1F13D84A1F4AB4D1BDF7127A6E5F', X'6BFC1F13D84A1F4AB4D1BDF7127A6E5F', 4, NULL, NULL, 2, NULL, 0, 'Raw Pack - Fill hot jars with corn, leaving 1-inch headspace. Add 1/2 teaspoon of salt to pints, 1 teaspoon to quarts, if desired. Fill jars with hot water to 1 inch from top. Remove air bubbles, adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE2596790DD44C48B9475AAC27BDAE77', X'6BFC1F13D84A1F4AB4D1BDF7127A6E5F', X'6BFC1F13D84A1F4AB4D1BDF7127A6E5F', 5, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'255DFABB2AA32E45A9FB3F86870324F6', X'F957D5773C6CCE4296CF7EA15954EB1D', X'F957D5773C6CCE4296CF7EA15954EB1D', 0, NULL, NULL, 2, NULL, 0, 'Optional mix - You may change the suggested proportions or substitute other favorite vegetables except leafy greens, dried beans, cream-style corn, squash and sweet potatoes.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38CDE66B7FE4F843810872A84BA7D70E', X'F957D5773C6CCE4296CF7EA15954EB1D', X'F957D5773C6CCE4296CF7EA15954EB1D', 1, NULL, NULL, 2, NULL, 0, 'Procedure: Except for zucchini, wash and prepare vegetables for canning. Wash, trim, and slice or cube zucchini (if used); combine all vegetables in a large pot or kettle, and add enough water to cover pieces. Boil 5 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2DEA62178CC0E34B97827BA73C277637', X'F957D5773C6CCE4296CF7EA15954EB1D', X'F957D5773C6CCE4296CF7EA15954EB1D', 2, NULL, NULL, 2, NULL, 0, 'Hot Pack - fill hot jars with hot pieces, leaving 1-inch headspace. Add 1/2 teaspoon of salt to pints, 1 teaspoon to quarts, if desired. Fill jars with hot water to 1 inch from top. Remove air bubbles, adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75A96FFB8857474E9CD2D98A7AE3390F', X'F957D5773C6CCE4296CF7EA15954EB1D', X'F957D5773C6CCE4296CF7EA15954EB1D', 3, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B565E207F552E42972DC35873710550', X'4AF6EB1F94929341A453D418C5B0C310', X'4AF6EB1F94929341A453D418C5B0C310', 0, NULL, NULL, 2, NULL, 0, 'Select only brightly colored, small to medium-size domestic mushrooms with short stems, tight veils (unopened caps), and no discoloration.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F245E20644987546939052875CC57194', X'4AF6EB1F94929341A453D418C5B0C310', X'4AF6EB1F94929341A453D418C5B0C310', 1, NULL, NULL, 2, NULL, 0, 'CAUTION Do not can wild mushrooms.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'80AF2187ED743B4CA150950334CDC692', X'4AF6EB1F94929341A453D418C5B0C310', X'4AF6EB1F94929341A453D418C5B0C310', 2, NULL, NULL, 2, NULL, 0, 'Procedure: Trim stems and discolored parts. Soak in cold water for 10 minutes to remove dirt. Wash in clean water. Leave small mushrooms whole; cut large ones.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D62388C5AD9AA74D87B15DD9149EB9FE', X'4AF6EB1F94929341A453D418C5B0C310', X'4AF6EB1F94929341A453D418C5B0C310', 3, NULL, NULL, 2, NULL, 0, 'Hot Pack - Cover with water in a saucepan and boil 5 minutes. Fill hot jars with hot mushrooms, leaving 1-inch headspace. Add 1/4 teaspoon of salt to half-pints, 1/2 teaspoon to pints, if desired. For better color, add 1/8 teaspoon of ascorbic acid powder, or a crushed 500-milligram tablet of vitamin C per pint. Fill jars with hot water to 1 inch from top. Remove air bubbles, adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B244167D8A2F941BA921F8C3B5514B9', X'4AF6EB1F94929341A453D418C5B0C310', X'4AF6EB1F94929341A453D418C5B0C310', 4, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B3195EC48C595546A98D731B1E56C0E7', X'440C802BA7C1004CB4DFC9C183B3BF0C', X'440C802BA7C1004CB4DFC9C183B3BF0C', 0, NULL, NULL, 2, NULL, 0, 'Select young, tender pods. Remove and discard diseased and rust-spotted pods.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A194C6D2FE5D948A05EA8EAEDBEF36A', X'440C802BA7C1004CB4DFC9C183B3BF0C', X'440C802BA7C1004CB4DFC9C183B3BF0C', 1, NULL, NULL, 2, NULL, 0, 'Procedure: Wash pods and trim ends. Leave whole or cut into 1 inch pieces.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CED60261A977234B94C2DEC860AB07DE', X'440C802BA7C1004CB4DFC9C183B3BF0C', X'440C802BA7C1004CB4DFC9C183B3BF0C', 2, NULL, NULL, 2, NULL, 0, 'Hot Pack - Cover with hot water in a saucepan, boil 2 minutes and drain. Fill hot jars with hot okra and cooking liquid, leaving 1-inch headspace. Add 1/2 teaspoon of salt to pints, 1 teaspoon to quarts, if desired. Remove air bubbles, adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C54FC7B8DAD154429EBE1B996581D7E4', X'440C802BA7C1004CB4DFC9C183B3BF0C', X'440C802BA7C1004CB4DFC9C183B3BF0C', 3, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B38BB8C2500FC4EA68BB27FB4579733', X'7F24EA7D37A5FC4FA8766459E7F1968B', X'7F24EA7D37A5FC4FA8766459E7F1968B', 0, NULL, NULL, 2, NULL, 0, 'All varieties');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'964530A9035310419E83BA2047A936B0', X'7F24EA7D37A5FC4FA8766459E7F1968B', X'7F24EA7D37A5FC4FA8766459E7F1968B', 1, NULL, NULL, 2, NULL, 0, 'Select mature, dry seeds. Sort out and discard discolored seeds.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB06E5029F942F4C867FC13D732481C6', X'7F24EA7D37A5FC4FA8766459E7F1968B', X'7F24EA7D37A5FC4FA8766459E7F1968B', 2, NULL, NULL, 2, NULL, 0, 'Procedure: Place dried peas in a large pot and cover with water. Soak 12 to 18 hours in a cool place. Drain water. To quickly hydrate peas, you may cover sorted and washed peas with boiling water in a saucepan. Boil 2 minutes, remove from heat, soak 1 hour and drain. Cover peas soaked by either method with fresh water and boil 30 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C296D1BBAF5CB74C9E91E6BF4059F31A', X'7F24EA7D37A5FC4FA8766459E7F1968B', X'7F24EA7D37A5FC4FA8766459E7F1968B', 3, NULL, NULL, 2, NULL, 0, 'Add 1/2 teaspoon of salt per pint or 1 teaspoon per quart to the jar, if desired. Fill hot jars with peas and cooking water, leaving 1-inch headspace. Remove air bubbles, adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B9ABD0A623E73C439A244BADE1D28F69', X'7F24EA7D37A5FC4FA8766459E7F1968B', X'7F24EA7D37A5FC4FA8766459E7F1968B', 4, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CF8AFAC53F0D64B93DBBFD8586EE4AF', X'67C0FDCD671EE144926B7A36188EF72F', X'67C0FDCD671EE144926B7A36188EF72F', 0, NULL, NULL, 2, NULL, 0, 'Shell and wash peas.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7EE62A8B0D7F2447A7D136A6F3C8CE52', X'67C0FDCD671EE144926B7A36188EF72F', X'67C0FDCD671EE144926B7A36188EF72F', 1, NULL, NULL, 2, NULL, 0, 'Cover with boiling water. Bring to a boil in a saucepan, and boil for 2 minutes. Fill hot jars loosely with hot peas, and add cooking liquid, leaving 1-inch headspace. Add 1/2 teaspoon of salt to pints, 1 teaspoon to quarts, if desired. Remove air bubbles, adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A57A54F3F6D204E8EEB356A4C98BCE5', X'67C0FDCD671EE144926B7A36188EF72F', X'67C0FDCD671EE144926B7A36188EF72F', 2, NULL, NULL, 2, NULL, 0, 'Fill hot jars with raw peas, add boiling water, leaving 1-inch headspace. Do not shake or press down peas. Add 1/2 teaspoon of salt to pints, 1 teaspoon to quarts, if desired. Remove air bubbles, adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E767EADE598D6540B51C415A347DEF9F', X'67C0FDCD671EE144926B7A36188EF72F', X'67C0FDCD671EE144926B7A36188EF72F', 3, NULL, NULL, 2, NULL, 0, 'It is recommended that sugar snap and Chinese edible pods be frozen for best quality. Select filled pods containing young, tender, sweet seeds. Discard diseased pods.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A11A413C6E24F42BF1DD70582FC108E', X'67C0FDCD671EE144926B7A36188EF72F', X'67C0FDCD671EE144926B7A36188EF72F', 4, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'536D42FCFBC9714598C0392A29901120', X'67C0FDCD671EE144926B7A36188EF72F', X'67C0FDCD671EE144926B7A36188EF72F', 5, NULL, NULL, 2, NULL, 0, 'NOTE For dry peas follow procedures and processing times for dry beans.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2377780B2092E4498AD9E2B4AED88574', X'14E96936CE7C7843AF1B716AC6977BAD', X'14E96936CE7C7843AF1B716AC6977BAD', 0, NULL, NULL, 2, NULL, 0, 'Hot or sweet, including chiles, jalapeno, and pimiento.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2812FCA27F77449BA63845ACF4121EB', X'14E96936CE7C7843AF1B716AC6977BAD', X'14E96936CE7C7843AF1B716AC6977BAD', 1, NULL, NULL, 2, NULL, 0, 'Procedure: Select firm yellow, green, or red peppers. Do not use soft or diseased peppers.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BEB675B8697074409068DFEF190FC88C', X'14E96936CE7C7843AF1B716AC6977BAD', X'14E96936CE7C7843AF1B716AC6977BAD', 2, NULL, NULL, 2, NULL, 0, 'CAUTION If you choose hot peppers, wear plastic or rubber gloves and do not touch your face while handling or cutting hot peppers. If you do not wear gloves, wash hands thoroughly with soap and water before touching your face or eyes. If you do touch your eyes you may have vision problems for 15-20 minutes, however, the condition is not threatening and the problem will go away. Remember, hot peppers are the main ingredient of self-defense pepper spray.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB37C5887FF4DA47B73093C2EA272E44', X'14E96936CE7C7843AF1B716AC6977BAD', X'14E96936CE7C7843AF1B716AC6977BAD', 3, NULL, NULL, 2, NULL, 0, 'Hot Pack - Small peppers may be left whole. Large peppers may be quartered. Remove cores and seeds. Slash two or four slits in each pepper, and either blanch in boiling water or blister skins using one of these two methods:');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DEE0EA42F475B54F9F851D130517767E', X'14E96936CE7C7843AF1B716AC6977BAD', X'14E96936CE7C7843AF1B716AC6977BAD', 4, NULL, NULL, 2, NULL, 0, 'Cool several minutes; peel off skins. Flatten whole peppers. Add 1/2 teaspoon of salt to each pint jar, if desired. Fill hot jars loosely with peppers and add fresh boiling water, leaving 1-inch headspace. Remove air bubbles, adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'22A60BF7B88C0449A69008D6D0A60C7C', X'14E96936CE7C7843AF1B716AC6977BAD', X'14E96936CE7C7843AF1B716AC6977BAD', 5, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6991E64A5763142B87F4EBA0DD945AC', X'14E96936CE7C7843AF1B716AC6977BAD', X'14E96936CE7C7843AF1B716AC6977BAD', 6, NULL, NULL, 2, NULL, 0, 'Oven or broiler method to blister skins  place peppers in a hot oven (400 F, 204 C) or broiler for 6-8 minutes until skins blister.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6E6B13D8F16C24183C51E1DA01954C2', X'14E96936CE7C7843AF1B716AC6977BAD', X'14E96936CE7C7843AF1B716AC6977BAD', 7, NULL, NULL, 2, NULL, 0, 'Range-top method to blister skins  Cover hot burner, either gas or electric, with heavy wire mesh. Place peppers on burner for several minutes until skins blister. After blistering skins, place peppers in a pan and cover with a damp cloth. (This will make peeling the peppers easier).');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6EE1EF159876CC419F3D929636B95D86', X'5143B7024278CC40A6ACE6645B5EAD34', X'5143B7024278CC40A6ACE6645B5EAD34', 0, NULL, NULL, 2, NULL, 0, 'Choose small to medium-sized potatoes. They should be mature and not too fibrous. Can within 1 to 2 months after harvest.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92EA6725149701428F836DA5BF7930D5', X'5143B7024278CC40A6ACE6645B5EAD34', X'5143B7024278CC40A6ACE6645B5EAD34', 1, NULL, NULL, 2, NULL, 0, 'Hot Pack - Wash potatoes and boil or steam until partially soft (15 to 20 minutes). Remove skins. Cut medium potatoes, if needed, so that pieces are uniform in size.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41C02E0BE5D5844890BD443C8809D7AE', X'5143B7024278CC40A6ACE6645B5EAD34', X'5143B7024278CC40A6ACE6645B5EAD34', 2, NULL, NULL, 2, NULL, 0, 'CAUTION Do not mash or puree pieces.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2C5BC90239E3845AC982F02B7FCF012', X'5143B7024278CC40A6ACE6645B5EAD34', X'5143B7024278CC40A6ACE6645B5EAD34', 3, NULL, NULL, 2, NULL, 0, 'Fill hot jars, leaving 1-inch headspace. Add 1/2 teaspoon of salt to pints, 1 teaspoon to quarts, if desired. Fill jars with hot water boiling water or syrup to 1 inch from top.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3DFF8A7EE8CAAA4AAE90F5042BD65BB6', X'5143B7024278CC40A6ACE6645B5EAD34', X'5143B7024278CC40A6ACE6645B5EAD34', 4, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5E7FBE0DBAFDE45886DEF3D1FA92943', X'B306A145A91AB54896DFB4343885673E', X'B306A145A91AB54896DFB4343885673E', 0, NULL, NULL, 2, NULL, 0, 'Select small to medium-size mature potatoes of ideal quality for cooking. Tubers stored below 458F may discolor when canned Choose potatoes 1 to 2 inches in diameter if they are to be packed whole.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1674EAD1DECB8749A0486C0086770F19', X'B306A145A91AB54896DFB4343885673E', X'B306A145A91AB54896DFB4343885673E', 1, NULL, NULL, 2, NULL, 0, 'Procedure: Wash and peel potatoes. If desired, cut into 1/2-inch cubes. To prevent darkening place potatoes in ascorbic acid solution. Drain.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1A5CFAA54AB8A4B9D90BD149C66C189', X'B306A145A91AB54896DFB4343885673E', X'B306A145A91AB54896DFB4343885673E', 2, NULL, NULL, 2, NULL, 0, 'Ascorbic acid solution: add 3000 mg (1 teaspoon) of ascorbic acid powder, or 6 crushed 500-milligram tablet of vitamin C to 1 gallon of water.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CDDA249C26DD53409E68B5E4BE1C97CB', X'B306A145A91AB54896DFB4343885673E', X'B306A145A91AB54896DFB4343885673E', 3, NULL, NULL, 2, NULL, 0, 'Hot Pack - Cook 2 minutes in boiling water and drain again. For whole potatoes, boil 10 minutes and drain. Fill hot jars with hot potatoes leaving 1-inch headspace. Add 1/2 teaspoon of salt to pints, 1 teaspoon to quarts, if desired. Fill jars with boiling water to 1 inch from top. Remove air bubbles Adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'43A549BFC0DAE542A639A9B38FBCA1CB', X'B306A145A91AB54896DFB4343885673E', X'B306A145A91AB54896DFB4343885673E', 4, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3AC56E1D5BE17544A5E184D948F10A06', X'1ECBADD3351A8A4082BA5DB3719BD825', X'1ECBADD3351A8A4082BA5DB3719BD825', 0, NULL, NULL, 2, NULL, 0, 'Pumpkins and squash should have a hard rind and stringless, mature pulp of ideal quality for cooking fresh. Small size pumpkins (sugar or pie varieties) make better products.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2B39C7D66563848A98A77F15E7E2F25', X'1ECBADD3351A8A4082BA5DB3719BD825', X'1ECBADD3351A8A4082BA5DB3719BD825', 1, NULL, NULL, 2, NULL, 0, 'Procedure: Wash, remove seeds, cut into 1-inch-wide slices, and peel. Cut flesh into 1-inch cubes. Boil 2 minutes in water.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'750FC9275BC08E4FBD76BCB908057D41', X'1ECBADD3351A8A4082BA5DB3719BD825', X'1ECBADD3351A8A4082BA5DB3719BD825', 2, NULL, NULL, 2, NULL, 0, 'CAUTION Do not mash or puree.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D1A08F1D5513A41AE6D99EDF87D59A2', X'1ECBADD3351A8A4082BA5DB3719BD825', X'1ECBADD3351A8A4082BA5DB3719BD825', 3, NULL, NULL, 2, NULL, 0, 'Hot Pack - Fill hot jars with cubes and boiling hot cooking liquid, leaving 1-inch headspace. Remove air bubbles, adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0DA76AD29EE9844BAB6AC34C97378F8', X'1ECBADD3351A8A4082BA5DB3719BD825', X'1ECBADD3351A8A4082BA5DB3719BD825', 4, NULL, NULL, 2, NULL, 0, 'For making pies, drain jars and strain or sieve the cubes at preparation time.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51CC34C1EA1B4147B95CBEEF6419A604', X'1ECBADD3351A8A4082BA5DB3719BD825', X'1ECBADD3351A8A4082BA5DB3719BD825', 5, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85EF90F55EA69248B797B4C069CE9D7D', X'1ECBADD3351A8A4082BA5DB3719BD825', X'1ECBADD3351A8A4082BA5DB3719BD825', 6, NULL, NULL, 2, NULL, 0, 'Prepare and process according to instructions for `Pumpkin` above.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FECADAD17C9D4F44912F8B0DF06CBE5B', X'C71CCD32346AD342892757982CFB2853', X'C71CCD32346AD342892757982CFB2853', 0, NULL, NULL, 2, NULL, 0, 'Vegetable, dried bean, pea, meat, poultry, or seafoods.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51DFCA6EEECFB5489ABF685094EAE498', X'C71CCD32346AD342892757982CFB2853', X'C71CCD32346AD342892757982CFB2853', 1, NULL, NULL, 2, NULL, 0, 'CAUTION Do not add noodles or other pasta, rice, flour, cream, milk or other thickening agents to home canned soups. If dried beans or peas are used, they must be fully rehydrated first.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6AAB1B97B11264EA1C5FE03A91B393D', X'C71CCD32346AD342892757982CFB2853', X'C71CCD32346AD342892757982CFB2853', 2, NULL, NULL, 2, NULL, 0, 'Procedure: Select, wash, and prepare vegetables, meat, and seafoods as described for the specific foods. Cover meat with water and cook until tender. Cool meat and remove bones. Cook vegetables. For each cup of dried beans or peas, add 3 cups of water, boil 2 minutes, remove from heat, soak 1 hour, and heat to boil. Drain all foods and combine with meat broth, tomatoes, or water to cover. Boil 5 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C4DC11B1732764187A2157482F45207', X'C71CCD32346AD342892757982CFB2853', X'C71CCD32346AD342892757982CFB2853', 3, NULL, NULL, 2, NULL, 0, 'CAUTION Do not thicken. Salt to taste, if desired.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF89E020BF443E4DA1101EF83A2B922E', X'C71CCD32346AD342892757982CFB2853', X'C71CCD32346AD342892757982CFB2853', 4, NULL, NULL, 2, NULL, 0, 'Hot Pack - Fill hot jars only halfway with mixture of solids. Add and cover with remaining liquid, leaving 1-inch headspace. Remove air bubbles, adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E204512C9513A54389496AB9AF8E3478', X'C71CCD32346AD342892757982CFB2853', X'C71CCD32346AD342892757982CFB2853', 5, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'80842372BA92274EB27037E98A54AFD4', X'BB495F608F413845AB0199AC7D57B6AA', X'BB495F608F413845AB0199AC7D57B6AA', 0, NULL, NULL, 2, NULL, 0, 'Can only freshly harvested greens. Discard any wilted, discolored, diseased, or insect-damaged leaves. Leaves should be tender and attractive in color.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EBBBEF8E6D67BC42851F3919DBE83A6A', X'BB495F608F413845AB0199AC7D57B6AA', X'BB495F608F413845AB0199AC7D57B6AA', 1, NULL, NULL, 2, NULL, 0, 'Procedure: Wash only small amounts of greens at one time. Drain water and continue rinsing until water is clear and free of grit. Cut out tough stems and midribs. Place 1 pound of greens at a time in cheesecloth bag or blancher basket and steam 3 to 5 minutes or until well wilted. Add 1/4 teaspoon of salt to pints and 1/2 teaspoon of salt to quarts, if desired.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25AFE15F29C47A48BF8C34AB5EF55C14', X'BB495F608F413845AB0199AC7D57B6AA', X'BB495F608F413845AB0199AC7D57B6AA', 2, NULL, NULL, 2, NULL, 0, 'Hot Pack - Fill hot jars loosely with greens and add fresh boiling water, leaving 1-inch headspace. Remove air bubbles, adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'381CADF49FC01C489E29C72DFE5CD988', X'BB495F608F413845AB0199AC7D57B6AA', X'BB495F608F413845AB0199AC7D57B6AA', 3, NULL, NULL, 2, NULL, 0, '* CAUTION process 100 minutes if soup contains seafoods.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE9B50EB64F09049B97DDB5BE04C7F72', X'BB495F608F413845AB0199AC7D57B6AA', X'BB495F608F413845AB0199AC7D57B6AA', 4, NULL, NULL, 2, NULL, 0, 'For processing at above 1,000 ft, see Altitude Adjustments.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8053B7463E588844A6157DDFCA86AD85', X'B48E77F0C955134499E2764E85B04F8B', X'B48E77F0C955134499E2764E85B04F8B', 0, NULL, NULL, 2, NULL, 0, 'Apple butter is a thickened apple sauce with added spices.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26A0F4172286AA49B0F7B2C16CA09882', X'B48E77F0C955134499E2764E85B04F8B', X'B48E77F0C955134499E2764E85B04F8B', 1, NULL, NULL, 2, NULL, 0, 'Quarter and core apples, leave the skin on. Cook apples in vinegar and cider on low heat.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30A802EA7A25634580F2314CFA3F6D19', X'B48E77F0C955134499E2764E85B04F8B', X'B48E77F0C955134499E2764E85B04F8B', 2, NULL, NULL, 2, NULL, 0, 'Press apple pulp through a strainer or food mill. Put apple pulp in a pot and add sugar and spices.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'00A7AC0FD6C9FD4ABA71DF2DD7720868', X'B48E77F0C955134499E2764E85B04F8B', X'B48E77F0C955134499E2764E85B04F8B', 3, NULL, NULL, 2, NULL, 0, 'Cook on low heat until thick, stirring frequently. This will take about 60 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B056092F3C7A2E4E99D6BAB0FFAF7C70', X'B48E77F0C955134499E2764E85B04F8B', X'B48E77F0C955134499E2764E85B04F8B', 4, NULL, NULL, 2, NULL, 0, 'Test for doneness  spoon a portion of apple butter into a plate. If a rim of liquid does not form around the butter, it has been properly cooked.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ADF0051ABA41EF4B8C46A81CC85CE718', X'B48E77F0C955134499E2764E85B04F8B', X'B48E77F0C955134499E2764E85B04F8B', 5, NULL, NULL, 2, NULL, 0, 'Without a delay, fill sterile glass jars leaving  inch headspace. Wipe rims, tighten lids and process in a bath canner for 5 minutes. At altitudes above 1000 feet, process for 10 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B20228B856C4AD4EB66B028FD97AEC50', X'B48E77F0C955134499E2764E85B04F8B', X'B48E77F0C955134499E2764E85B04F8B', 6, NULL, NULL, 2, NULL, 0, 'Remove jars from canner and let stand undisturbed for 12 hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B5EE22D9AF5C2941BF459AAA8DCE73E4', X'83EB8280263814489ABFA138799BAA6D', X'83EB8280263814489ABFA138799BAA6D', 0, NULL, NULL, 2, NULL, 0, 'The flavor of apricot jam may be varied by the amount of pits used. Pits of fruits such as apricots, peaches or sour cherries impart almond like flavor not only in jams but in alcoholic infusions as well. In apricots, only the inside of the pit is used. The pits are easier to open when they are baked first. Then, the inside core of the pit is baked as well, as the pits of the above mentioned fruits contain traces of cyanide (cyanide produces almond smell). This heat treatment will prevent any allergic reaction.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12657860E282FD4E8E3F63706CC75087', X'83EB8280263814489ABFA138799BAA6D', X'83EB8280263814489ABFA138799BAA6D', 1, NULL, NULL, 2, NULL, 0, 'Split open apricots, save 6 pits, discard the rest. Slice apricots and place in a large bowl. Add sugar, mix, cover and leave for 4 hours at room temperature.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B402CDF977BB56439C15F7078C4A3439', X'83EB8280263814489ABFA138799BAA6D', X'83EB8280263814489ABFA138799BAA6D', 2, NULL, NULL, 2, NULL, 0, 'Bake pits for 15 minutes on a cookie sheet at 356 F (180 C), remove from the oven, but maintain the heat. Crack shells open, remove inside cores and bake them again for 5 minutes at above temperature.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07EB1BDA322B2746871124AE42F455F8', X'83EB8280263814489ABFA138799BAA6D', X'83EB8280263814489ABFA138799BAA6D', 3, NULL, NULL, 2, NULL, 0, 'Place apricots in a large pot, add lemon juice, and bring to a boil over medium heat.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5077217A9071D342BE93E16A85488D82', X'83EB8280263814489ABFA138799BAA6D', X'83EB8280263814489ABFA138799BAA6D', 4, NULL, NULL, 2, NULL, 0, 'Continue cooking for about 15 minutes, stirring often, until jam thickens. Do not cover, the purpose of cooking is to evaporate water.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4AA46EC2E44E254F85815284867EC1CE', X'83EB8280263814489ABFA138799BAA6D', X'83EB8280263814489ABFA138799BAA6D', 5, NULL, NULL, 2, NULL, 0, 'Place one or two pits in each jar, then fill with jam to within  inch from the top. Remove air bubbles, install and tighten covers.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0828DCA8B002364FBBB48AB90821FF84', X'83EB8280263814489ABFA138799BAA6D', X'83EB8280263814489ABFA138799BAA6D', 6, NULL, NULL, 2, NULL, 0, 'Place jars in a bath canner and bring water to boil. Cook for 10 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F1A8D730C90D74D8645EF5777647B6C', X'83EB8280263814489ABFA138799BAA6D', X'83EB8280263814489ABFA138799BAA6D', 7, NULL, NULL, 2, NULL, 0, 'Remove jars from the canner and leave undisturbed for 12 hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48C72679FC2E84489BEBB59FF3FF5280', X'83EB8280263814489ABFA138799BAA6D', X'83EB8280263814489ABFA138799BAA6D', 8, NULL, NULL, 2, NULL, 0, 'Store in cool and dark place.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B01AA51B8B4A5B4BB39B6C72B7138935', X'F45C9D0773FDCC4EB6EFBA9E413CA879', X'F45C9D0773FDCC4EB6EFBA9E413CA879', 0, NULL, NULL, 2, NULL, 0, 'Procedure: Wash and thoroughly crush blueberries, one layer at a time, in a saucepan. Add lemon juice, spice, and water. Stir in pectin and bring to a full rolling boil over high heat, stirring frequently. Add the sugar and return to a full rolling boil. Boil hard for 1 minute, stirring constantly. Remove from heat, quickly skim off foam, and fill sterile jars, leaving 1/4-inch headspace. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD05C6207101B446B8412F7A0188530F', X'D710483B9750074892A0C8A4A536987A', X'D710483B9750074892A0C8A4A536987A', 0, NULL, NULL, 2, NULL, 0, 'Chokeberry, scientific name: Aronia melanocarpa, also known as Aronia, is blessed with unusually large number of antioxidants and vitamins. Aronia have grabbed recently the attention of food scientists as it has shown an exceptional medicinal properties. The laboratory tests have shown that aronia is effective in fighting different types of cancer, aging, neurological diseases, diabetes and many infections. Aronia is also used for making alcoholic infusions. Chokeberry contains plenty of pectin so the jam gels easily.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D6A2892FF7F254BBF9E7D170363C4F3', X'D710483B9750074892A0C8A4A536987A', X'D710483B9750074892A0C8A4A536987A', 1, NULL, NULL, 2, NULL, 0, 'Mix berries with sugar, place in pot and bring to a boil. Cook over low heat for 15 minutes stirring often. At the end of cooking add lemon juice.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7F499D148EF0E43AA6B37B94001CF93', X'D710483B9750074892A0C8A4A536987A', X'D710483B9750074892A0C8A4A536987A', 2, NULL, NULL, 2, NULL, 0, 'Fill jars with jam and process for 10 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'49CF555746DABD49868D5E331540E837', X'218A49B00FFFA74BA80396AC100F9AE1', X'218A49B00FFFA74BA80396AC100F9AE1', 0, NULL, NULL, 2, NULL, 0, 'Black elderberry (Sumbucus nigra) has been used medicinally for hundreds of years. Many studies demonstrate that elderberry may have a measurable effect in treating the flu, alleviating allergies, and boosting overall respiratory health. Elderberry is used in Traditional Chinese Medicine, dissolved in wine,for rheumatism and traumatic injury. The berries are a favorite food resource for many birds.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A9CFDBF01342245A3E8CC64041E4EAC', X'218A49B00FFFA74BA80396AC100F9AE1', X'218A49B00FFFA74BA80396AC100F9AE1', 1, NULL, NULL, 2, NULL, 0, '1. Using fork separate berries from stalks. Peel and core apple, dice into small cubes.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6275E94484A8F45B28D585F161AFB10', X'218A49B00FFFA74BA80396AC100F9AE1', X'218A49B00FFFA74BA80396AC100F9AE1', 2, NULL, NULL, 2, NULL, 0, '2. Place elderberries in pot, and bring slowly to a boil, stirring constantly. Once the berries release juice, add finely diced apple. Bring to a boil.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92FD18A752EC4441A0089B70174C4EF2', X'218A49B00FFFA74BA80396AC100F9AE1', X'218A49B00FFFA74BA80396AC100F9AE1', 3, NULL, NULL, 2, NULL, 0, '3. Decrease the heat, add sugar and lemon juice and cook over low heat for 45 minutes stirring often until the jam thickens.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41DC125955A1504FA7D10E987643B436', X'218A49B00FFFA74BA80396AC100F9AE1', X'218A49B00FFFA74BA80396AC100F9AE1', 4, NULL, NULL, 2, NULL, 0, 'Perform refrigerator test.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68BBC9350CE1394680EAC483606ECA10', X'218A49B00FFFA74BA80396AC100F9AE1', X'218A49B00FFFA74BA80396AC100F9AE1', 5, NULL, NULL, 2, NULL, 0, '4. Fill jam into jars and process in water bath canner for 10 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8DAA02A2B4AF94EA97C20FD8D58AB0A', X'218A49B00FFFA74BA80396AC100F9AE1', X'218A49B00FFFA74BA80396AC100F9AE1', 6, NULL, NULL, 2, NULL, 0, '5. Remove jars and let stand for 12 hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3046C8E51E37714D998AD576F9914029', X'90C7116DAC3635419C97B7ACCA27C66A', X'90C7116DAC3635419C97B7ACCA27C66A', 0, NULL, NULL, 2, NULL, 0, 'Elderberry (Sumbucus nigra) can be found in moderate climate all over the world. The berries are credited with alleviating allergies and treating the flu. The tree produces abundance of fruit and is very common in North Europe where it grows everywhere, in gardens, parks and on streets.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E8084B51C33384C99680B7425331A1D', X'90C7116DAC3635419C97B7ACCA27C66A', X'90C7116DAC3635419C97B7ACCA27C66A', 1, NULL, NULL, 2, NULL, 0, 'Elderberries are commonly used in Europe to make wines, cordials, jams, fruit pies and relishes. Both, flowers and ripe berries are used for food. St. Germain, a French liqueur and Hallands Flader, a Swedish akvavit are made from elderflowers. The Italian liqueur Sambuca is flavored with elderflowers.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F0E0DA49D3312408BCAB626F3D91EB2', X'90C7116DAC3635419C97B7ACCA27C66A', X'90C7116DAC3635419C97B7ACCA27C66A', 2, NULL, NULL, 2, NULL, 0, '1. Using fork separate berries from stalks. Peel and core apples, dice into small cubes.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38524347FD969842A5823B6CF21A58A7', X'90C7116DAC3635419C97B7ACCA27C66A', X'90C7116DAC3635419C97B7ACCA27C66A', 3, NULL, NULL, 2, NULL, 0, '2. Place elderberries in pot, and bring slowly to a boil, stirring constantly. Once the berries release juice, add diced apples. Bring to a boil.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C83B777C0F69D47A90011E893E9906B', X'90C7116DAC3635419C97B7ACCA27C66A', X'90C7116DAC3635419C97B7ACCA27C66A', 4, NULL, NULL, 2, NULL, 0, '3. Decrease the heat, add sugar and lemon juice, grated lemon zest, and cook over low heat for 40 minutes stirring often until the jam thickens.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE48155D44AA5E47A72FE269BB91CFCE', X'90C7116DAC3635419C97B7ACCA27C66A', X'90C7116DAC3635419C97B7ACCA27C66A', 5, NULL, NULL, 2, NULL, 0, 'Perform refrigerator test.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'84D4E7B2D9A8A14F923A76E0284EFD96', X'90C7116DAC3635419C97B7ACCA27C66A', X'90C7116DAC3635419C97B7ACCA27C66A', 6, NULL, NULL, 2, NULL, 0, '4. Fill jam into jars and process in water bath canner for 10 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09DC4AEA3F384346B5E32E50250FFB71', X'90C7116DAC3635419C97B7ACCA27C66A', X'90C7116DAC3635419C97B7ACCA27C66A', 7, NULL, NULL, 2, NULL, 0, '5. Remove jars and let stand for 12 hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D480F75B2207440B4512ECA31ACFCF8', X'7803E33A9A393E469C1EC0844FCC5975', X'7803E33A9A393E469C1EC0844FCC5975', 0, NULL, NULL, 2, NULL, 0, 'Figs are low in pectin so adding lemon will help the jam to gel.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'23D08EAD5993F847995962AA1D64F0A2', X'7803E33A9A393E469C1EC0844FCC5975', X'7803E33A9A393E469C1EC0844FCC5975', 1, NULL, NULL, 2, NULL, 0, 'Place all ingredients in a bowl and leave for 4 hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4B60097AD578B49A102482C7283AE27', X'7803E33A9A393E469C1EC0844FCC5975', X'7803E33A9A393E469C1EC0844FCC5975', 2, NULL, NULL, 2, NULL, 0, 'Bring to a boil, then cook over low heat for 45 minutes, stirring often.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC49D8B02FDCD74AAAC40BA33C0E36C0', X'7803E33A9A393E469C1EC0844FCC5975', X'7803E33A9A393E469C1EC0844FCC5975', 3, NULL, NULL, 2, NULL, 0, 'Fill into glass jars and process in a water bath canner for 10 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69FB6787C3C3B945834B4757C6FE1990', X'7803E33A9A393E469C1EC0844FCC5975', X'7803E33A9A393E469C1EC0844FCC5975', 4, NULL, NULL, 2, NULL, 0, 'Remove jars from the canner and let stand for 12 hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4975517806522448A71BF85F3CA49636', X'B62D413489E88047A12702FBCA45857E', X'B62D413489E88047A12702FBCA45857E', 0, NULL, NULL, 2, NULL, 0, 'Procedure:');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE1F38ECCD25E4429B9FBC0006D70A57', X'B62D413489E88047A12702FBCA45857E', X'B62D413489E88047A12702FBCA45857E', 1, NULL, NULL, 2, NULL, 0, 'CAUTION Wear plastic or rubber gloves and do not touch your face while handling or cutting hot peppers. If you do not wear gloves, wash hands thoroughly with soap and water before touching your face or eyes.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FCBB270D2240D249A96D7976BD20DEFC', X'B62D413489E88047A12702FBCA45857E', X'B62D413489E88047A12702FBCA45857E', 2, NULL, NULL, 2, NULL, 0, 'Wash all peppers thoroughly; remove stems and seeds from the peppers. Do not remove the membrane from the hot peppers, since the remaining capsaicin is located there. Place sweet and hot peppers in a blender or food processor. Add enough vinegar to puree the peppers, then puree. Combine the pepper-vinegar puree and remaining vinegar into a 6 or 8 quart saucepan. Heat to a boil; then boil 20 minutes to extract flavors and color. Remove from heat and strain through a jelly bag into a bowl. (The jelly bag is preferred; several layers of cheesecloth may also be used.) Add the strained pepper-vinegar juice back to the saucepan. Stir in sugar until dissolved and return to a boil. Add the pectin, return to a full rolling boil and boil hard for 1 minute, stirring constantly. Remove from heat, quickly skim off any foam, and fill into sterile jars, leaving 1/4-inch headspace. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDFEAE2987C6F54DBB7F8645EC1AA870', X'B62D413489E88047A12702FBCA45857E', X'B62D413489E88047A12702FBCA45857E', 3, NULL, NULL, 2, NULL, 0, 'NOTE The use of yellow peppers gives this jelly a light golden color. Other colored sweet peppers can be substituted, but these will provide a different jelly color. Other hot peppers can also be substituted. It is best to start with a mild hot pepper flavor and increase it to suit personal tastes. If properly prepared, the jelly will have a mildly firm set; it is best to use half-pint jars.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'83759C0E0A4A3B4D85B2D03B8D3A17C4', X'47E764F0E03D044A9A639FE64A010449', X'47E764F0E03D044A9A639FE64A010449', 0, NULL, NULL, 2, NULL, 0, 'Procedure: Wash and pit plums; do not peel. Thoroughly crush the plums and grapes, one layer at a time, in a saucepan with water. Bring to a boil, cover, and simmer 10 minutes. Strain juice through a jelly bag or double layer of cheesecloth. Measure sugar and set aside. Combine 6-1/2 cups of juice with butter and pectin in large saucepan. Bring to a hard boil over high heat, stirring constantly. Add the sugar and return to a full rolling boil. Boil hard for 1 minute, stirring constantly. Remove from heat, quickly skim off foam, and fill sterile jars, leaving 1/4-inch headspace. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E37E1023AFECFF4E8C9E2A810C9E27EA', X'CBBF42F9007C814EAA29E4AD83EC43D9', X'CBBF42F9007C814EAA29E4AD83EC43D9', 0, NULL, NULL, 2, NULL, 0, 'Peel off peaches, remove pits and dice peaches. Place in small pot, add 3 tablespoon of water and bring to a boil. Cover the pot and cook on low heat for 10 minutes, stirring often.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5EACF168FD83D145BC0074C4B8117F33', X'CBBF42F9007C814EAA29E4AD83EC43D9', X'CBBF42F9007C814EAA29E4AD83EC43D9', 1, NULL, NULL, 2, NULL, 0, 'Add sugar, passion fruit pulp and vanilla. Increase heat and bring to a boil.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60077AF8B693554B90FE63B5EA6F45E1', X'CBBF42F9007C814EAA29E4AD83EC43D9', X'CBBF42F9007C814EAA29E4AD83EC43D9', 2, NULL, NULL, 2, NULL, 0, 'Cook without cover on medium heat for 30 minutes stirring often.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FED901C99C712848AFC4AB5888FBE1C8', X'CBBF42F9007C814EAA29E4AD83EC43D9', X'CBBF42F9007C814EAA29E4AD83EC43D9', 3, NULL, NULL, 2, NULL, 0, 'Fill jam into sterile glass jars, tighten lids and process in a water bath canner for 5 minutes.');
