INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8FEFEFF9FF246E4EBC7FBE1D035E5E70', X'D00487842BBCDC45A950B7ABFD2B6265', X'D00487842BBCDC45A950B7ABFD2B6265', 2, NULL, NULL, 2, NULL, 0, 'Bake pits for 15 minutes on a cookie sheet at 356 F (180 C), remove from the oven, but maintain the heat. Crack shells open, remove inside cores and bake them again for 5 minutes at above temperature.');
